insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('1','红楼梦','[清]曹雪芹著/高鹗','清','人民文学出版社','1996-12','1606','59.70元','平装','中国古典文学读本丛书','9787020002207','9.6','384595','《红楼梦》是一部百科全书式的长篇小说。以宝黛爱情悲剧为主线，以四大家族的荣辱兴衰为背景，描绘出18世纪中国封建社会的方方面面，以及封建专制下新兴资本主义民主思想的萌动。结构宏大、情节委婉、细节精致，人物形象栩栩如生，声口毕现，堪称中国古代小说中的经典。由红楼梦研究所校注、人民文学出版社出版的《红楼梦》以庚辰（1760）本《脂砚斋重评石头记》为底本，以甲戌（1754）本、已卯（1759）本、蒙古王府本、戚蓼生序本、舒元炜序本、郑振铎藏本、红楼梦稿本、列宁格勒藏本（俄藏本）、程甲本、程乙本等众多版本为参校本，是一个博采众长、非常适合大众阅读的本子；同时，对底本的重要修改，皆出校记，读者可因以了解《红楼梦》的不同版本状况。红学所的校注本已印行二十五年，其间1994年曾做过一次修订，又十几年过去，2008年推出修订第三版，体现了新的校注成果和科研成果。关于《红楼梦》的作者，原本就有多种说法及推想，“前八十回曹雪芹著、后四十回高鹗续”的说法只是其中之一，这次修订中校注者改为“前八十回曹雪芹著；后四十回无名氏续，程伟元、高鹗整理”，应当是一种更科学的表述，体现了校注者对这一问题的新的认识。现在这个修订后的《红楼梦》是更加完善。','1007305','https://book.douban.com/subject/1007305/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('2','活着','余华','中国','作家出版社','2012-8-1','191','20.00元','平装','余华作品（2012版）','9787506365437','9.4','727843','《活着(新版)》讲述了农村人福贵悲惨的人生遭遇。福贵本是个阔少爷，可他嗜赌如命，终于赌光了家业，一贫如洗。他的父亲被他活活气死，母亲则在穷困中患了重病，福贵前去求药，却在途中被国民党抓去当壮丁。经过几番波折回到家里，才知道母亲早已去世，妻子家珍含辛茹苦地养大两个儿女。此后更加悲惨的命运一次又一次降临到福贵身上，他的妻子、儿女和孙子相继死去，最后只剩福贵和一头老牛相依为命，但老人依旧活着，仿佛比往日更加洒脱与坚强。《活着(新版)》荣获意大利格林扎纳•卡佛文学奖最高奖项（1998年）、台湾《中国时报》10本好书奖（1994年）、香港“博益”15本好书奖（1994年）、第三届世界华文“冰心文学奖”（2002年），入选香港《亚洲周刊》评选的“20世纪中文小说百年百强”、中国百位批评家和文学编辑评选的“20世纪90年代最有影响的10部作品”。','4913064','https://book.douban.com/subject/4913064/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('3','1984','[英]乔治·奥威尔','英','北京十月文艺出版社','2010-4-1','304','28.00','精装','新经典文库：桂冠文丛','9787530210291','9.4','251387','★村上春树以《1Q84》向本书致敬★著名学者刘绍铭经典译本内地首次出版★62种文字风靡110个国家，全球销量超过5000万册★《时代周刊》“最好的100本英语小说”★兰登书屋“100本20世纪最佳英语小说”★入选英美中学生必读书书目1936年以来，我所写的每一部严肃作品，都是直接或间接地反对极权主义，支持我所理解的民主社会主义。——乔治•奥威尔（《我为何写作》）《1984》是一部杰出的政治寓言小说，也是一部幻想小说。作品刻画了人类在极权主义社会的生存状态，有若一个永不褪色的警示标签，警醒世人提防这种预想中的黑暗成为现实。历经几十年，其生命力益显强大，被誉为20世纪影响最为深远的文学经典之一。','4820710','https://book.douban.com/subject/4820710/',' Nineteen Eighty-Four','刘绍铭');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('4','百年孤独','[哥伦比亚]加西亚·马尔克斯','哥伦比亚','南海出版公司','2011-6','360','39.50元','精装','新经典文库：加西亚·马尔克斯作品','9787544253994','9.3','387083','《百年孤独》是魔幻现实主义文学的代表作，描写了布恩迪亚家族七代人的传奇故事，以及加勒比海沿岸小镇马孔多的百年兴衰，反映了拉丁美洲一个世纪以来风云变幻的历史。作品融入神话传说、民间故事、宗教典故等神秘因素，巧妙地糅合了现实与虚幻，展现出一个瑰丽的想象世界，成为20世纪最重要的经典文学巨著之一。1982年加西亚•马尔克斯获得诺贝尔文学奖，奠定世界级文学大师的地位，很大程度上乃是凭借《百年孤独》的巨大影响。','6082808','https://book.douban.com/subject/6082808/',' Cien años de soledad','范晔');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('5','三体全集','刘慈欣','中国','重庆出版社','2012-1-1','1285','168.00元','精装','科幻世界·中国科幻基石丛书','9787229042066','9.5','147663','三体三部曲(《三体》《三体Ⅱ·黑暗森林》《三体Ⅲ·死神永生》)，原名“地球往事三部曲”，是中国著名科幻作家刘慈欣的首个长篇系列，由科幻世界杂志社策划制作，重庆出版集团出版。小说讲述了文革期间一次偶然的星际通讯引发的三体世界对地球的入侵以及之后人类文明与三体文明三百多年的恩怨情仇。三体三部曲出版后十分畅销，并深受读者和主流媒体好评，被普遍认为是中国科幻文学的里程碑之作，为中国科幻确立了一个新高度。《三体》文化大革命如火如荼进行的同时。军方探寻外星文明的绝秘计划“红岸工程”取得了突破性进展。但在按下发射键的那一刻，历经劫难的叶文洁没有意识到，她彻底改变了人类的命运。地球文明向宇宙发出的第一声啼鸣，以太阳为中心，以光速向宇宙深处飞驰……四光年外，“三体文明”正苦苦挣扎——三颗无规则运行的太阳主导下的百余次毁灭与重生逼迫他们逃离母星。而恰在此时。他们接收到了地球发来的信息。在运用超技术锁死地球人的基础科学之后。三体人庞大的宇宙舰队开始向地球进发……人类的末日悄然来临。《三体Ⅱ·黑暗森林》三体人在利用魔法般的科技锁死了地球人的科学之后，庞大的宇宙舰队杀气腾腾地直扑太阳系，意欲清除地球文明。面对前所未有的危局，经历过无数磨难的地球人组建起同样庞大的太空舰队，同时，利用三体人思维透明的致命缺陷，制订了神秘莫测的“面壁计划”，精选出四位“面壁者”。秘密展开对三体人的反击。三体人自身虽然无法识破人类的诡谲计谋，却依靠由地球人中的背叛者挑选出的“破壁人”，与“面壁者”展开智慧博弈……“面壁计划”究竟能否成功？地球人究竟能否在这场你死我活的文明生存竞争中战而胜之？神秘的“黑暗森林”究竟意味着什么？《三体Ⅲ·死神永生》与三体文明的战争使人类第一次看到了宇宙黑暗的真相，地球文明像一个恐惧的孩子，熄灭了寻友的篝火，在暗夜中发抖。自以为历经沧桑，其实刚刚蹒跚学步；自以为悟出了生存竞争的秘密，其实还远没有竞争的资格。使两个文明命悬一线的黑暗森林打击，不过是宇宙战场上一个微不足道的插曲。真正的星际战争没人见过，也不可能见到，因为战争的方式和武器已经远远超出人类的想象，目睹战场之日，即是灭亡之时。宇宙的田园时代已经远去，昙花一现的终极之美最终变成任何智慧体都无法做出的梦，变成游吟诗人缥缈的残歌；宇宙的物竞天择已到了最惨烈的时刻，在亿万光年暗无天日的战场上，深渊最底层的毁灭力量被唤醒，太空变成了死神广阔的披风。太阳系中的人们永远不会知道这一切，最后直面真相的，只有两双眼睛。','6518605','https://book.douban.com/subject/6518605/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('6','哈利·波特','J.K.罗琳(J.K.Rowling)','中国','人民文学出版社','2008-12-1','3104','498.00元','精装','','9787020096695','9.7','80057','《哈利·波特(共7册)(精)》编著者J.K.罗琳。《哈利·波特(共7册)(精)》内容提要：2000年的一个深夜，在美国书店的烛光中，穿着黑斗篷，戴着小眼镜的店员开始销售“哈利o波特4”英文版，从此哈利·波特系列图书席卷全球，也是在这一年，简体中文版“哈利o波特”系列图书通过人民文学出版社引进版权登陆中国，作者J.K.罗琳用一种简单的魔法游戏将魔幻的故事传递给了中国乃至全球的青少年读者，与此同时商业电影的改编更是将这部魔幻题材的小说推向了巅峰。截止到2013年，哈利·波特系列图书累计销量达到200多万套。此次，人民文学出版社隆重推出《哈利o波特（精装本）》（全七册），此套精装珍藏版《哈利·波特》听取广大资深哈迷的心声，恢复选用陪伴广大哈迷十年之久深入人心的原有封面，并在此基础上提高工艺、完善装帧，配有独具匠心的精美函套，使哈利波特系列7本浑然一体，必将成为每一个喜欢哈利·波特读者爱不释手的珍藏！《哈利·波特与魔法石》一岁的哈利·波特失去父母后，神秘地出现在姨父姨妈家的门前。哈利在姨父家饱受欺凌，度过十年极其痛苦的日子。姨父和姨妈好似凶神恶煞，他们那混世魔王儿子达力一个肥胖、娇惯、欺负人的大块头，更是经济对哈利拳脚相加。哈利的“房间”是位于楼梯口的一个又暗又小的碗橱。十年来，从来没有人为他过过生日。但是在他十一岁生日那天，一切都发生了变化，信使猫头鹰带来了一封神秘的信：邀请哈利去一个他以及所有读到哈利故事的人会觉得望远难忘的、不可思议的地方霍格沃茨魔法学校。在魔法学校，哈利不仅找着了朋友，学会了空中飞行，骑着一把型号为光轮2000的飞天扫帚打魁地奇球，还得到了一件隐形衣。他发现那里的一切从上课到吃饭到睡觉都充满了魔力，但是一块魔石出现，它与哈利的命运息息相关……《哈利·波特与密室》哈利·波特在霍格沃茨魔法学校学习一年之后，暑假开始了。他在姨父姨妈家熬过痛苦的假期。正当他准备打点行装去学校时，小精灵多比前来发出警告：如果哈利返回霍格沃茨，灾难将会临头。但哈利义无返顾地回到了霍格沃茨，新来的吉罗德·洛哈特教授装腔作势，让他作呕；游荡在女生舆洗室里的幽灵“哭泣的桃金娘”搅得他不得安宁；低年级的小女生金妮对他投来关切目光常令他尴尬不已；小男生科林·克里维“追星”式的跟踪又经常使他落荒而逃。但是，这一切仅仅是灾难的序曲。正如多比所预言的，哈利遭受了重重磨难，经历了种种危险，难解之谜又使他煞费苦心：霍格沃茨的学生接二连三地变成了石头。这一切是品德败坏的学生德拉科·马尔福精心策划的杰作？还是忠厚善良的海格无心铸成的大错？或者另有人将在霍格沃茨制造更大的阴谋？这一切又是否与传说中的密室有关？哈利决心揭开谜底……《哈利·波特与阿兹卡班的囚徒》哈利·波特在霍格沃茨魔法学校已经度过了不平凡的两年，而且早已听说魔法世界中有一座守备森严的阿兹卡班监狱，里面关押着一个臭名昭著的囚徒，名字叫小天狼星布莱克。传言布莱克是“黑魔法”高手伏地魔杀害哈利父母的凶手的忠实信徒，曾经用一句魔咒接连结束了十三条性命。不幸的是，布莱克逃出了阿兹卡班，一心追寻哈利。布莱克在睡梦中仍然呓语不休：“他在霍格沃茨……他在霍格沃茨。”哈利·波特虽然身在魔法学校的城堡内，既有朋友的赤诚帮助，也有老师的悉心呵护，但校园内危机四伏，哈利的生命时时受到威胁。一天，布莱克终于站到了哈利的面前……《哈利·波特与火焰杯》哈利·波特在霍格沃茨魔法学校经过三年的学习和磨炼，逐渐成长为一个出色的巫师。新学年开始前，哈利和好朋友罗恩，赫敏一起去观看精彩的魁地奇世界杯赛，无意间发现了消失十三年的黑魔标记。哈利的心头笼上了一团浓重的阴云，但三个少年依然拥有他们自己的伊甸园。然而，少男少女的心思是那样难以捉摸，三人之间的美好友情竟是那样一波三折，忽晴忽雨……哈利渴望与美丽的秋·张共同走进一个美丽的故事，但这个朦朦胧胧的憧憬却遭受了小小的失意。他要做一个普普通通的四年级魔法学生，可不幸的是，哈利注定永远都不可能平平常常即使拿魔法界的标准来衡量。黑魔的阴影始终挥之不去，种种暗藏杀机的神秘事件将哈利一步步推向了伏地魔的魔爪。哈利渴望在百年不遇的三强争霸赛中战胜自我，完成三个惊险艰巨的魔法项目，谁知整个竞赛竟是一个天大的黑魔法阴谋……《哈利·波特与凤凰社》漫长的暑假，哈利·波特被困在女贞路4号，意外地遭到摄魂怪的袭击……逃过一劫的哈利被护送到伦敦一个秘密的地方，在那里他见了自己的教父小天狼星布莱克、前任黑魔法防御术课教师卢平、疯眼汉穆迪以及他的朋友赫敏和罗恩。他只是知道邓布利多与凤凰社成员正在加紧秘密活动，以对抗日益强大的伏地魔。但是，所有的人都不愿向他透露更多的情况……哈利在茫然与愤怒中来到霍格沃茨，然而令他不解的是，邓布利多不愿见他，海格不知去向。更糟糕的是，哈利越来越频繁地在梦中看见一道长长的走廊，每当他快要走进长廊尽头的一扇门时，他都会头痛欲裂地从梦中惊醒，觉得自己身体里蠕动着一条大蛇……魔法部派来了一位专横跋扈的高级调查官，对学校各项事务横加干涉，并最终赶走了邓布利多校长，打伤了麦格教授，整个学校笼罩在一片混乱和压抑之中……大蛇的影子在哈利的脑海里越来越清晰，伏地魔走近了哈利……这时，邓布利多告诉他一个天大的秘密……《哈利·波特与“混血王子”》仲夏的一个夜晚，反常的浓雾笼罩在窗户玻璃上，哈利·波特在女贞路4号德思礼家自己的卧室里紧张地等待着邓布利多教授的来访。哈利不太确定邓布利多是否真的会来德思礼家。邓布利多为什么现在要来看他呢？几个星期之后，他就要返校，邓布利多为什么不能等一等呢？哈利六年级的学习似乎就这样出人意料地提前开始了……而更加出人意料的事情还在接踵而至：邓布利多终于让斯内普教授如愿以偿，任命其担任黑魔法防御术课教师……哈利从教室的储藏柜里翻到一本魔药课本，它的前任主人是“混血王子”，从此哈利在神秘“王子”的帮助下成为“魔药奇才”……邓布利多开始了给哈利的单独授课，但奇怪的是，邓布利多却经常离开学校外出……在邓布利多的课上，哈利经历了几段关于少年伏地魔的惊心动魄的记忆，揭示了伏地魔不同寻常的身世之谜……哈利隐隐觉得这一学期期内普教授和马尔福的关系发生了微妙变化，其中似乎别有一番隐情，而马尔福更是行踪诡秘……哈利试图揭穿马尔福的阴谋，但始终没有成功，直到马尔福把食死徒引进学校，斯内普对邓布利多校长举起了魔杖……《哈利·波特与死亡圣器》还有四天，哈利就要迎来自己十七岁的生日，成为一名真正的魔法师。然而，他不得不提前离开女贞路4号，永远离开这个他曾经生活过十六年的地方。凤凰社的成员精心谋划了秘密转移哈利的计划，以防哈利遭到伏地魔及其追随者食死徒的袭击。然而，可怕的意外还是发生了……与此同时，卷土重来的伏地魔已经染指霍格沃茨魔法学校，占领了魔法部，控制了半个魔法界，形势急转直下……哈利在罗恩、赫敏的陪伴下，不得不逃亡在外，隐形遁迹。为了完成校长邓布利多的遗命，一直在暗中寻机销毁伏地魔魂器的哈利，意外地获悉如果他们能够拥有传说中的三件死亡圣器，伏地魔将必死无疑。但是，伏地魔也早已开始了寻找死亡圣器的行动，并派出众多食死徒，布下天罗地网追捕哈利……哈利与伏地魔在魔法学校的禁林中遭遇了，哈利倒在伏地魔抢先到手的一件致命的圣器之下……然而，伏地魔未能如愿以偿，死亡圣器不可能战胜纯正的灵魂。哈利赢得了这场殊死较量的最终胜利……','24531956','https://book.douban.com/subject/24531956/',' Harry Potter','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('7','飘','[美国]玛格丽特·米切尔','美国','译林出版社','2000-9','1235','40.00元','精装','','9787806570920','9.3','197668','小说中的故事发生在1861年美国南北战争前夕。生活在南方的少女郝思嘉从小深受南方文化传统的熏陶，可在她的血液里却流淌着野性的叛逆因素。随着战火的蔓廷和环境的恶化，郝思嘉的叛逆个性越来越丰满，越鲜明，在一系列的的挫折中她改造了自我，改变了个人甚至整个家族的命运，成为时代时势造就的新女性的形象。作品在描绘人物生活与爱情的同时，勾勒出南北双方在政治，经济，文化各个层次的异同，具有浓厚的史诗风格，堪称美国历史转折时期的真实写照，同时也成为历久不衰的爱情经典。','1068920','https://book.douban.com/subject/1068920/',' Gone with the Wind','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('8','三国演义（全二册）','[明]罗贯中','明','人民文学出版社','1998-05','990','39.50元','平装','中国古典文学读本丛书','9787020008728','9.3','153937','《三国演义》又名《三国志演义》、《三国志通俗演义》，是我国小说史上最著名最杰出的长篇章回体历史小说。《三国演义》的作者是元末明初人罗贯中，由毛纶，毛宗岗父子批改。在其成书前，“三国故事”已经历了数百年的历史发展过程。在唐代，三国故事已广为流传，连儿童都很熟悉。随着市民文艺的发展，宋代的“说话”艺人，已有专门说三国故事的，当时称为“说三分”。元代出现的《三国志平话》，实际上是从说书人使用的本子，虽较简略粗糙，但已初肯《三国演义》的规模。罗贯中在群众传说和民间艺人创作的基础上，又依据陈寿《三国志》及裴松之注中所征引的资料（还包括《世说新语》及注中的资料），经过巨大的创作劳动，写在了规模宏伟的巨著——《三国志通俗演义》全书24卷，240回。后来经过毛纶，毛宗岗父子批改，形成我们现在所见的《三国演义》120回版由于人文出版社选取的毛批版本和明嘉靖版的《三国志通俗演义》颇有出入，将拥刘抑曹的思想发展到一个几乎病态的角度，且大量地删除了原文中赞扬曹操一方人物的诗词和评论，所以给世人造成一种三国演义打击曹操、歌颂刘备的错误印象，且该批改版将刘备一方无限神化，甚至将诸葛亮准备将魏延烧死在上方谷这样的情节统统删除，将诸葛说成是一个完美近乎神人的形象，这是大家阅读时候值得注意的地方。由于该版本选用的是毛批改版，而且没有将毛家父子的批语选入，就会给人造成一种错觉，认为该版本和毛批本是不相同的，其实本书和毛批本完全一致，不过是删除了批语部分，加入了编者的注脚。请读者朋友们阅读时注意','1019568','https://book.douban.com/subject/1019568/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('9','动物农场','[英国]乔治·奥威尔','英国','上海译文出版社','2007-3','119','10.00元','平装','译文名著文库','9787532741854','9.3','140089','《动物农场》是奥威尔最优秀的作品之一，是一则入木三分的反乌托的政治讽喻寓言。农场的一群动物成功地进行了一场“革命”，将压榨他们的人类东家赶出农场，建立起一个平等的动物社会。然而，动物领袖，那些聪明的猪们最终却篡夺了革命的果实，成为比人类东家更加独裁和极权的统治者。','2035179','https://book.douban.com/subject/2035179/',' Animal Farm','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('10','房思琪的初恋乐园','林奕含','中国','北京联合出版公司','2018-2','272','45.00元','平装','','9787559614636','9.2','330141','令人心碎却无能为力的真实故事。向死而生的文学绝唱打动万千读者的年度华语小说。李银河戴锦华骆以军张悦然冯唐詹宏志蒋方舟史航等多位学者作家社会名人郑重推荐。痛苦的际遇是如此难以分享，好险这个世界还有文学。我下楼拿作文给李老师改。他掏出来，我被逼到涂在墙上。老师说了九个字：“不行的话，嘴巴可以吧。”我说了五个字：“不行，我不会。”他就塞进来。那感觉像溺水。可以说话之后，我对老师说：“对不起。”有一种功课做不好的感觉。小小的房思琪住在金碧辉煌的人生里，她的脸和她可以想象的将来一样漂亮。补习班语文名师李国华是同一栋高级住宅的邻居。崇拜文学的小房思琪同样崇拜饱读诗书的李老师。有一天李老师说，你的程度这么好，不如每个礼拜交一篇作文给我吧，不收你周点费。思琪听话地下楼了。老师在家里等她，桌上没有纸笔。思琪的初恋是李老师。因为李老师把她翻面，把他的东西塞进去。那年的教师节思琪才十三岁，这个世界和她原本认识的不一样。如果这是爱情，为什么觉得暴力？为什么觉得被折断？为什么老师要一个女学生换过一个女学生？如果这不是爱情，那满口学问的李老师怎么能做了以后，还这么自信、无疑、无愧于心？故事必须重新讲过，与房思琪情同双胞的刘怡婷，接到警局通知，去带回神志不清，被判定疯了的房思琪。透过思琪的日记，怡婷得知思琪五年中的所见所思……嫁入钱家的许伊纹，是两位少女的忘年交，二十余岁的她，是两位少女的文学启蒙者同时也是丈夫家暴的沉默受害者……升入大学后的郭晓奇仍旧爱着高中时的补习教师李国华，而这位文质彬彬的补习教师并不只有平时人们眼中受人尊敬的老师形象的一面……这是一部惊人而特别的小说，小说作者既具有高度敏锐的感受力、又是一个近距离目击者，使这整件事像一个“幸存的标本”那样地被保留下来。整本书反覆地、用极度贴近被侵害者的视角，直直逼视那种“别人夺去你某个珍贵之物”的痛苦──且掠夺之人是以此为乐。','27614904','https://book.douban.com/subject/27614904/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('11','福尔摩斯探案全集（上中下）','[英]阿瑟·柯南·道尔','英','群众出版社','1981-8','531；706；583','53.00元/68.00元','平装/精装','','9787501408580','9.3','121668','最经典的群众出版社的翻译版本，一经出版，立即风靡成千上万的中国人。离奇的情节，扣人的悬念，世界上最聪明的侦探，人间最诡秘的案情，福尔摩斯不但让罪犯无处藏身，也让你的脑细胞热情激荡，本套书获第一届全国优秀外国文学图书奖。','1040211','https://book.douban.com/subject/1040211/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('12','白夜行','[日]东野圭吾','日','南海出版公司','2013-1-1','538','39.50元','精装','新经典文库·东野圭吾作品','9787544258609','9.2','413486','东野圭吾万千书迷心中的无冕之王周刊文春推理小说年度BEST10第1名本格推理小说年度BEST10第2名《白夜行》是东野圭吾迄今口碑最好的长篇杰作，具备经典名著的一切要素：一宗离奇命案牵出跨度近20年步步惊心的故事：悲凉的爱情、吊诡的命运、令人发指的犯罪、复杂人性的对决与救赎……-1973年，大阪的一栋废弃建筑中发现一名遭利器刺死的男子。案件扑朔迷离，悬而未决。此后20年间，案件滋生出的恶逐渐萌芽生长，绽放出恶之花。案件相关者的人生逐渐被越来越重的阴影笼罩……“我的天空里没有太阳，总是黑夜，但并不暗，因为有东西代替了太阳。虽然没有太阳那么明亮，但对我来说已经足够。凭借着这份光，我便能把黑夜当成白天。我从来就没有太阳，所以不怕失去。”“只希望能手牵手在太阳下散步”，这句象征本书故事内核的绝望念想，有如一个美丽的幌子，随着无数凌乱、压抑、悲凉的事件片段如纪录片一样一一还原，最后一丝温情也被完全抛弃，万千读者在一曲救赎罪恶的爱情之中悲切动容。','10554308','https://book.douban.com/subject/10554308/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('13','小王子','[法国]安东尼·德·圣-埃克苏佩里','法国','人民文学出版社','2003-8','97','22.00元','平装','','9787020042494','9.1','706462','小王子是一个超凡脱俗的仙童，他住在一颗只比他大一丁点儿的小行星上。陪伴他的是一朵他非常喜爱的小玫瑰花。但玫瑰花的虚荣心伤害了小王子对她的感情。小王子告别小行星，开始了遨游太空的旅行。他先后访问了六个行星，各种见闻使他陷入忧伤，他感到大人们荒唐可笑、太不正常。只有在其中一个点灯人的星球上，小王子才找到一个可以作为朋友的人。但点灯人的天地又十分狭小，除了点灯人他自己，不能容下第二个人。在地理学家的指点下，孤单的小王子来到人类居住的地球。小王子发现人类缺乏想象力，只知像鹦鹉那样重复别人讲过的话。小王子这时越来越思念自己星球上的那枝小玫瑰。后来，小王子遇到一只小狐狸，小王子用耐心征服了小狐狸，与它结成了亲密的朋友。小狐狸把自己心中的秘密——肉眼看不见事务的本质，只有用心灵才能洞察一切——作为礼物，送给小王子。用这个秘密，小王子在撒哈拉大沙漠与遇险的飞行员一起找到了生命的泉水。最后，小王子在蛇的帮助下离开地球，重新回到他的B612号小行星上。童话描写小王子没有被成人那骗人的世界所征服，而最终找到自己的理想。这理想就是连结宇宙万物的爱，而这种爱又是世间所缺少的。因此，小王子常常流露出一种伤感的情绪。作者圣埃克絮佩里在献辞中说：这本书是献给长成了大人的从前那个孩子。《小王子》不仅赢得了儿童读者，也为成年人所喜爱，作品凝练的语言渗透了作者对人类及人类文明深邃的思索。它所表现出的讽刺与幻想，真情与哲理，使之成为法国乃至世界上最为著名的一部童话小说。','1084336','https://book.douban.com/subject/1084336/',' Le Petit Prince','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('14','安徒生童话故事集','（丹麦）安徒生','中国','人民文学出版社','1997-08','550','25.00元','精装','世界文学名著文库','9787020017713','9.2','119086','','1046209','https://book.douban.com/subject/1046209/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('15','天龙八部','金庸','中国','生活·读书·新知三联书店','1994-5','1978','96.00元','平装','金庸作品集（三联版）','9787108006721','9.2','124143','天龙八部乃金笔下的一部长篇小说，与《射雕》，《神雕》等几部长篇小说一起被称为可读性最高的金庸小说。《天龙》的故事情节曲折，内容丰富，也曾多次被改编为电视作品。是金庸作品中集大成的一部。故事以北宋末年动荡的社会环境为背景，展开波澜壮阔的历史画卷，塑造了乔峰、段誉、虚竹、慕容复等形象鲜明的人物，成为武侠史上的经典之作。故事精彩纷呈，人物命运悲壮多变，是可读性很强的作品，具有震撼人心的力量','1255625','https://book.douban.com/subject/1255625/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('16','撒哈拉的故事','三毛','中国','哈尔滨出版社','2003-8','217','15.80元','平装','三毛全集（华文天下2003版）','9787806398791','9.2','147897','三毛作品中最脍炙人口的《撒哈拉的故事》，由12篇精彩动人的散文结合而成，其中《沙漠中的饭店》，是三毛适应荒凉单调的沙漠生活后，重新拾笔的第一篇文字，自此之后，三毛便写出一系列以沙漠为背景的故事，倾倒了全世界的中文读者。','1060068','https://book.douban.com/subject/1060068/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('17','杀死一只知更鸟','[美国]哈珀·李','美国','译林出版社','2012-9','346','32.00元','精装','译林纪念版','9787544722766','9.2','127098','成长总是个让人烦恼的命题。成长有时会很缓慢，如小溪般唱着叮咚的歌曲趟过，有时却如此突如其来，如暴雨般劈头盖脸……三个孩子因为小镇上的几桩冤案经历了猝不及防的成长——痛苦与迷惑，悲伤与愤怒，也有温情与感动。这是爱与真知的成长经典。《杀死一只知更鸟》获1961年普利策奖。美国图书馆借阅率最高的书，英国青少年最喜爱的小说之一。美国中学推荐课外读物。由小说改编的电影获第25届奥斯卡三项大奖。美国电影协会评选的“100名银幕英雄与恶人”中，派克主演的芬奇律师名列英雄第一位。作为史上最受喜爱的小说之一，《杀死一只知更鸟》已获得显赫声誉。它赢得过普利策奖，被翻译成四十多种语言，在世界范围内售出超过三千万册，并曾被拍成，备受欢迎的电影。——哈珀•柯林斯出版集团斯塔尔六月对北卡罗来纳州律师协会发表演说时，自比为《杀死一只知更鸟》中勇敢的南方白人律师阿蒂克斯•芬奇。斯塔尔凭着他的道德优越感，不把规则、秩序和正直放在眼里，这样一个人居然自比为芬奇，实在令我和比尔难以忍受。——希拉里•克林顿年轻时看过《梅岗城故事》（即《杀死一只知更鸟》），对片中法律人为弱势者争取权益奋斗，为恶法非法或恶法亦法辩论的故事感到澎湃不已，更加确定要成为法律人的心愿。——许宗力（台大法学院院长）','6781808','https://book.douban.com/subject/6781808/',' To Kill a Mocking Bird','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('18','呐喊','鲁迅','中国','人民文学出版社','1973-3','160','0.36元','平装','鲁迅作品集','','9.2','140632','《呐喊》是鲁迅1918-1922年所作的短篇小说的结集，收有《狂人日记》、《孔乙己》、《药》、《明天》、《一件小事》、《头发的故事》等14篇作品。当时正值五四革命精神高扬时期。作者创作小说意在描写“病态社会的不幸的人们”，“揭出病苦，引起疗救的注意”，并为新文化运动“呐喊”。作品真实地描绘了从辛亥革命到五四时期的社会生活，揭示了种种深层次的社会矛盾，对封建制度及陈腐的传统观念进行了深刻的剖析和彻底的否定，表现出对民族生存的浓重的忧患意识和对社会变革的强烈渴望。','1449351','https://book.douban.com/subject/1449351/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('19','明朝那些事儿（1-9）','当年明月','中国','中国海关出版社','2009-4','','358.20元','精装16开','明朝那些事儿（典藏本）','9787801656087','9.2','146787','《明朝那些事儿》讲述从1344年到1644年，明朝三百年间的历史。作品以史料为基础，以年代和具体人物为主线，运用小说的笔法，对明朝十七帝和其他王公权贵和小人物的命运进行全景展示，尤其对官场政治、战争、帝王心术着墨最多。作品也是一部明朝政治经济制度、人伦道德的演义。','3674537','https://book.douban.com/subject/3674537/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('20','沉默的大多数','王小波','中国','中国青年出版社','1997-10','555','27.00元','平装','王小波作品系列','9787500627098','9.1','138325','这本杂文随笔集包括思想文化方面的文章，涉及知识分子的处境及思考，社会道德伦理，文化论争，国学与新儒家，民族主义等问题；包括从日常生活中发掘出来的各种真知灼见，涉及科学与邪道，女权主义等；包括对社会科学研究的评论，涉及性问题，生育问题，同性恋问题，社会研究的伦理问题和方法问题等；包括创作谈和文论，如写作的动机，作者的师承，作者对小说艺术的看法，作者对文体格调的看法，对影视的看法等；包括少量的书评，其中既有对文学经典的评论，也有对当代作家作品的一些看法；最后，还包括一些域外生活的杂感以及对某些社会现象的评点。','1054685','https://book.douban.com/subject/1054685/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('21','失踪的孩子','[意]埃莱娜·费兰特','意','人民文学出版社','2018-7','480','62.00元','平装','那不勒斯四部曲','9787020139927','9.2','65747','“我的整个生命，只是一场为了提升社会地位的低俗斗争。”全球畅销近千万册被翻译成40多种语言《我的天才女友》《新名字的故事》《离开的，留下的》——那不勒斯四部曲NO.4令人欲罢不能的大结局穷尽友谊对一个人最极致的塑造《失踪的孩子》是“那不勒斯四部曲”的第四部，小说聚焦了莉拉和埃莱娜（“我”）的壮年和晚年，为她们持续了五十多年的友谊划上了一个令人心碎的句号。“我”为了爱情和写作，离开丈夫带着两个女儿回到了那不勒斯，不可避免地与莉拉，还有我曾想要逃离的城区再度变得亲密。“我”和莉拉甚至在同一年怀孕、生子，并经历了恐怖残暴的那不勒斯大地震，一切都分崩离析，一切又将被重建。“我”在不自觉中卷入莉拉秘密的企图——她希望利用我的名声和写作技巧来对抗城区陈腐而猖獗的恶势力。但在经历了生命最恐怖的打击之后，莉拉选择以一种怪异夸张的方式在城区彻底将自己流放。而“我”也将以惊人的韧性和诚实面对写作、爱情、家庭的失败。在经历了多年的自我怀疑之后，“我”决定忤逆（罔顾？）莉拉的告诫，将“我”和她一生的友谊写成一本小说，却招来了最深的背叛……','30172069','https://book.douban.com/subject/30172069/',' Storia della bambina perduta','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('22','新名字的故事','[意]埃莱娜·费兰特','意','人民文学出版社','2017-4','492','59.00元','平装','那不勒斯四部曲','9787020125265','9.2','76227','只有你身为女人才会知道这些丑陋的秘密两个女人50年的友谊和战争如何成为那个更强大的她，又不被她战胜那不勒斯四部曲NO.2《我的天才女友》后续，意大利最神秘的作家费兰特探索青年时代的激情、困惑、挣扎、背叛和失去全球畅销近千万册被翻译成40多种语言希拉里·克林顿、乔纳森·弗兰岑、詹姆斯·伍德、詹姆斯·弗兰科都是她的粉丝-《新名字的故事》是埃莱娜·费兰特的“那不勒斯四部曲”的第二部，描述了埃莱娜和莉拉的青年时代。在她们的人生以最快的速度急遽分化的那些年里，她们共同体验了爱、失去、困惑、挣扎、嫉妒和隐蔽的破坏。莉拉在结婚当天就发现婚姻根本不是她想象的那样，她的初夜几乎是一场强奸。她带着一种强大的破坏欲介入了斯特凡诺的家族生意，似乎成为了她和埃莱娜小时候都想成为的那种女人。久未有身孕的莉拉，和埃莱娜去海边度假休养。而在伊斯基亚岛的那个夏天，改变了所有人的一生……出于对莉拉所拥有的爱情的愤怒，“我”（埃莱娜）奋力摆脱这个破败、暴力、充满宿仇的街区。“我”成了街区的第一个大学生，并和一个高级知识分子家庭的男孩订婚，甚至出版了第一本小说。“我”以胜利者的形象回到那不勒斯，却发现告别了丑陋婚姻的莉拉，在一家肉食加工厂备受屈辱地打工。当“我”发现自己的小说，其实完全窃取了莉拉交托给“我”的秘密笔记本里那些独特的力量和灵感，“我”被迫面临一个极度痛苦的问题：“我”和莉拉，到底谁离开了，又是谁留下了？无论是生活还是小说中，一段友谊里，更强大、性格更丰富的那个人会让软弱的那个人的形象变得模糊。但是在埃莱娜和莉拉的关系中，处于弱势的埃莱娜，从自身的从属位置中获得了某种才智——那让莉拉失去了方向，心晕目眩。这种过程很难描述，但也正是这一点让我着迷。可以这么说：在莉拉和埃莱娜的生命中，有很多事件显示了一个人如何从另一人身上汲取力量。但要记住这一点：这不仅表现在她们帮助彼此的层面上，同样也体现为她们互相洗劫，从对方身上窃取情感和知识，消耗对方的力量。——埃莱娜•费兰特答意大利《晚邮报》采访（2011）那不勒斯四部曲精彩而又持久地探索了嫉妒——这种最为致命的情感，因它有时候会将自己伪装成爱。——《纽约书评》关于女性友谊和命运的意大利史诗……在荷马和奥维德之后，我们第一次有了一个女性史诗作者。——《大西洋月刊》费兰特的行文如此清澈，她的叙述如此率真，摄人心魄，引人入胜。——《经济学人》对于费兰特而言，小说的首要目的，就是就搅动那些感染的、敞开的伤口，捅开它，让你意识到自己的伤口在哪里，这伤口又从何而来。——《洛杉矶书评》埃莱娜必须挣脱她从小要面对的那些令人难以忍受的价值，抵抗世俗意义上的成功的诱惑，并从莉拉身上获得知识和力量，却又不能被她压倒……现代小说中对友谊最丰富的描写。——NPR每个人都应该看有埃莱娜·费兰特署名的任何文字。——《波士顿环球报》','26986954','https://book.douban.com/subject/26986954/',' Storia del nuovo cognome','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('23','中国历代政治得失','钱穆','中国','生活·读书·新知三联书店','2001','161','12.00元','平装','钱穆作品系列','9787108015280','9.2','62154','《中国历代政治得失》为作者的专题演讲合集，分别就中国汉、唐、宋、明、清五代的政府组织、百官职权、考试监察、财经赋税、兵役义务等种种政治制度作了提要勾玄的概观与比照，叙述因革演变，指陈利害得失。既高屋建瓴地总括了中国历史与政治的精要大义，又点明了近现代国人对传统文化和精神的种种误解。言简意赅，语重心长，实不失为一部简明的“中国政治制度史”。','1003479','https://book.douban.com/subject/1003479/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('24','局外人','[法]阿尔贝·加缪','法','上海译文出版社','2010-8','128','22.00元','精装','译文经典（精装本）','9787532751471','9.1','206819','《局外人》是法国作家加缪的成名作，同时也是存在主义文学的杰出作品之一，该书以一种客观记录式的“零度风格”，粗线条地描述了主人公默尔索在荒谬的世界中经历的种种荒谬的事，以及自身的荒诞体验。从参加母亲的葬礼到偶然成了杀人犯，再到被判处死刑，默尔索似乎对一切都无动于衷，他像一个象征性的符号，代表了一种普遍的存在，又像是一个血红色的灯塔，具有高度的警示性。然而，局外人现象的产生无疑是由这个世界本身所孕育的，默尔索的存在有其深刻的外部原因。','4908885','https://book.douban.com/subject/4908885/',' LEtranger','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('25','人类简史','[以色列]尤瓦尔·赫拉利','以色列','中信出版社','2014-11','440','68.00元','平装','“中信史学大师畅销经典”系列','9787508647357','9.1','181358','十万年前，地球上至少有六种不同的人但今日，世界舞台为什么只剩下了我们自己？从只能啃食虎狼吃剩的残骨的猿人，到跃居食物链顶端的智人，从雪维洞穴壁上的原始人手印，到阿姆斯壮踩上月球的脚印，从认知革命、农业革命，到科学革命、生物科技革命，我们如何登上世界舞台成为万物之灵的？从公元前1776年的《汉摩拉比法典》，到1776年的美国独立宣言，从帝国主义、资本主义，到自由主义、消费主义，从兽欲，到物欲，从兽性、人性，到神性，我们了解自己吗？我们过得更快乐吗？我们究竟希望自己得到什么、变成什么？【编辑推荐】1、由书改变而成的人类简史课程风靡全球，成为希伯来大学最受欢迎课程Coursera最受欢迎课程，mooc最受欢迎课程。学界和大众都一致认可，戴蒙德推荐。2、一大开创性：打通文字发明前后历史的界限。文字发明之前的年代，是生物学家、考古学家的专长；文字发明之后的年代，是历史学家、政治学家和经济学家的专长；许多著名史家的作品，欠缺遗传学或生态学的视野，如何让这两大段历史之间没有断层，能够有一以贯之的宏观解读？本书不同于《枪炮、病菌与钢铁》从生物与环境的角度看人类社会的发展，也不同于麦克尼尔的《世界史》以文明为单位绘制的人类文明交织的世界。他笔下的历史，在科学中有深沉的人文关怀。3、填补传统人类史的三大鸿沟：历史观与哲学观之间的鸿沟（提供有史实根据的深刻哲学思考）；人类和生态系统之间的鸿沟（作者多从生态来思考，而不是只讲人类的利益）；集体和个人之间的鸿沟（检视历史事件如何影响到当时一般人的生活）。4、四大部分描述人类大历史：7万年前的大脑认知革命（有能力谈八卦，想象不存在的事物，让陌生人开始合作、建立组织）1.2万年前的农业革命（让我们渴求更多、生产更多，分工分职愈趋细腻）、500年前的科学革命（带来快速进步，让我们拥有上帝的力量，也带来毁灭）全球大一统、人类大融合的关键因素——金钱、帝国、宗教5、一部个人幸福探索之书。用通俗的语言和新鲜的视角，将个体的幸福放在人类发展的过程中审视，不再错过历史中最关键、最有意思的部分。涵盖了生物学，人类学，哲学，心理学，艺术，文学，伦理学等众多领域。涉及幸福，生命的意义等众多话题，有人说这是一部个人幸福探索之书。6、一部“瘦身版”人类简史。如果说给我一本书，不到五百页，没有一堆令人晕头转向的年份、人名、地名、称号，就能涵盖了人类如何崛起、影响现代生活甚巨的资本主义、一神教、自由人文主义、基因工程如何兴盛的人类历史重大脉络……这是仅有的一部！7、一部引发多国版权大战的神秘大书。获得波兰斯基人文学科创造力与独创性奖。100周蝉联以色列畅销书排行榜第一，在英国，9家出版商疯狂竞价。23个国家竞相购买版权。台湾上市一周，金石堂、诚品历史类畅销书榜第一，诚品总榜第八，西班牙上市两周总榜第一。【内容简介】《人类简史：从动物到上帝》是以色列新锐历史学家的一部重磅作品。从十万年前有生命迹象开始到21世纪资本、科技交织的人类发展史。十万年前，地球上至少有六个人种，为何今天却只剩下了我们自己？我们曾经只是非洲角落一个毫不起眼的族群，对地球上生态的影响力和萤火虫、猩猩或者水母相差无几。为何我们能登上生物链的顶端，最终成为地球的主宰？从认知革命、农业革命到科学革命，我们真的了解自己吗？我们过得更加快乐吗？我们知道金钱和宗教从何而来，为何产生吗？人类创建的帝国为何一个个衰亡又兴起？为什么地球上几乎每一个社会都有男尊女卑的观念？为何一神教成为最为广泛接受的宗教？科学和资本主义如何成为现代社会最重要的信条？理清影响人类发展的重大脉络，挖掘人类文化、宗教、法律、国家、信贷等产生的根源。这是一部宏大的人类简史，更见微知著、以小写大，让人类重新审视自己。【各界评论】《人类简史：从动物到上帝》为什么能够在国际畅销书榜上爆冲？原因很简单，它处理的是历史的大问题、现代世界的大问题，而且，它的写作风格是刻骨铭心的生动。你会爱上它！——普利策奖得主《枪炮、病菌与钢铁》作者戴蒙德这是此类大历史中最好的一本：规模宏大但绝非泛泛而谈；具有时代性又不花哨，观念惊世骇俗但绝不是个简单的反对者。每个人都能在书里找到一部分和另外的人开始讨论，但是弄清楚是哪部分、为什么，对我们都有好处。——牛津大学历史学院院长斯蒂文·刚尼我拿起这本《人类简史》立刻“着道”了——拿起了就放不下，几乎一口气读完。吸引力主要来自作者才思的旷达敏捷，还有译者文笔的生动晓畅，而书中屡屡提及中国的相关史实，也能让人感到一种说不出的亲切，好像自己也被融入其中，读来欲罢不能。——北京大学历史系教授高毅《人类简史》的目的不是传授人类考古学的所有研究成果，而是提供一种看历史的视角，一种全局的观点。当你不再执着于科学、政治或宗教等某一个领域的发展过程，而是关注人类社会的整体演变，观察这些领域之间的相互作用，你会感到你脑海中零碎的历史知识忽然像拼图一样各就各位，构成一幅宏大的图景，这样的视角非常新鲜。——果壳网姬十三读完这本书，有一种跟着人类一同走过十万年的感觉！——读者这本书如果要用“宏伟史诗”来形容一点也不过分，其中包含了很多学科的内容，例如考古学、生物学、物理学、经济学、宗教、社会学甚至计算机科学等等。书里里面的大部分观点都是从不同的角度进行介绍，其中很多都是经无数人严格验证过的，讲师很多时候仅仅是通过自己的表达希望尽量客观地介绍一些内容，至于每个人怎么理解并没有统一的标准了。——读者超级好的课程，付费都坚持下来了，观点独特，从来没想到能从这个角度理解我们人类自己。——读者','25985021','https://book.douban.com/subject/25985021/',' A brief history of humankind','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('26','邓小平时代','[美]傅高义','美','生活·读书·新知三联书店','2013-1','754','88.00元','平装','','9787108041531','9.3','54476','邓小平深刻影响了中国历史和世界历史的走向，也改变了每一当代中国人的命运。解读邓小平的政治生涯及其行为逻辑，就是解读当代中国，解读个人命运背后的历史变局。哈佛大学傅高义教授倾十年心力完成的权威巨著《邓小平时代》，是对邓小平跌宕起伏的一生以及中国惊险崎岖的改革开放之路的全景式描述。作者对邓小平个人性格及执政风格进行了深层分析，并对中国改革开放史进行了完整而独到的阐释。书中涵盖丰富的中外档案资料和研究成果，以及为数众多的作者独家访谈。对中美建交、华国锋历史评估、改革开放的幕后曲折、邓小平和诸多当时关键人物之关系、香港问题、邓小平南巡等重大议题均有详述及剖析。本书既具学院研究的严谨专精，又对中国现实政治与事理人情有透彻把握，被誉为邓小平研究“纪念碑式”的著作。','20424526','https://book.douban.com/subject/20424526/',' Deng Xiaoping and the Transformation of China','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('27','围城','钱锺书','中国','人民文学出版社','1991-2','359','19.00','平装','','9787020024759','9','428183','《围城》是钱钟书所著的长篇小说。第一版于1947年由上海晨光出版公司出版。1949年之后，由于政治等方面的原因，本书长期无法在中国大陆和台湾重印，仅在香港出现过盗印本。1980年由作者重新修订之后，在中国大陆地区由人民文学出版社刊印。此后作者又曾小幅修改过几次。《围城》自从出版以来，就受到许多人的推崇。由于1949年后长期无法重印，这本书逐渐淡出人们的视野。1960年代，旅美汉学家夏志清在《中国现代小说史》(AHistoryofModernChineseFiction)中对本书作出很高的评价，这才重新引起人们对它的关注。人们对它的评价一般集中在两方面，幽默的语言和对生活深刻的观察。从1990年代开始，也有人提出对本书的不同看法，认为这是一部被“拔高”的小说，并不是一部出色的作品。很多人认为这是一部幽默作品。除了各具特色的人物语言之外，作者夹叙其间的文字也显着机智与幽默。这是本书的一大特色。也有人认为这是作者卖弄文字，语言显得尖酸刻薄。但这一说法并不为大多数人接受。','1008145','https://book.douban.com/subject/1008145/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('28','平凡的世界（全三部）','路遥','中国','人民文学出版社','2005-1','1631','64.00元','平装','茅盾文学奖获奖作品全集','9787020049295','9','303950','《平凡的世界》是一部现实主义小说，也是一部小说形式的家族史。作者浓缩了中国西北农村的历史变迁过程，在小说中全景式地表现了中国当代城乡的社会生活。在近十年的广阔背景下，通过复杂的矛盾纠葛，刻划社会各阶层众多普通人的形象。劳动与爱情，挫折与追求，痛苦与欢乐，日常生活与巨大社会冲突，纷繁地交织在一起，深刻地展示了普通人在大时代历史进程中所走过的艰难曲折的道路。','1200840','https://book.douban.com/subject/1200840/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('29','野草','鲁迅','中国','人民文学出版社','1973-3','62','0.20元','平装','鲁迅作品集','','9.5','38265','本书所收散文诗23篇〔包括一首打油诗和一出诗剧〕，最初都曾陆续发表于1924年12月至1926年1月的《语丝》周刊上，《题辞》最初也曾发表于1927年7月2日出版的《语丝》第138期，发表时署名均为鲁迅。本书于于年1927年4月由作者亲自编定，同年7月由上海北新书局初版印行列为作者所编的《乌合丛书》之一。此后印行的版本，除个别字和标点有所不同外，各篇文字大都和初版相同。《题辞》在本书最初的几次印刷都曾印入；后来被国民党政府书报检查机关抽去〔鲁迅在1935年11月23日致邱遇信和1936年2月19日致夏传经信中，均提及此事〕，至1941年上海鲁迅全集出版社出版《鲁迅三十年集》时才重新收入。《野草》的封面画系孙福熙所作，初版封面题字署“鲁迅先生”，后按鲁迅意思改为“鲁迅著”，鲁迅在1927年12月9日夜致章廷谦信中曾提及此事。鲁迅写作《野草》时，适值“五四”退潮，正如作者在《〈自选集〉自序》〔《南腔北调集》〕中所说：“后来《新青年》的团体散掉了，有的高升，有的退隐，有的前进，我又经验了一回同一战阵中的伙伴还是会这么变化”，在这种情况下，“有了小感触，就写些短文，夸大点说，就是散文诗，以后印成一本，谓之《野草》。”编成本书的时候，如《题辞》篇末所记，正在蒋介石发动“四·一二”清党的稍后。关于本书各篇作品，作者在1931年曾为本书的英译本写过一篇短序，作了一些说明，收在《二心集》中。在1934年10月9日致萧军信中，鲁迅谈到《野草》时说：“我的那本《野草》，技术不算坏，但心情太颓唐了，因为那是我碰了许多钉子之后写出来的”。在《华盖集续编·海上通信》中说：“至于《野草》，此后做不做很难说，大约是不见得再做了，省得人来谬托知己，舐皮论骨，什么是‘入于心’的。”','1915958','https://book.douban.com/subject/1915958/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('30','乡土中国','费孝通','中国','上海人民出版社','2006-04-01','426','38.00','平装','名家名著·文化史','9787208062153','9.2','71843','著名社会学家费孝通教授，一生行行重行行，实地调查和考察总结中国农村经济发展的各种模式，写下了诸多不朽篇章。本书推出的是学界共认的中国乡土社会传统文化和社会结构理论研究的代表作《乡土中国》、《生育制度》、《乡土重建》和《皇权与绅权》四篇著作，可供社会学工作或教学、研究者参考。','1795079','https://book.douban.com/subject/1795079/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('31','悉达多','[德]赫尔曼·黑塞','德','天津人民出版社','2017-1','144','32.00元','精装','果麦·外国文学经典','9787201112695','9.3','46286','《悉达多》并非是佛陀的故事，它讲述了一个人的一生，千万寻常人亦会经历的一生。意气风发的少年郎，常认为自己是被命运选中的人。抛下过去，随了跌跌撞撞的步伐，找寻心中的声音，追逐名利，经历友情，品尝爱情。不同的是，悉达多一生追求的是生命的圆融统一，看似宏大，确是每个人无法回避的问题。黑塞的语言是充满诗性的，正如本书副题“一首印度的诗”，因不得而知的原因，此副题在前人所出版本中都被回避了。为了尽可能表现黑塞的诗意，我们选择从德文直译，尽管我们的译文不能完全实现这种诗意，但其诗的本性与精神显而易见。本书地位在前人的版本中已被反复强调——亨利·米勒的最爱；影响了包括电台司令在内的许多名人雅士；在六十年代美国掀起过阅读黑塞的热潮，大学生们人手一册等等。但如书中成为摆渡人的悉达多所述，智慧无法分享，它可以被发现，被体验。所以倘若你碰巧遇到了这本小书，请细细品味其中的文字。愿你在读了悉达多的故事后，也能有所感悟，并开始体验属于自己的人生。','26980487','https://book.douban.com/subject/26980487/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('32','笑傲江湖（全四册）','金庸','中国','生活·读书·新知三联书店','1994-5','1599','76.80元','平装','金庸作品集（三联版）','9787108006639','9.1','104723','《笑傲江湖》是金庸1967年写的一部武侠小说，属于金庸的后期作品。','1002299','https://book.douban.com/subject/1002299/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('33','白鹿原','陈忠实','中国','人民文学出版社','2012-9','697','39.00元','精装','','9787020090297','9.3','89182','在从清末民初到建国之初的半个世纪里，一阵阵狂风掠过了白鹿原上空，而每一次的变动都震荡着它的内在结构：打乱了再恢复，恢复了再打乱，细腻地反映出白姓和鹿姓两大家族祖孙三代的恩怨纷争。陈忠实先生在这里，人物的命运是纵线，百回千转，社会历史的演进是横面，愈拓愈宽，传统文化的兴衰则是全书的精神主体，以至人、社会历史、文化精神三者之间相互激荡，相互作用，共同推进了作品的时空，在我们眼前铺开了一轴恢宏的、动态的、极富纵深感的关于我们民族灵魂的现实主义的画卷。新版《白鹿原》特色：1、完整呈现了1993年最原始版本。2、全新精装本。内附多幅彩插，包括作者旧照、手稿影印件。更有电影《白鹿原》的美术设计师创作的精美插图首次面世。3、全书浓缩着深沉的民族历史内涵，有令人震撼的真实感和厚重的史诗风格。4、本书1997年荣获中国长篇小说最高荣誉第四届茅盾文学奖。已被改编成同名话剧、电影、舞剧、秦腔等多种艺术形式。','10564071','https://book.douban.com/subject/10564071/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('34','霍乱时期的爱情','[哥伦比亚]加西亚·马尔克斯','哥伦比亚','南海出版公司','2012-9-1','401','39.50元','精装','新经典文库：加西亚·马尔克斯作品','9787544258975','9','256347','★马尔克斯唯一正式授权，首次完整翻译★《霍乱时期的爱情》是我最好的作品，是我发自内心的创作。——加西亚•马尔克斯★这部光芒闪耀、令人心碎的作品是人类有史以来最伟大的爱情小说。——《纽约时报》《霍乱时期的爱情》是加西亚•马尔克斯获得诺贝尔文学奖之后完成的第一部小说。讲述了一段跨越半个多世纪的爱情史诗，穷尽了所有爱情的可能性：忠贞的、隐秘的、粗暴的、羞怯的、柏拉图式的、放荡的、转瞬即逝的、生死相依的……再现了时光的无情流逝，被誉为“人类有史以来最伟大的爱情小说”，是20世纪最重要的经典文学巨著之一。','10594787','https://book.douban.com/subject/10594787/',' El amor en los tiempos del cólera','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('35','肖申克的救赎','[美]斯蒂芬·金','美','人民文学出版社','2006-7','466','29.90元','平装','99畅销文库：斯蒂芬·金','9787020054985','9.1','111746','这本书收入斯蒂芬·金的四部中篇小说，是他作品中的杰出代表作。其英文版一经推出，即登上《纽约时报》畅销书排行榜的冠军之位，当年在美国狂销二十八万册。目前，这本书已经被翻译成三十一种语言，同时创下了收录的四篇小说中有三篇被改编成轰动一时的电影的记录。其中最为著名是曾获奥斯卡奖七项提名、被称为电影史上最完美影片的《肖申克救赎》（又译《刺激一九九五》）。这部小说展现了斯蒂芬·金于擅长的惊悚题材之外的过人功力。书中的另两篇小说《纳粹高徒》与《尸体》拍成电影后也赢得了极佳的口碑。其中《尸体》还被视为斯蒂芬·金最具自传色彩的作品。','1829226','https://book.douban.com/subject/1829226/',' Different Seasons','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('36','东方快车谋杀案','[英]阿加莎·克里斯蒂','英','人民文学出版社','2006-5','254','18.00元','平装','阿加莎·克里斯蒂侦探推理系列','9787020056309','9.1','127092','午夜过后，一场大雪迫使东方快车停了下来。这辆豪华列车整年都处于满员状态。但那天早上却发现少了一名乘客。一个美国人死在了他的包厢里，他被刺了十二刀，可他包厢的门却是反锁着的。随着紧张气氛逐渐增强，侦探赫尔克里·波洛想出了两个侦破此案的方法……','1827374','https://book.douban.com/subject/1827374/',' Murder on the Orient Express','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('37','基督山伯爵','[法国]大仲马','法国','上海译文出版社','1991-12-1','1453','43.90元','精装','','9787532712243','9.1','121749','小说以法国波旁王朝和七月王朝两大时期为背景，描写了一个报恩复仇的故事。法老号大副唐泰斯受船长的临终嘱托，为拿破仑送了一封信，受到两个对他嫉妒的小人的陷害，被打入死牢，狱友法里亚神甫向他传授了各种知识，还在临终前把一批宝藏的秘密告诉了他。他设法越狱后找到了宝藏，成为巨富。从此他化名为基督山伯爵，经过精心策划，报答了他的恩人，惩罚了三个一心想置他于死地的仇人。','1085860','https://book.douban.com/subject/1085860/',' Le Comte de Monte-Cristo','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('38','江城','[美国]彼得·海斯勒','美国','上海译文出版社','2012-1','446','36.00元','平装','译文纪实','9787532756728','9.1','54872','《寻路中国》姐妹篇、纪实中国三部曲序曲奇里雅玛环太平洋图书奖《纽约时报》、《经济学人》、《纽约客》、《时代周刊》《华尔街日报》、《国家地理》、《华盛顿邮报》联合推荐！1996年8月底一个温热而清朗的夜晚，我从重庆出发，乘慢船，顺江而下来到涪陵。涪陵没有铁路，历来是四川省的贫困地区，公路非常糟糕。去哪里你都得坐船，但多半你哪里也不会去。在随后的两年，这座城市就是我的家。在这里，我有时是一个旁观者，有时又置身于当地的生活之中，这种亲疏结合的观察构成了我在四川停留两年的部分生活。2001年，也就是这本书在美国出版的时候，一条通往重庆的高速公路通车了，一条铁路也正在修建之中，基本上再也没有人坐船去涪陵了。这座城市正在飞速发展着，在过去的二十年，那样一种转型变化的感觉——接二连三、冷酷无情、势不可挡——正是中国的本质特征。很难相信，这个国家曾经完全是另外一种模样，是19世纪西方人眼中“永远停滞的民族”。2003年，三峡大坝一期完工后，不断上涨的江水将陆续淹没那些江畔之城，这多少令我有些伤感。而对于大多数中国人来说，这正是不断变革的对应面：贫穷、烂路、慢船。这并不是一本关于中国的书，它只涉及一小段特定时期内中国的某个小地方。从地理和历史上看，涪陵都位于江河中游，所以人们有时很难看清她从何而来，又去往何处。在1996年至1998年间，我学会了热爱涪陵。能再次回到长江上的感觉真好，哪怕它的旧时激流只存于我的记忆之中。“充满着坦率、热情、洞察力和亲历接触，《江城》雄辩地展示了鲜为人知的当代中国。读来精彩。”－－哈金（美籍华裔作家，美国国家图书奖得主）“如果你只读一本关于中国的书，那就是这本了。”－－梅兆赞（英国资深记者、中国问题专家）“何伟的笔下是真中国，是连一些生活在中国的青年人都不知道或拒绝认识的中国。”－－《南方人物周刊》','7060185','https://book.douban.com/subject/7060185/',' River Town','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('39','我与地坛','史铁生','中国','春风文艺出版社','2002-5','399','25.00元','平装','春风文艺社·新经典文库','9787531324362','9.2','89364','收有“午餐半小时”、“我的遥远的清平湾”、“命若琴弦”、“第一人称”、“两个故事”等15篇史铁生的代表作。','1209899','https://book.douban.com/subject/1209899/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('40','彷徨','鲁迅','中国','人民文学出版社','1973-3','162','0.37元','平装','鲁迅作品集','','9.2','54717','《彷徨》收入鲁迅1924年至1925年所作的小说，首篇《祝福》写于1924年2月16日，末篇《离婚》写于1925年11月6日，实际的时间跨度是一年半多，1926年由北京北新书局出版，列为作者所编的《乌合丛书》之一。此后印行的版本都与初版同。书的扉页，有作者的题记：朝发轫于苍梧兮，夕余至乎县圃；欲少留此灵琐兮，日忽忽将其暮。吾令羲和弭节兮，望崦嵫而勿迫；路漫漫其修远兮，吾将上下而求索。《彷徨》鲁迅先生写作于“五·四”运动后新文化阵营分化的时期。原来参加过新文化运动的人，“有的退隐，有的高升，有的前进”，鲁迅当时象布不成阵的游勇那样“孤独”和“彷徨”。《彷徨》表现了他在这一时期在革命征途上探索的心情。《彷徨》目录：《祝福》《在酒楼上》《幸福的家庭》《肥皂》《长明灯》《示众》《高老夫子》《孤独者》《伤逝》《弟兄》《离婚》','1449348','https://book.douban.com/subject/1449348/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('41','月亮和六便士','[英]毛姆','英','上海译文出版社','2006-8','275','15.00元','平装','译文名著文库','9787532739547','9','190693','一个英国证券交易所的经纪人，本已有牢靠的职业和地位、美满的家庭，但却迷恋上绘画，像“被魔鬼附了体”，突然弃家出走，到巴黎去追求绘画的理想。他的行径没有人能够理解。他在异国不仅肉体受着贫穷和饥饿煎熬，而且为了寻找表现手法，精神亦在忍受痛苦折磨。经过一番离奇的遭遇后，主人公最后离开文明世界，远遁到与世隔绝的塔希提岛上。他终于找到灵魂的宁静和适合自己艺术气质的氛围。他同一个土著女子同居，创作出一幅又一幅使后世震惊的杰作。在他染上麻风病双目失明之前，曾在自己住房四壁画了一幅表现伊甸园的伟大作品。但在逝世之前，他却命令土著女子在他死后把这幅画作付之一炬。通过这样一个一心追求艺术、不通人性世故的怪才，毛姆探索了艺术的产生与本质、个性与天才的关系、艺术家与社会的矛盾等等引人深思的问题。同时这本书也引发了人们对摆脱世俗束缚逃离世俗社会寻找心灵家园这一话题的思考，而关于南太平洋小岛的自然民风的描写也引人向往。《月亮和六便士》说问世后，以情节入胜、文字深刻在文坛轰动一时，人们争相传看。在小说中，毛姆用第一人称的叙述手法，借“我”之口，叙述整个故事，有人认为这篇小说的原型是法国印象派画家高更，这更增加了它的传奇色彩，受到了全世界读者的关注。','1858513','https://book.douban.com/subject/1858513/',' The Moon and Sixpence','傅惟慈');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('42','献给阿尔吉侬的花束','[美]丹尼尔·凯斯','美','广西师范大学出版社','2015-4','288','36.00元','平装','','9787549565115','9.1','74583','声称能改造智能的科学实验在白老鼠阿尔吉侬身上获得了突破性的进展，下一步急需进行人体实验。个性和善、学习态度积极的心智障碍者查理·高登成为最佳人选。手术成功后，查理的智商从68跃升为185，然而那些从未有过的情绪和记忆也逐渐浮现。','26362836','https://book.douban.com/subject/26362836/',' Flowers for Algernon','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('43','万历十五年','[美]黄仁宇','美','生活·读书·新知三联书店','1997-5','320','18.00元','平装','黄仁宇作品系列','9787108009821','9','184380','万历十五年，亦即公元1587年，在西欧历史上为西班牙舰队全部出动征英的前一年；而在中国，这平平淡淡的一年中，发生了若干为历史学家所易于忽视的事件。这些事件，表面看来虽似末端小节，但实质上却是以前发生大事的症结，也是将在以后掀起波澜的机缘。在历史学家黄仁宇的眼中，其间的关系因果，恰为历史的重点，而我们的大历史之旅，也自此开始……《万历十五年》是黄仁宇的成名之作，也是他的代表作之一。这本书融会了他数十年人生经历与治学体会，首次以“大历史观”分析明代社会之症结，观察现代中国之来路，给人启发良多。英文原本推出后，被美国多所大学采用为教科书，并两次获得美国书卷奖历史类好书的提名。','1041482','https://book.douban.com/subject/1041482/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('44','城南旧事','林海音文/关维兴图','中国','中国青年出版社','2003-7','198','16.00元','平装','现当代文学名著系列','9787500652045','9.1','139671','多少年来，《城南旧事》感动了一代又一代的读者，除了再版无数次的小说版外，1985年，本书在中国大陆搬上银幕，电影“城南旧事”获得“中国电影金鸡奖”、第二届“马尼拉国际电影节最佳故事片奖金鹰奖章”、第十四届“贝尔格勒国际儿童电影节最佳影片思想奖”等多项大奖。儿童绘本版《城南旧事》由当今中国极具代表性的水彩画家关维兴，运用优雅诗意的风格和穿透人心的独到技巧，将创作的图画全然融入故事的情节。画中举凡服饰、器物和建筑，考据详实，没一点随便或含糊。人物细致的表情、光线和空气的律动、圆熟丰美的调子，使文图呈现无懈可击的呼应。绘本《城南旧事》甫出版即获选1993、1994年“波隆那国际儿童书插画展”、1993年“布拉迪斯国际插画双年展”以及1994年“加泰隆尼亚国际插画双年展”。从小说到电影，从成人书到儿童绘本，《城南旧事》是故事、是梦幻，读时仿若音乐，轻轻扣动人心，字里行间所隐涵的深意，更令人撼动，久久不能自已。','1254588','https://book.douban.com/subject/1254588/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('45','罗杰疑案','[英]阿加莎·克里斯蒂','英','新星出版社','2013-3','246','28.00元','平装','午夜文库·大师系列：阿加莎·克里斯蒂作品','9787513311236','9.2','53880','金斯艾伯特村里坐落着两座豪宅，“皇家围场”是其中之一，而居住其中的弗拉尔斯太太刚刚过世。此后不久，她的情人，也就是住在另一所大宅里的罗杰•艾克罗伊德先生便得知，弗拉尔斯太太一年前杀害了自己的丈夫，最终因愧疚而自杀。更重要的是，罗杰还发现她在死之前一直被一个神秘人敲诈。他展开她最后的来信，打算读出那个人的名字，可惜没有人听到。因为一把短剑插进了他的后颈……','21371175','https://book.douban.com/subject/21371175/',' The Murder of Roger Ackroyd','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('46','艺术的故事','[英国]E·H·贡布里希','英国','广西美术出版社','2008-04','688','280.00','平装','','9787807463726','9.6','23002','《艺术的故事》概括地叙述了从最早的洞窟绘画到当今的实验艺术的发展历程，以阐明艺术史是“各种传统不断迂回、不断改变的历史，每一件作品在这历史中都既回顾过去又导向未来。继1997年三联版《艺术的故事》后，英国费顿出版社授权广西美术出版社独家出版，也是国内唯一合法授权的版本。','3162991','https://book.douban.com/subject/3162991/',' The Story of Art','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('47','卡拉马佐夫兄弟','[俄]费奥多尔·陀思妥耶夫斯基','俄','上海译文出版社','2006-8','847','25.00元','平装','译文名著文库','9787532740086','9.5','25694','老卡拉马佐夫贪婪好色，独占妻子留给儿子们的遗产，并与长子德米特里为一个风流女子争风吃醋。一天黑夜，德米特里疑心自己的情人去跟老头儿幽会，便闯入家园，一怒之下，差点把老头儿砸死。他仓皇逃离后，躲在暗中装病的老卡拉马佐夫的私生子斯乜尔加科夫悄然杀死老爷，造成了一桩震惊全俄的扑朔迷离的血案，从而引发一连串惊心动魄的事件。作品展示了错综复杂的社会、家庭矛盾和人性悲剧，体现了作家一生的最高艺术成就。','1856494','https://book.douban.com/subject/1856494/',' Бра́тья Карама́зовы','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('48','射雕英雄传','金庸','中国','生活·读书·新知三联书店','1999-04','1263','47.00元','平装','金庸作品集（三联口袋本）','9787108012586','9.1','78908','《射雕英雄传》是金庸的代表作之一，作于一九五七年到一九五九年，在《香港商报》连载。《射雕》中的人物个性单纯，郭靖诚朴厚重、黄蓉机智狡狯，读者容易印象深刻。这是中国传统小说和戏剧的特征，但不免缺乏人物内心世界的复杂性。由于人物性格单纯而情节热闹，所以《射雕》比较得到欢迎，被拍成各种语种的电影和电视剧在全球众多国家和地区热播。','1044547','https://book.douban.com/subject/1044547/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('49','追风筝的人','[美]卡勒德·胡赛尼','美','上海人民出版社','2006-5','362','29.00元','平装','卡勒德·胡赛尼作品','9787208061644','8.9','756659','12岁的阿富汗富家少爷阿米尔与仆人哈桑情同手足。然而，在一场风筝比赛后，发生了一件悲惨不堪的事，阿米尔为自己的懦弱感到自责和痛苦，逼走了哈桑，不久，自己也跟随父亲逃往美国。成年后的阿米尔始终无法原谅自己当年对哈桑的背叛。为了赎罪，阿米尔再度踏上暌违二十多年的故乡，希望能为不幸的好友尽最后一点心力，却发现一个惊天谎言，儿时的噩梦再度重演，阿米尔该如何抉择？故事如此残忍而又美丽，作者以温暖细腻的笔法勾勒人性的本质与救赎，读来令人荡气回肠。','1770782','https://book.douban.com/subject/1770782/',' The Kite Runner','李继宏');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('50','许三观卖血记','余华','中国','作家出版社','2012-9','264','24.00元','平装','余华作品（2012版）','9787506365680','9.1','117963','《许三观卖血记》是余华1995年创作的一部长篇小说。《许三观卖血记》以博大的温情描绘了磨难中的人生，以激烈的故事形式表达了人在面对厄运时求生的欲望。小说讲述了许三观靠着卖血渡过了人生的一个个难关，战胜了命运强加给他的惊涛骇浪，而当他老了，知道自己的血再也没有人要时，精神却崩溃了。','4760224','https://book.douban.com/subject/4760224/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('51','故事新编','鲁迅','中国','人民文学出版社','1973-12-01','129','0.31元','平装','鲁迅作品集','','9.4','34035','《故事新编》是鲁迅的一部短篇小说集，收录了鲁迅在1922年～1935年间创作的短篇小说八篇。1936年1月由上海文化生活出版社初版，列为巴金所编的《文学丛刊》之一作者生前共印行七版次。这八篇小说分别是：《补天》、《奔月》、《理水》、《采薇》、《铸剑》、《出关》、《非攻》、《起死》。外加一篇《序言》。此书主要以神话为题材，多数是在“博考文献”的基础上，“取一点因由，随意点染。”其中，《补天》写于1922年冬天，原题《不周山》，收录于《呐喊》初版，后改名《补天》并抽出；《奔月》、《铸剑》写于1926年和1927年，《铸剑》在《莽原》上发表时题名《眉间尺》；《补天》、《理水》、《非攻》、《采薇》、《起死》写于1934至1935年。','2046909','https://book.douban.com/subject/2046909/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('52','寻路中国','[美]彼得·海斯勒','美','上海译文出版社','2011-1','426','33.00元','平装','译文纪实','9787532752805','9','52060','我叫彼得·海斯勒，是《纽约客》驻北京记者。这本书讲述了我驾车漫游中国大陆的经历。2001年夏天，我考取了中国驾照，在此后的七年中，我驾车漫游于中国的乡村与城市。这七年也正是中国汽车业的高速发展期，单在北京一地，每天申领驾照的新人就有一千多，其中有好几年，乘用车销售额的年增长率超过了百分之五十。仅仅两年多的时间，中国政府在乡村所铺设的公路里程数，就超过了此前半个世纪的总量。《寻路中国》一书有几条不同的线索。它首先叙述了我由东海之滨沿着长城一路向西，横跨中国北方的万里行程；另一条线索集中讲述了一个因中国汽车业的高速发展而发生巨变的乡村，在这里，我特写了一个农民家庭由农而商的变化经历；最后，则是中国东南部一个工业小镇的城市生活场景。书中所描述的这种由农而工而商、乡村变身城市的发展，正是1978年改革以来中国所发生的最重要的变化。《寻路中国》是我的中国纪实三部曲之尾曲。它探讨经济，追踪发展的源头，探究个人对变革的应对。如前两本书那样，它研究中国的核心议题，但并不通过解读著名的政治或文化人物来实现这个目的，也不做宏观的大而无当的分析。它相信通过叙述普通中国人的经历来展现中国变革的实质。我经常在一地连续呆上数月、甚至数年，跟踪变化。我不会仅仅听主人公自己讲述，我会睁大眼睛，看着他们的故事在我面前一点点展开。这三本书横跨了我的中国十年，从1996至2007。我们可以看出，这个处于世纪之交的十年是中国历史上最关键的时期之一。正是在这十年中，中国经济实现了腾飞，中国对外部世界的影响力开始增大。更重要的是，这是邓小平去世后的第一个十年。在这十年中，中国历史的面貌开始变化，大规模的政治事件与强力领袖开始从中退却。相反，中国巨变的推动者变成了普通人——走向城市的农民、边学边干的企业家，他们的能量与决心是过去这十年中的决定因素。从《江城》到《甲骨文》再到《寻路中国》，我所讲述的都是他们的故事。','5414391','https://book.douban.com/subject/5414391/',' Country Driving','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('53','刀锋','[英]毛姆','英','上海译文出版社','2007-3','318','18.00元','平装','译文名著文库','9787532741922','9','80152','威廉·萨默塞特·毛姆（1874-1965），英国著名小说家、戏剧家。《刀锋》是他的主要作品之一。小说写一个参加第一次世界大战的美国青年飞行员拉里·达雷尔。在军队里，拉里结识了一个爱尔兰好友：这人平时是那样一个生龙活虎般的置生死于度外的飞行员，但在一次遭遇战中，因去救拉里而中弹牺牲。拉里因此对人生感到迷惘，弄不懂世界上为什么有恶和不幸，拉里开始了他令人匪夷所思的转变……','2035162','https://book.douban.com/subject/2035162/'," The Razor's Edge",'');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('54','朝花夕拾','鲁迅','中国','人民文学出版社','1973-4','92','0.25元','平装','鲁迅作品集','','9','135615','《小引》《狗·猫·鼠》《阿长与山海经》《二十四孝图》《五猖会》《无常》《从百草园到三味书屋》《父亲的病》《琐记》《藤野先生》《范爱农》《后记》','1449352','https://book.douban.com/subject/1449352/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('55','格林童话全集','[德国]格林兄弟','德国','人民文学出版社','1994-11','625','21.45元','精装','世界文学名著文库','9787020019526','9','88091','《格林童话全集》是德国民间故事集。由德国的雅各・格林和威廉・格林兄弟根据民间口述材料改写而成。他们搜集、整理、出版这些民间童话的目的，正如威廉所说，是"希望它成为一本有教育意义的书，因为我再也想不出什么更富有教益，更天真无邪，更令人心旷神怡的读物，能比它更适合于儿童的心性与能力了。"作品的主要内容是颂扬勤劳和诚实，鄙弃懒惰和自私，鼓励对暴力和邪恶的反抗，激发对被压迫者的同情和爱护。书中的200多个故事，大部分源自民问的口头传说，因而比较正确地反映了当时人民的思想感情。其中的《灰姑娘》、《白雪公主》、《小红帽》、《青蛙王子》等童话故事，脍炙人口，享誉世界。作品问世一百多年来，已被译成70多种文字，在世界各国广为流传，成为各地收集民间故事的范例。','1043008','https://book.douban.com/subject/1043008/',' Kinder-und Hausmärchen','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('56','无人生还','[英]阿加莎·克里斯蒂','英','人民文学出版社','2008-3','254','19.00','平装','阿加莎·克里斯蒂侦探推理系列','9787020065394','9','133598','十个互不相识的人，被富有的欧文先生邀请到了印地安岛上的私人别墅里。晚餐后，一个神秘的声音揭开了人们心中所各自隐藏着的可怕秘密。当天晚上，年轻的马斯顿先生离奇死去，古老的童谣就像诅咒一样笼罩着所有人，似乎有一双神秘的眼镜在时刻窥视着这场死亡游戏，到访者就像消失的印地安小瓷人一样一个又一个的走向死神……','3006581','https://book.douban.com/subject/3006581/',' And Then There Were None','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('57','嫌疑人X的献身','[日本]东野圭吾','日本','南海出版公司','2008-9','251','28.00','精装','新经典文库·东野圭吾作品','9787544241694','8.9','492528','百年一遇的数学天才石神，每天唯一的乐趣，便是去固定的便当店买午餐，只为看一眼在便当店做事的邻居靖子。靖子与女儿相依为命，失手杀了前来纠缠的前夫。石神提出由他料理善后。石神设了一个匪夷所思的局，令警方始终只能在外围敲敲打打，根本无法与案子沾边。石神究竟使用了什么手法？','3211779','https://book.douban.com/subject/3211779/',' 容疑者Xの献身','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('58','树上的男爵','[意]伊塔洛·卡尔维诺','意','译林出版社','2012-4-1','280','30.00元','精装','卡尔维诺经典','9787544722803','9.1','47170','在大陆，王小波、苏童、阿城、止庵是他的忠实粉丝在台湾，朱天文，唐诺是卡尔维诺不余遗力的传播者在香港，梁文道说他一直在准备谈卡尔维诺，可是一直没准备好权威版本，全面修订2006年单行本译本，并增补卡尔维诺各作品自序、后记、注释等重要资料知名设计师全新装帧，精装双封设计，书脊烫金，封面采用原创线条图，赋予每部作品文学个性卡尔维诺“我们的祖先”三部曲之一。“我们的祖先”三部曲包括：《不存在的骑士》《分成两半的子爵》《树上的男爵》，这三个故事代表通向自由的三个阶段，关于人如何实现自我的经验：在《不存在的骑士》中争取生存，在《分成两半的子爵》中追求不受社会摧残的完整人生，在《树上的男爵》中有一条通向完整的道路——这是通过对个人的自我抉择矢志不移的努力而达到的非个人主义的完整。一次倔强的反抗，让科希莫从十二岁起就决定永不下树。从此，他一生都生活在树上，却将生命更紧密地与大地相连。是不是真的只有先与人疏离，才能最终与他们在一起？','6789605','https://book.douban.com/subject/6789605/',' The Baron in the Trees','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('59','黄金时代','王小波','中国','花城出版社','1999-3','375','19.00元','平装','王小波《时代三部曲》','9787536025080','8.9','155727','《黄金时代》是《时代三部曲》之一。这是以文革时期为背景的系列作品构成的长篇。发生“文化大革命”的二十世纪六七十年代，正是我们国家和民族的灾难年代。那时，知识分子群体无能为力而极“左”政治泛滥横行。作为倍受歧视的知识分子，往往丧失了自我意志和个人尊严。在这组系列作品里面，名叫“王二”的男主人公处于恐怖和荒谬的环境，遭到各种不公正待遇，但他却摆脱了传统文化人的悲愤心态，创造出一种反抗和超越的方式：既然不能证明自己无辜，便倾向于证明自己不无辜。于是他以性爱作为对抗外部世界的最后据点，将性爱表现得既放浪形骸又纯','1089243','https://book.douban.com/subject/1089243/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('60','史记（全十册）','司马迁/张守节','中国','中华书局','1982-11','3356','125.00','平装','点校本二十四史·清史稿（繁体竖排）','9787101003048','9.5','22513','史记是我国第一部通史。在史记之前，有以年代为次的“编年史”如春秋，有以地域为限的“国别史”如国语、战国策，有以文告档卷形式保存下来的“政治史”如尚书，可是没有上下几千年，包罗各方面，而又融会贯通，�络分明，像史记那样的通史。唐刘知几的史通分叙六家，统归二体。所谓“二体”，就是“编年体”和“纪传体”，而史记是纪传体的创始。从此以后，历代的所谓“正史，从汉书到明史，尽管名目有改变（例如汉书改“书”为“志”，晋书改世家”为“载记”），门类有短缺（例如汉书无“世家”，后汉书、三国志等都无“表”、“志及世家”），但都有“纪”有“传”，绝无例外地沿袭了史记的体例。据司马迁自序，史记全书本纪十二篇，表十篇，书八篇，世家三十篇，列传七十篇（包括太史公自序），共一百三十篇。今本史记一百三十卷，篇数跟司马迁自序所说的相符。但汉书司马迁传说其中“十篇缺，有录无书。三国魏张晏注：“迁没之后，亡景纪、武纪、礼书、乐书、兵书（按：即律书）、汉兴以来将相年表、日者传、三王世家、龟策列传、传靳列传。兀成之间，褚先生补缺，作武帝纪、三王世家、龟策、日者列传，言辞鄙陋，非迁本意也。可见司马迁编写史记，只能说是基本上完成，其中有若干篇，或者没有写定，或者已经定稿而后来散失了。','1077847','https://book.douban.com/subject/1077847/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('61','中国少年儿童百科全书（全四册）','林崇德主编','中国','浙江教育出版社','1991-4','约2800','168.00元','平装','','9787533830953','9.4','15304','浙江教育出版社出版的《中国少年儿童百科全书》(双色版)作为中国第一部少年儿童百科全书，自1991年儿童节出版以来，一直热销全国，被誉为国内图书市场的“常青树”。6000多个知识条目、200多万字的阐述说明，在少儿类的百科书中堪称“巨无霸”，沉甸甸地诠释着世界的丰富精彩，也让孩子们充分体验到有问必答的满足。6000多幅生动精彩的彩色图片结合国际流行的版面设计大大提升了百科全书的视觉表现力，让阅读者获取知识的过程成为享受。版面上精心设置的各种小栏目更在细节处展现了新一代百科全书对多媒体、网络等先进手段的吸收运用。贯穿全书各个不同知识领域的“参见”栏目如网络上的“链接”一般，让读者在不知不觉中提高举一反三的自主学习能力。趣味盎然的“拓展阅读”则像多媒体软件界面上的按键，有助于培养少年儿童主动联想的思维模式，使他们在探求、思考中获益、成长。与以往种种传统百科全书相比，这套彩图版百科全书以更加新颖、多样、前沿的表现形式，涵盖了天文、地理、政治、经济、军事、科技、文化、艺术、法律、宗教等诸多领域，为中国少年儿童架构了一座国际化的知识体系，在广度、深度和新度上代表了当前百科全书的最新发展成果和水平。','1028409','https://book.douban.com/subject/1028409/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('62','傲慢与偏见','[英]奥斯丁','英','人民文学出版社','1993-7','310','13.00元','平装','名著名译插图本','9787020040179','8.9','220920','《傲慢与偏见》是简·奥斯汀的代表作，是一部描写爱情与婚姻的经典小说。作品以男女主人公达西和伊丽莎白由于傲慢和偏见而产生的爱情纠葛为线索，共写了四起姻缘：伊丽莎白与达西、简与宾利、莉迪亚与威克姆、夏洛蒂与柯林斯。伊丽莎白、简和莉迪亚是贝内特家五个女儿中的三个姐妹，而夏洛蒂则是她们的邻居，也是伊丽莎白的朋友。男主人公达西与宾利是好友，且与威克姆一起长大，而柯林斯则是贝内特家的远房亲戚。贝内特夫妇五个女儿待字闺中，没有子嗣，依照当时法律，他们死后家产须由远房内侄柯林斯继承，因此把五个女儿嫁到有钱人家，成了贝内特太太最大的心愿。宾利，一位未婚富家子弟，租赁了贝内特家附近的内瑟菲尔德庄园，成为众人注目的焦点和谈论的话题。不久，宾利就与美丽贤淑的大小姐简相爱了。宾利的朋友达西对聪明直率的二小姐伊丽莎白颇有好感，却因在一次舞会上出言不逊使伊丽莎白对他心存偏见。品行不端的威克姆告诉伊丽莎白，他是达西庄园已故总管的儿子，与达西一起长大，达西的父亲先前许诺给他的教职，被达西无端剥夺了。而达西则因为伊丽莎白的母亲及其他妹妹的缘故，劝说宾利中止与简的关系，结果四人不欢而散。威克姆对达西的诋毁，以及达西的劝说对简造成的伤害进一步加深了伊丽莎白对达西的偏见。柯林斯为心安理得地继承财产，决定从贝内特家五个漂亮的女儿之中挑选一个“妻子”，于是向伊丽莎白求婚。遭到拒绝后，他马上转向尚未婚配急于找到“归宿”的夏洛蒂小姐，竟然得到应允。伊丽莎白应邀到新婚的柯林斯和夏洛蒂夫妇家中做客，不期遇见前来探望凯瑟琳夫人的达西。达西为伊丽莎白所倾倒，向她求婚，但因其言辞的傲慢，遭到伊丽莎白的愤然拒绝。同时，伊丽莎白指责达西对威克姆冷酷无情，更不应该破坏宾利同简的爱情。事后达西写信为自己申辩，令伊丽莎白的偏见逐渐消除。伊丽莎白随舅父舅妈出游时经过达西的庄园，以为达西不在，进去参观，不料达西突然归来，伊丽莎白感到十分窘迫。然而，达西丝毫没有以往的傲慢，非常热情地接待了他们。此时，伊丽莎白突然接到家信，得知威克姆带着妹妹莉迪亚私奔了！匆忙回家后，全家一筹莫展，不料达西暗访到两人的行踪，出资促成他们的婚事并安排了他们的生活，为贝内特一家保全了尊严。此事使伊丽莎白与达西尽释前嫌，宾利也和简重修旧好，最后有情人终成眷属。','1083428','https://book.douban.com/subject/1083428/',' Pride and Prejudice','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('63','悲惨世界（上中下）','[法]雨果','法','人民文学出版社','1992-6','1760','66.00元','平装','外国古典文学名著选粹','9787020017164','9','59831','这是法国十九世纪浪漫派领袖雨果继《巴黎圣母院》之后创作的又一部气势恢宏的鸿篇巨著。全书以卓越的艺术魅力，展示了一幅自1793年法国大革命至1832年巴黎人民起义期间，法国近代社会生活和政治生活的辉煌画卷，最大限度地体现了雨果在叙事方面的过人才华，是世界文学史上现实主义与浪漫主义结合的典范。小说集中反映了雨果的人道主义思想，饱含了雨果对于人类苦难命运的关心和对末来坚定不移的信念，具有震撼人心的艺术感染力。','1205054','https://book.douban.com/subject/1205054/',' Les Misérables','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('64','鼠疫','[法]阿尔贝·加缪','法','上海译文出版社','2013-8','280','34.00元','','加缪作品','9787532761753','9.1','60221','阿尔贝•加缪（1913—1960）是法国声名卓著的小说家、散文家和剧作家，“存在主义”文学的大师。1957年因“热情而冷静地阐明了当代向人类良知提出的种种问题”而获诺贝尔文学奖，是有史以来最年轻的诺奖获奖作家之一。加缪在他的小说、戏剧、随笔和论著中深刻地揭示出人在异己的世界中的孤独、个人与自身的日益异化，以及罪恶和死亡的不可避免，但他在揭示出世界的荒诞的同时却并不绝望和颓丧，他主张要在荒诞中奋起反抗，在绝望中坚持真理和正义，他为世人指出了一条基督教和马克思主义以外的自由人道主义道路。他直面惨淡人生的勇气，他“知其不可而为之”的大无畏精神使他在第二次世界大战之后不仅在法国，而且在欧洲并最终在全世界成为他那一代人的代言人和下一代人的精神导师。','24257229','https://book.douban.com/subject/24257229/',' La Peste','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('65','厌女','上野千鹤子','中国','上海三联书店','2015-1','261','28.00元','平装','','9787542645715','9.1','51756','《厌女》不是一部纯理论性著作，而是运用女性主义理论针对日本当代的各种社会现象的实践性分析。《厌女》处理了广泛的日本题材，其中既有文学性的，比如谈到小说家吉行淳之介、永井荷风、林真理子等，更多的则是社会性的。上野千鹤子谈到的许多现象，尤其是家庭的、婚姻的，在中国也有很类似的情况。知性的读者会从这些论述中获得强烈共鸣并得到理论上的指引。','25836270','https://book.douban.com/subject/25836270/',' 女ぎらい','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('66','历史深处的忧虑','林达/李晓林','中国','生活·读书·新知三联书店','1997-5','320','19.00元','平装','林达作品系列','9787108010186','9','43128','美国的面积和中国差不多。和大多数留学生及新移民一样，当我们一脚踏上这块广袤的陌生土地时，最初落脚点的选择是十分偶然的。我们落在了一个普通的地方，居住的环境平常而宁静。周围的美国人老老少少都在辛勤劳作，过着普通得不能再普通的生活。要想谋出一番好的光景，对他们也不是一件轻而易举的事情。还来不及作任何思索，新移民所面临的生活压力就立即把我们投入了同样的辛勤劳作之中。这样，我们和周围的普通美国人之间，自然就有了共同谋生而产生的共同语言，也有了共同的经历，共同的喜怒哀乐。不少美国人还成了我们的好朋友。我们无形中发现，美国已经渐渐地从一堆抽象的概念中走出来，变成了脚下实实在在的土地，变成了一片片森林牧场，变成了一个一个美国人。我们也有过被称之为“边缘人”的苦闷。我们显然不可能在进入这一块国土的同时，就在一瞬间也进入它的文化，而原来的文化背景又由于天长地远而渐显陌生。在实际生活中，两种截然不同的文化，会经常骤然直率地在我们面前相遇碰撞，一开始真有无所适从亦无可躲避的窘迫。然而，时间长了，我们开始对“边缘人”的处境逐渐坦然，甚至悟出了些什么。冷静下来，我们发现自己第一次能够这样坐在一个安静的地方，同时和两个文化保持一个不远也不近的距离，它们不仅开始有可能化为一片风景，而且第一次向我们呈现出前所未有的意义。“边缘人”的苦恼在新移民中十分普遍，它只是两个强势文化聚焦在一个小人物身上而产生的悲剧性效应。我们庆幸自己能够脱身出来。但是，我们的处境使我们不可能不想到，作为一个夹缝中的移民，尚且感受到矛盾与压力，那么，当这两种文化两个大国真的正面遭遇，彼此实际上是多么陌生和困惑，说是危机四伏似乎也并不过分。我们所处的位置，使我们有可能更多地看到它们的差异。我们发现，它们不仅有各自圆满的价值体系，甚至有各自不同的语汇、语境和逻辑。有时，从一个大前提出发，顺着各自的逻辑走去，竟会走出风马牛不相及的结果来。在这样的基础上，对话只可能显得痛苦而吃力。因此，好像在作出举手相迎或严辞相拒的决定之前，还有一段相当艰苦而漫长的互相了解的路必须去走。否则，轻易称“是”显得没有根据，动辄言“不”也容易文不对题。更何况，历史已经规定了它们不可能永远回避。它们都已尝试过这样做，但是，眼看着毫无结果，于是，它们又试图相互走近。有时，如果不暂时和自己的习惯思维方式生生拉开一段距离，就无法在相互了解的路上走出一步。我们则是以一个普通新移民的身份去了解这个陌生的国家。既来之则安之，这也是我们的必由之路，我们也别无选择。美国不是一个善于遮羞的国家，它投出一片阳光，就落下一片阴影。它全部的阴影都毫无遮掩地暴露在所有的人面前，哪怕你是一个陌生人。所以，即使是像我们这样的平常外来者，也会一眼就看出～大堆毛病来。但是我们马上就明白自己必须从沾沾自喜中醒来，因为后来很快发现，这并不见得就～定是自己比美国人更高明且目光更敏锐。他们似乎也很清楚有这么一片阴影，他们只是迫不得已在承认一个简单的道理，如果你追求阳光，你就躲不开身后的阴影。我们开始奇怪，美国人在容忍这片阴影的时候(这种容忍常常使他们痛苦不堪)，什么是他们心中不熄的光明呢?他们在寻求着什么，以至于不惜支付出沉重的代价呢?对于我们，这真是一个问题。在了解它的过程中，我们对这个历史短短的陌生国家产生了与日俱增的兴趣。根据自己作为一个平民的经验，我们发现，了解的第一步就是简单的交往。不论一个大国看上去有着多么坚硬的外壳，它的人民都是一个个平平常常，而再大的大国也就是这些平常人组成的。当人民成为朋友，交流变成友谊，相互了解自然就开始了。任何一方，能以更宽广的视角去看这个世界，总是一件好事情。去年，我们逐渐给自己在国内的好朋友写了这些信。希望能和朋友们分享我们的所读所见所闻所思。在此，衷心感谢三联书店的编辑和所有帮助这本书出版的朋友。','1027191','https://book.douban.com/subject/1027191/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('67','上帝掷骰子吗','曹天元','中国','辽宁教育出版社','2006-1','369','32.00元','平装','','9787538276374','9.2','31794','《上帝掷骰子吗》这本书是关于量子论的故事。量子论是一个极为奇妙的理论：从物理角度来说，它在科学家中间引起了最为激烈的争议和关注；从现实角度来说，它给我们的社会带来了无与伦比的变化和进步；从科学史角度来说，也几乎没有哪段历史比量子论的创立得到了更为彻底的研究。然而不可思议的是，它的基本观点和假说至今没有渗透到大众的意识中去，这无疑又给它增添了一道神秘的光环。这本书带读者做一次量子之旅，从神话时代出发，沿着量子发展的道路，亲身去经历科学史上的乌云和暴雨，追逐流星的辉光，穿越重重迷雾和险滩，和最伟大的物理学家们并肩作战。除了回顾基本的历史背景，还将向着未来探险，去逐一摸索量子论面前的不同道路，闯入人迹罕至的未知境地，和先行者们一起开疆扩土。让人惊叹的，不仅仅是沿途那令人眼花缭乱的绚丽风景，更来自于人内心深处的思索和启示——那是科学深植在每个人心中不可抗拒的魅力。这本书适合任何有中学基本物理概念的读者。','1467022','https://book.douban.com/subject/1467022/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('68','诗经','孔丘','中国','北京出版社','2006-7','363','19.90元','平装','家庭书架·传世经典读库','9787200065039','9.5','25604','这本《诗经》收录全部305篇，每篇诗歌都配有相应的解释及译文，使读者更容易理解。','1883245','https://book.douban.com/subject/1883245/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('69','永恒的终结','[美]艾萨克·阿西莫夫','美','江苏凤凰文艺出版社','2014-9','264','32.00元','平装','读客外国小说文库：艾萨克·阿西莫夫作品','9787539957173','9.1','39298','24世纪，人类发明了时间力场。27世纪，人类在掌握时间旅行技术后，成立了一个叫做永恒时空（Eternity）的组织，在每个时代的背后，默默地守护着人类社会的发展。永恒时空以一个世纪为单位，并视每个世纪的发展需要而加以微调，以避免社会全体受到更大伤害。通过纠正过去的错误，将所有灾难扼杀在萌芽中，人类终于获得安宁的未来。然而，这种“绝对安全”的未来却在某一天迎来了终结。不知不觉中形成的因果链，仿佛从四面八方涌来的黑暗，即将吞噬全人类。安德鲁•哈伦，生于95世纪，他是永恒时空的时空技师、人类未来社会的精英。他的天职是靠操纵时空壶，进行时间旅行来守护500亿人类，而在一次时空任务中，他邂逅了令他一见倾心的姑娘，而突然来到的爱情却让他开始质疑整个世界。同时，人类最后的希望，也落在了时间旅行者安德鲁•哈伦最后的时空任务上……◆《永恒的终结》是最伟大的科幻作家阿西莫夫被公认的最高杰作。◆《永恒的终结》厘清了关于时间旅行的一切构想！◆1955年原作出版，科幻迷们翘首以盼60年来，唯一中文版全译本首次登陆中国。','25829693','https://book.douban.com/subject/25829693/',' The End of Eternity','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('70','西游记（全二册）','吴承恩/校注黄肃秋','中国','人民文学出版社','2004-8','1198','47.20元','平装','中国古典文学读本丛书','9787020008735','9','77799','《西游记》主要描写的是孙悟空保唐僧西天取经，历经九九八十一难的故事。唐僧取经是历史上一件真实的事。大约距今一千三百多年前，即唐太宗贞观元年（627），年仅25岁的青年和尚玄奘离开京城长安，只身到天竺（印度）游学。他从长安出发后，途经中亚、阿富汗、巴基斯坦，历尽艰难险阻，最后到达了印度。他在那里学习了两年多，并在一次大型佛教经学辩论会任主讲，受到了赞誉。贞观十九年（645）玄奘回到了长安，带回佛经657部。他这次西天取经，前后十九年，行程几万里，是一次传奇式的万里长征，轰动一时。后来玄奘口述西行见闻，由弟子辩机辑录成《大唐西域记》十二卷。但这部书主要讲述了路上所见各国的历史、地理及交通，没有什么故事。及到他的弟子慧立、彦琮撰写的《大唐大慈恩寺三藏法师传》，则为玄奘的经历增添了许多神话色彩，从此，唐僧取经的故事便开始在民间广为流传。南宋有《大唐三藏取经诗话》，金代院本有《唐三藏》、《蟠桃会》等，元杂剧有吴昌龄的《唐三藏西天取经》、无名氏的《二郎神锁齐大圣》等，这些都为《西游记》的创作奠定了基础。吴承恩也正是在民间传说和话本、戏曲的基础上，经过艰苦的再创造，完成了这部令中华民族为之骄傲的伟大大文学巨著。吴承恩，字汝忠，号射阳山人，淮安府山阳（今江苏省淮安市）人。约生于明弘治十三年至正德初年之间（1500—1510），约卒于万历十年（1582）。吴承恩的曾祖父、祖父都是读书人，任过县学的训导、教谕。但到了他父亲吴锐这一辈，由于家贫困，出赘徐家，“遂袭徐氏业，坐肆中”，当起了小商人。尽管如此，吴家却不失读书的传统。据说其父吴锐虽为商人，不仅为人正派，而且好读书，好谈时政，这自然对吴承恩产生较大影响。吴承恩从小就很聪明，很早入了学，少年得志，名满乡里。天启《淮安府志》卷十六说吴承恩“性敏而多慧，博极群书，为诗文，下笔立成。”但成年后的吴承恩却很不顺利，在科举进身的道路上屡遭挫折，到四十多岁才补了一个岁贡生，五十多岁任过浙江长兴县丞，后又担任过荆王府纪善，这是同县丞级别差不多的闲职。吴承恩创作《西游记》大约是中年以后，或认为是晚年所作，具体时间无法确定。除《西游记》外，他还创作有长诗《二郎搜山图歌》和《禹鼎志》。现存《射阳先生存稿》四卷，包括诗一卷、散文三卷，是吴承恩逝世后由丘度编订而成。《西游记》全书一百回，从大的结构上看，可分成三个部分。第一回至第八回是第一部分，主要写了孙悟空出世、拜师、大闹天宫，这是全书最精彩的章节，热闹非凡，孙悟空上天入地好一顿折腾，将他的反抗性格表现得淋漓尽致。第八回至第十二回是第二部分，主要写了唐僧的出身及取经的缘由。第十三回至最后一回是第三部分，主要写唐僧西天取经，路上先后收了孙悟空、猪八戒、沙和尚三个徒弟，并历经九九八十一难，终于取到了真经，修成了正果。《西游记》向人们展示了一个绚丽多彩的神魔世界，人们无不在作者丰富而大胆的艺术想象面前惊叹不已。然而，任何一部文学作品都是一定社会生活的反映，作为神魔小说杰出代表的《西游记》亦不例外。正如鲁迅先生在《中国小说史略》中指出，《西游记》“讽刺揶揄则取当时世态，加以铺张描写”。又说：“作者禀性，‘复善谐剧’，故虽述变幻恍忽之事，亦每杂解颐之言，使神魔皆有人情，精魅亦通世故。”的确如此。通过《西游记》中虚幻的神魔世界，我们处处可以看到现实社会的投影。如在孙悟空的形象创造上，就寄托了作者的理想。孙悟空那种不屈不挠的斗争精神，奋起金箍棒，横扫一切妖魔鬼怪的大无畏气概，反映了人民的愿望和要求。他代表了一种正义的力量，表现出人民战胜一切困难的必胜信念。又如取经路上遇到的那些妖魔，或是自然灾难的幻化，或是邪恶势力的象征。他们的贪婪、凶残、阴险和狡诈，也正是封建社会里的黑暗势力的特点。不仅如此，玉皇大帝统治的天宫、如来佛祖管辖的西方极乐世界，也都浓浓地涂上了人间社会的色彩。而作者对封建社会最高统治者的态度也颇可玩味，在《西游记》中，简直找不出一个称职的皇帝；至于昏聩无能的玉皇大帝、宠信妖怪的车迟国国王、要将小儿心肝当药引子的比丘国国王，则不是昏君就是暴君。对这些形象的刻画，即使是信手拈来，也无不具有很强的现实意义。《西游记》不仅有较深刻的思想内容，艺术上也取得了很高的成就。它以丰富奇特的艺术想象、生动曲折的故事情节，栩栩如生的人物形象，幽默诙谐的语言，构筑了一座独具特色的《西游记》艺术宫殿。但我认为，《西游记》在艺术上的最大成就，是成功地创造了孙悟空、猪八戒这两个不朽的艺术形象。孙悟空是《西游记》中第一主人公，是个非常了不起的英雄。他有无穷的本领，天不怕地不怕，具有不屈的反抗精神。他有着大英雄的不凡气度，也有爱听恭维话的缺点。他机智勇敢又诙谐好闹。而他最大的特点就是敢斗。与至高至尊的玉皇大帝敢斗，楞是叫响了“齐天大圣”的美名；与妖魔鬼怪敢斗，火眼金睛决不放过一个妖魔，如意金箍棒下决不对妖魔留情；与一切困难敢斗，决不退却低头。这就是孙悟空，一个光彩夺目的神话英雄。说到猪八戒，他的本事比孙悟空可差远了，更谈不上什么光辉高大，但这个形象同样刻画得非常好。猪八戒是一个喜剧形象，他憨厚老实，有力气，也敢与妖魔作斗争，是孙悟空第一得力助手。但他又满身毛病，如好吃，好占小便宜，好女色，怕困难，常常要打退堂鼓，心里老想着高老庄的媳妇；他有时爱撒个谎，可笨嘴拙腮的又说不圆；他还时不时地挑拨唐僧念紧箍咒，让孙悟空吃点苦头；他甚至还藏了点私房钱，塞在耳朵里。他的毛病实在多，这正是小私有者的恶习。作者对猪八戒缺点的批评是很严厉的，但又是善意的。他并不是一个被否定的人物，因此人们并不厌恶猪八戒，相反却感到十分真实可爱。唐僧的形象写得也不错，但比起孙悟空、猪八戒来，则要逊色得多。沙僧更是缺少鲜明的性格特点，这不能不说是《西游记》的缺憾。尽管如此，《西游记》在艺术上取得的成就仍是十分惊人的，孙悟空、猪八戒这两个形象，以其鲜明的个性特征，在中国文学史上立起了一座不朽的艺术丰碑。','1029553','https://book.douban.com/subject/1029553/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('71','神雕侠侣','金庸','中国','生活·读书·新知三联书店','1994-5','1564','76.80元','平装','金庸作品集（三联版）','9787108006660','8.9','95152','《神雕侠侣》是金庸作品集之一。其主人公杨过自然而然地走上了非正统的人生道路，入了“道流”。其特点是“至情至性，实现自我”，即把个人的利益、情感、个性及人格尊严置于人生首位，作为首要目标，亦作为待人处事，评价是非的首要原则。书中将杨过对郭靖的杀父之仇与疼惜之恩难以取舍的复杂心理刻画得维妙维肖；他与“姑姑”小龙女的情感纠葛和他对江湖世事的渴望又令他挣扎不已……','1255624','https://book.douban.com/subject/1255624/',' 神鵰俠侶','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('72','台北人','白先勇','中国','广西师范大学出版社','2010-10','392','38.00元','精装','理想国·白先勇作品','9787563397648','9','61196','作为20世纪中文小说100强的《台北人》，是一部深具复杂性的短篇小说集，由十四个一流的短篇小说构成，串联成一体，则效果遽然增加，不但小说之幅面变广，使我们看到社会之“众生相”，更重要的，由于主题命意之一再重复，与互相陪衬辅佐，使我们能更进一步深入了解作品之含义，并使我们得以一窥隐藏在作品内的作者之人生观与宇宙观。《台北人》之人物，可以说囊括了台北都市社会之各阶层：从年迈挺拔的儒将朴公（《梁父吟》）到退休了的女仆顺恩嫂（《思旧赋》），从上流社会的窦夫人（《游园惊梦》）到下流社会的“总司令”（《孤恋花》）。有知识分子，如《冬夜》之余嵚磊教授；有商人，如《花桥荣记》之老板娘；有帮佣工人，如《那血一般红的杜鹃花》之王雄；有军队里的人，如《岁除》之赖鸣升；有社交界名女，如尹雪艳；有低级舞女，如金大班。这些“大”人物、“中”人物与“小”人物，来自中国大陆不同的省籍或都市（上海、南京、四川、湖南、桂林、北平等），他们贫富悬殊，行业各异，但没有一个不背负着一段沉重的、斩不断的往事。而这份“过去”，这份“记忆”，或多或少与中华民国成立到大陆沦陷那段“忧患重重的时代”，有直接的关系。','5337248','https://book.douban.com/subject/5337248/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('73','文学回忆录','木心口述/陈丹青笔录','中国','广西师范大学出版社','2013-1-10','1102','98.00元','精装','理想国·木心作品','9787549530816','9.1','41456','文学是可爱的。生活是好玩的。艺术是要有所牺牲的。八十年代末，木心客居纽约时期，亦自他恢复写作、持续出书以来，纽约地面的大陆和台湾同行在异国谋饭之中，居然促成木心开讲“世界文学史”，忽忽长达五年的一场“文学的远征”——从1989年1月15日开课，到1994年1月9日最后一课，每位听课人轮流提供自家客厅，在座者有画家、舞蹈家、史家、雕刻家等等。听课学生陈丹青说，“我们当年这样地胡闹一场，回想起来，近于荒谬的境界：没有注册，没有教室，没有课本，没有考试与证书，更没有赞助与课题费，不过是在纽约市皇后区、曼哈顿区、布鲁克林区的不同寓所中，团团坐拢来，听木心神聊。”菜单开出来，大家选。从古希腊神话、新旧约，到诗经、楚辞，从中世纪欧洲文学，到二十世纪文学世界，东方西方通讲，知识灵感并作。其中听的听，讲的讲，“金句”纷披，兀自燃烧。“讲完后，一部文学史，重要的是我的观点。”木心说。古代，中世纪，近代，每个时代都能找到精神血统，艺术亲人。他爱先秦典籍，只为诸子的文学才华；他以为今日所有伪君子身上，仍然活着孔丘；他想对他爱敬的尼采说：从哲学跑出来吧；他激赏拜伦、雪莱、海涅，却说他们其实不太会作诗；他说托尔斯泰可惜“头脑不行”，但讲到托翁坟头不设十字架，不设墓碑，忽而语音低弱了，颤声说：“伟大！”而谈及萨特的葬礼，木心脸色一正，引尼采的话：唯有戏子才能唤起群众巨大的兴奋。木心开讲时六十二岁。多少民国书籍与读者，湮灭了。他的一生，密集伴随愈演愈烈的文化断层。他不肯断，而居然不曾断，这就是纽约世界文学史讲座潜藏的背景：在累累断层之间、之外、之后，木心始终将自己尽可能置于世界性的文学景观，倘若不是出走，这顽强而持久的挣扎，几乎濒于徒劳。如今，听课学生陈丹青整理那五年那五册听课笔记，共八十五讲，逾四十万字，结集这本大书时，已不再将之仅仅看做“世界文学史讲座”。诚如木心所最早时设想的那样，这是他自己的“文学回忆录”，是一部“荒诞小说”，“在自己的身上，克服这个时代”。这也是木心留给世界的礼物，文学的福音书。本书首次披露的木心先生及其亲属的珍贵照片，由陈丹青先生和木心的外甥王韦先生提供。附印民国版本的世界文学书影，是一部民国出版史的私人旁证。','20440644','https://book.douban.com/subject/20440644/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('74','秋园','杨本芬','中国','北京联合出版公司','2020-6','280','38.00元','平装','看见女性三重奏','9787559640666','9','55212','听八旬奶奶讲述她和妈妈的故事。1914年，世上有了“秋园”这个人。1918年，汉语有了“她”这个字。秋园，她来过，挣扎过，绝望过，幸福过。今天，她80岁的女儿，把普普通通的她，讲给世界听。“我写了一位普通中国女性一生的故事，写了我们一家人如何像水中的浮木般挣扎求生，写了中南腹地那些乡间人物的生生死死。我知道自己写出的故事如同一滴水，最终将汇入人类历史的长河。”','34998019','https://book.douban.com/subject/34998019/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('75','置身事内','兰小欢','中国','上海人民出版社','2021-8','340','65.00元','平装','世纪文景“大国之治”套装','9787208171336','9.1','44094','“在成功的经济体中，经济政策一定是务实的，不是意识形态化的。是具体的，不是抽象的。”直面真问题、深究真逻辑的复旦大学经济学“毕业课”☆连接抽象经济学理论与中国发展实际在市场机制不完善的情况下，如何实现可行的动员与资源调配，推动经济增长？在确保增长的过程中，如何获得更多资源和时间去建设和完善市场经济？☆结合学界前沿研究与一线调研经历扼要论述+延伸阅读指引，满足弹性认知需求生动解说光伏、面板等行业案例，清晰复盘宽窄巷子、华夏幸福等建设经验☆微观机制+宏观现象双重维度串联中国经济生活中中知识与现象的碎片理解地方政府经济治理的决策与行动逻辑解读城市化、房价、贫富差距、债务、国内外贸易生态等现象背后各种要素的联动☆从热闹的政经新闻中看出门道，从枯燥的政府文件中觉察到机会。“了解政府认为应该怎么办，比了解‘我’认为应该怎么办，重要得多。”本书是是兰小欢多年教学、调研与研究内容的凝练，将经济学原理与中国经济发展的实践有机融合，以地方政府投融资为主线，深入浅出地论述了中国经济的发展，笔触简练客观，并广泛采纳了各领域学者的最新研究成果。全书分上下两篇。上篇解释微观机制，包括地方政府的基本事务、收支、土地融资和开发、投资和债务等；下篇解释这些微观行为与宏观现象的联系，包括城市化和工业化、房价、地区差异、债务风险、国内经济结构失衡、国际贸易冲突等。最后一章提炼和总结全书内容。通过对中国政治经济体系的论述，作者简明地刻画了地方政府进行经济治理的基本方式，并指出，中国政府通过深度介入工业化和城市化的进程，在发展经济的同时逐步推动了市场机制的建立和完善，以一种有别于所谓发达国家经验的方式实现了经济奇迹。基于对改革历程与社会矛盾的回顾与分析，作者也在书中对当前推进的市场化改革与政府转型进行了解读，帮助读者增进对中国发展现实的把握。','35546622','https://book.douban.com/subject/35546622/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('76','哭泣的骆驼','三毛','中国','哈尔滨出版社','2003-6','222','15.80元','平装','三毛全集（华文天下2003版）','9787806399071','9','54286','在《哭泣的骆驼》中，三毛依然恋恋着墨沙漠生活周遭的人与事，《收魂记》、《搭车客》、《逍遥七岛游》、《一个陌生人的死》、《大胡子与我》等篇，情趣盎然；《沙巴军曹》与《哑奴》所刻画的主角，给人留下了难以磨灭的印象；而压轴的《哭泣的骆驼》，以游击战事为背景，细细铺写一对沙漠情鸳的生死盟，竟犹如史诗般的磅礴。','1029111','https://book.douban.com/subject/1029111/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('77','一个叫欧维的男人决定去死','[瑞典]弗雷德里克·巴克曼','瑞典','四川文艺出版社','2015-12','312','35.00元','平装','弗雷德里克·巴克曼作品','9787541142185','9.1','56279','来认识一下欧维，这个59岁的老头，脾气古怪，带着坚不可摧的原则、每天恪守的常规以及随时发飙的脾性在社区晃来晃去，被背地里称为“地狱来的恶邻”。他每天一大早就四处巡视，搬动没停进格线的脚踏车，检查垃圾是否按规定分类，抱怨谁家的草坪还不修剪，诅咒那只掉了毛的流浪猫。没完没了。他想自杀。直到一个十一月的早晨，当一对话痨夫妇和他们的两个话痨女儿搬到隔壁，不小心撞坏了他的邮筒……“读这个故事，”《人物周刊》说，“你会笑，你会哭，会因此想搬到北欧去，因为那里的一切都更可爱一些。”这个发生在瑞典的故事，如生命庆典般绚丽斑斓，它让我们思考，自己是谁，以及我们要如何过这一生。有的人称它是《一个的朝圣》瑞典版，读者评价它“令人愉悦又心碎”“搞笑之余让你感动且哭得稀里哗啦”“读到一本好小说的感觉真好”！它已被拍成电影（传送门：https://movie.douban.com/subject/26628357/），这里有一个愤怒的恪守常规的普通邻家老头，一对话痨夫妇和话痨女儿、这里有一只掉了毛的乱蓬蓬的倒霉猫，这是一场人与人之间的迷人相遇。','26672693','https://book.douban.com/subject/26672693/',' En man som heter Ove','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('78','灿烂千阳','[美]卡勒德·胡赛尼','美','上海人民出版社','2007-9','428','28.00元','平装','卡勒德·胡赛尼作品','9787208072107','8.9','113133','私生女玛丽雅姆在父亲的宅院门口苦苦守候，回到家却看到因绝望而上吊自杀的母亲。那天是她十五岁的生日，而童年嘎然而止。玛丽雅姆随后由父亲安排远嫁喀布尔四十多岁的鞋匠拉希德，几经流产，终因无法生子而长期生活在家暴阴影之下。十八年后，少女莱拉的父母死于战火，青梅竹马的恋人也在战乱中失踪，举目无亲的莱拉别无选择，被迫嫁给拉希德。两名阿富汗女性各自带着属于不同时代的悲惨回忆，共同经受着战乱、贫困与家庭暴力的重压，心底潜藏着的悲苦与忍耐相互交织，让她们曾经水火不容，又让她们缔结情谊，如母女般相濡以沫。然而，多年的骗局终有被揭穿的一天……她们将做出如何的选择？她们的命运又将何去何从？关于不可宽恕的时代，不可能的友谊以及不可毁灭的爱。《灿烂千阳》再次以阿富汗战乱为背景，时空跨越三十年，用细腻感人的笔触描绘了阿富汗旧家族制度下苦苦挣扎的妇女，她们所怀抱的希望、爱情、梦想与所有的失落。','2143732','https://book.douban.com/subject/2143732/',' A Thousand Splendid Suns','李继宏');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('79','孽子','白先勇','中国','广西师范大学出版社','2010.10','552','46.00元','精装','理想国·白先勇作品','9787563397655','9.1','37215','白先勇唯一的一部长篇小说《孽子》，以同性恋为题材，全书共分为四章，依次是“放逐”、“在我们的王国里”、“安乐乡”、“那些青春鸟的行旅”，描写了上个世纪六七十年代生活在台北的一群为家庭所弃、为社会所遗的“孽子”。白先勇题记：“写给那一群，在最深最深的黑夜里，独自彷徨街头，无所依归的孩子们。”其中父子冲突的主题，亦令人印象深刻。','5337254','https://book.douban.com/subject/5337254/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('80','唐诗三百首','蘅塘退士/陈婉俊','中国','中华书局','1984-07-01','229','0.65','平装','','9787101001105','9.4','23387','《唐诗三百首》的旧注本亦有许多种，其中以陈婉俊的补注本简明精当，流行最广。她的注解除简介作者生平外，比较注重语词名物的诠释，博引旁征，字梳句栉，对今天的读者仍有一定的参考价值。','1013267','https://book.douban.com/subject/1013267/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('81','人间词话','王国维','中国','上海古籍出版社','1998-12','77','9.80元','平装','蓬莱阁丛书','9787532524808','9','51289','《人间词话》一书乃是王氏接受了西洋美学思想之洗礼后，以崭新的眼光对中国旧文学所作的评论，具有划时代的意义，向来极受学术界重视。本书并约请当今著名专家黄霖为之导读，不仅梳理其理论框架，剔抉其精义要眇，更着重揭橥其学术源流、历史文化背景，及撰作者当时特定的情境与心态，从而在帮助读者确切理解原著的同时，凸现词学大师王国维的学术个性。','1203426','https://book.douban.com/subject/1203426/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('82','最好的告别','[美]阿图·葛文德（AtulGawande）','美','浙江人民出版社','2015-7-31','248','54.90元','平装','湛庐文化·心视界','9787213067792','9','43445','当独立、自助的生活不能再维持时，我们该怎么办？在生命临近终点的时刻，我们该和医生谈些什么？应该如何优雅地跨越生命的终点？对于这些问题，大多数人缺少清晰的观念，而只是把命运交由医学、技术和陌生人来掌控。影响世界的医生阿图•葛文德结合其多年的外科医生经验与流畅的文笔，讲述了一个个伤感而发人深省的故事，对在21世纪变老意味着什么进行了清醒、深入的探索。本书富有洞见、感人至深，并为我们提供了实用的路线图，告诉我们为了使生命最后的岁月有意义，我们可以做什么、应该做什么。作者选择了常人往往不愿面对的话题——衰老与死亡，梳理了美国社会养老的方方面面和发展历程，以及医学界对末期病人的不当处置。书中不只讲述了死亡和医药的局限，也揭示了如何自主、快乐、拥有尊严地活到生命的终点。书中对“善终服务”“辅助生活”“生前预嘱”等一系列作者推崇的理念，都穿插在故事中作出了详尽的说明，相信会给老龄化日益加剧的中国社会以启迪。众多专家、媒体推荐。创新工场CEO李开复：作为一名医生，阿图•葛文德关注的是医疗的局限以及人的尊严。作为凡人，我们都将面对人生的终点，《最好的告别》给我们重要的启示。《新知》杂志主编苗炜：希望大家有机会能看看阿图•葛文德医生的著作，他能帮助我们更好地理解医学，知道医学的局限和可能。畅销书作家马尔科姆•格拉德威尔：这是阿图•葛文德最有力，也最感人的一本书。《自然》杂志：难得读到这样一本发人深省的书。[编辑推荐]亚马逊年度好书、《纽约时报》畅销书。人终有一死，如何向死而生。“全球十大思想家”、美国著名外科医生划时代之作。奥巴马、李开复、余华、刘瑜、苗炜力荐。湛庐文化出品。','26576861','https://book.douban.com/subject/26576861/',' Being Mortal','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('83','美的历程','李泽厚','中国','生活·读书·新知三联书店','2009-7','217','43.00元','精装','李泽厚集','9787108030375','9.2','27608','本书是中国美学的经典之作。凝聚了作者李泽厚先生多年研究，他把中国人古往今来对美的感觉玲珑剔透地展现在大家眼前，如斯感性，如斯亲切。今配以精美的插图，本书就更具体地显现出中国这段波澜壮阔的美的历程。《美的历程》是部大书（应该说是几部大书），是一部中国美学和美术史，一部中国文学史，一部中国哲学史，一部中国文化史。　　　　——冯友兰这样的著作能有多少呢？凤毛麟角吧，以十几万字的篇幅来完成这样一个”美的历程”，高屋建瓴，势如破竹，且能做到“天网恢恢，疏而不漏”，该细密处细密，该留连处留连，丝丝入扣，顺理成章，在看似漫不经心的巡礼中触摸到文明古国的心灵历史，诚非大手笔而不能为。《美的历程》一书真是写得英姿勃发。才气逼人。单是标题，便气度不凡：龙飞凤舞。青铜饕餮。魏晋风度、盛唐之音，更不用说每过几页就有一段华彩乐章了。实际上，《美的历程》是可以当作艺术品来看待的。它充分地表现着李泽厚的艺术魁力。　　　　——易中天','3410718','https://book.douban.com/subject/3410718/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('84','窗边的小豆豆','[日]黑柳彻子著/岩崎千弘图','日','南海出版公司','2003-1','288','20.00元','平装','新经典文库·巴学园','9787544222976','8.8','196063','《窗边的小豆豆》讲述了作者上小学时的一段真实的故事。作者因淘气被原学校退学后，来到巴学园。在小林校长的爱护和引导下，让一般人眼里“怪怪”的小豆豆逐渐成了一个大家都能接受的孩子，并奠定了她一生的基础。这本书不仅带给世界几千万读者无数的笑声和感动，而且为现代教育的发展注入了新的活力。','1007914','https://book.douban.com/subject/1007914/',' 窓ぎわのトットちゃん','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('85','浪潮之巅','吴军','中国','电子工业出版社','2011-8','584','55.00元','平装','','9787121139512','9','31903','近一百多年来，总有一些公司很幸运地、有意识或无意识地站在技术革命的浪尖之上。在这十几年间，它们代表着科技的浪潮，直到下一波浪潮的来临。从一百年前算起，AT&T公司、IBM公司、苹果公司、英特尔公司、微软公司、思科公司、雅虎公司和Google公司都先后被幸运地推到了浪尖。虽然，它们来自不同的领域，中间有些已经衰落或正在衰落，但是它们都极度辉煌过。本书系统地介绍了这些公司成功的本质原因及科技工业一百多年的发展。在极度商业化的今天，科技的进步和商机是分不开的。因此，本书也系统地介绍了影响到科技浪潮的风险投资公司，诸如KPCB和红杉资本，以及百年来为科技捧场的投资银行，例如高盛公司，等等。在这些公司兴衰的背后，有着它必然的规律。本书不仅讲述科技工业的历史，更重在揭示它的规律性。','6709783','https://book.douban.com/subject/6709783/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('86','呼兰河传','萧红','中国','百花文艺出版社','2005-01','271','19.00','平装','现代文学名著原版珍藏','9787530640470','8.9','76839','','1060852','https://book.douban.com/subject/1060852/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('87','看见','柴静','中国','广西师范大学出版社','2013-1-1','405','39.80元','平装','','9787549529322','8.8','322554','《看见》是知名记者和主持人柴静讲述央视十年历程的自传性作品，既是柴静个人的成长告白书，某种程度上亦可视作中国社会十年变迁的备忘录。十年前她被选择成为国家电视台新闻主播，却因毫无经验而遭遇挫败，非典时期成为现场记者后，现实生活犬牙交错的切肤之感，让她一点一滴脱离外在与自我的束缚，对生活与人性有了更为宽广与深厚的理解。十年之间，非典、汶川地震、两会报道、北京奥运……在每个重大事件现场，几乎都能发现柴静的身影，而如华南虎照、征地等刚性的调查报道她也多有制作。在书中，她记录下淹没在宏大叙事中的动人细节，为时代留下私人的注脚。一如既往，柴静看见并记录下新闻中给她留下强烈生命印象的个人，每个人都深嵌在世界之中，没有人可以只是一个旁观者，他人经受的，我必经受。书中记录下的人与事，是他们的生活，也是你和我的生活。《看见》中，我没有刻意选择标志性事件，也没有描绘历史的雄心，在大量的新闻报道里，我只选择了留给我强烈生命印象的人，因为工作原因，我恰好与这些人相遇。他们是流淌的，从我心腹深处的石坝上漫溢出来，坚硬的成见和模式被一遍遍冲刷，摇摇欲坠，土崩瓦解。这种摇晃是危险的，但思想的本质就是不安。我试着尽可能诚实地写下这不断犯错、不断推翻、不断疑问、不断重建的事实和因果，一个国家由人构成，一个人也由无数他人构成，你想如何报道一个国家，就要如何报道自已。——柴静海报：','20427187','https://book.douban.com/subject/20427187/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('88','道德经','黄元吉','中国','陕西人民出版社','1996-10','121','4.50元','平装','中国传统文化精品丛书','9787224034813','9.5','18126','《道德經》，又稱《道德真經》、《老子》、《五千言》、《老子五千文》，是中國古代先秦諸子分家前的一部著作，為其時諸子所共仰，傳說是春秋時期的老子李耳所撰寫，是道家哲學思想的重要來源，道德經分上下兩篇，原文上篇《德經》、下篇《道經》，不分章，後改為《道經》37章在前，第38章之後為《德經》，並分為81章，是中國歷史上首部完整的哲學著作。','1059258','https://book.douban.com/subject/1059258/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('89','四世同堂','老舍','中国','北京十月文艺出版社','2008-07','590','36.00元','精装','老舍集','9787530209226','9.4','32224','值得每一代中国人阅读的文学经典，值得每一个中国人珍藏的民族记忆。老舍：它是我从事写作以来最长的、可能也是最好的一本书。入选《亚洲周刊》“二十世纪中文小说一百强”。这是一部中国现代长篇小说经典名著，是老舍先生的代表作之一。小说在卢沟桥事变爆发、北平沦陷的时代背景下，以祁家四世同堂的生活为主线，形象、真切地描绘了以小羊圈胡同住户为代表的各个阶层、各色人等的荣辱浮沉、生死存亡。作品记叙了北平沦陷后的畸形世态中，日寇铁蹄下广大平民的悲惨遭遇，那一派古老、宁静生活被打破后的不安、惶惑与震撼，鞭挞了附敌作恶者的丑恶灵魂，揭露了日本军国主义的残暴罪行，更反映出百姓们面对强敌愤而反抗的英勇无畏，讴歌、弘扬了中国人民伟大的爱国主义精神和坚贞高尚的民族气节，史诗般地展现了第二次世界大战期间，中国人民为世界反法西斯战争做出的杰出贡献，气度恢弘，可歌可泣。老舍先生以深厚精湛的艺术功力和炉火纯青的小说技艺刻画了祁老人、瑞宣、大赤包、冠晓荷等一系列栩栩如生的艺术形象，展现了风味浓郁的北平生活画卷，至今传读不衰，历久弥新……','3183775','https://book.douban.com/subject/3183775/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('90','美丽新世界','【英】奥尔德斯·赫胥黎','中国','上海译文出版社','2017-6','349','45.00元','精装','译文经典（精装本）','9787532774685','9.2','41123','出版于一九三二年的《美丽新世界》是奥尔德斯•赫胥黎最杰出的代表作，是二十世纪最著名的反乌托邦文学经典之一，与奥威尔的《一九八四》、扎米亚京的《我们》并称为“反乌托邦”三部曲，在国内外文学界和思想界影响深远。这是一部寓言作品，展现了赫胥黎眼中的人类社会的未来图景：通过最有效的科学和心理工程，人类从遗传和基因上就已经被先天设计为各种等级的社会成员，完全沦为驯顺的机器，个性和自由被扼杀，文学艺术濒于毁灭。继《美丽新世界》这部寓言小说杰作之后，赫胥黎又于一九五八年出版了论著《重返美丽新世界》，在这部雄辩的作品中，作者运用其丰富的社会学和人类学知识，比较了现代社会与他在《美丽新世界》中所构想的寓言性图景的方方面面，像人口过剩、宣传和洗脑以及化学劝诱等，认为他早年悲观的预言正在成为现实。本书将赫胥黎这两部最经典的名著全部收入。','27002046','https://book.douban.com/subject/27002046/',' BRAVE NEW WORLD,  BRAVE NEW WORLD REVISITED','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('91','爱你就像爱生命','李银河/王小波','中国','上海锦绣文章出版社','2008-5','222','18.00元','平装','王小波作品全集（磨铁2008版）','9787806859988','8.8','107519','王小波书信均选自朝华出版社2004年4月第一版《爱你就像爱生命》，此书系王小波生前从未发表过的与李银河的“两地书”，也是迄今他们夫妇最完整和独立的一本书信集。','3071717','https://book.douban.com/subject/3071717/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('92','罪与罚','[俄]陀思妥耶夫斯基','俄','上海译文出版社','2006-8','471','23.00元','平装','译文名著文库','9787532740079','9.3','29203','《罪与罚》系19世纪俄国大文豪陀思妥耶夫斯基的代表作之一。小说描写一心想成为拿破仑式的人物、认定自己是个超人的穷大学生拉斯柯尔尼科夫，受无政府主义思想毒害，为生活所迫，杀死放高利贷的房东老太婆和她的无辜的妹妹，制造了一起震惊全俄的凶杀案。经历了一场内心痛苦的忏悔后，他最终在基督教徒索尼雅姑娘的规劝下，投案自首，被判流放西伯利亚。作品着重表现主人公行凶后良心受到谴责，内心深感孤独、恐惧的精神状态，刻画他犯罪前后的心理变化。小说一方面描绘了俄国下层人民的悲惨生活，揭露贵族社会的罪恶；一方面也宣扬逆来顺受，从宗教中求解脱的思想。','1858568','https://book.douban.com/subject/1858568/',' Преступление и наказание','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('93','刘擎西方现代思想讲义','刘擎','中国','新星出版社','2021-1','384','79.00元','精装','得到讲义系列','9787513342919','9.2','24399','人生的意义，人们向往的自由和公平的价值，人类文明的复杂冲突与未来趋势……这些让你困惑的大小问题，过去也困扰过韦伯、尼采、萨特等杰出的头脑。他们尽最大努力做出阐释，为后人提供了宝贵的思想标识。在这部讲义里，刘擎介绍了现代视域下的19位思想大家，广泛而系统地讨论工具理性的利弊，如何面对虚无主义，消费主义对人的异化，财富分配的公平正义和全球化等议题。思想不惑，精神明亮。你将在这19位大家的生平故事中，理解他们建构思想大厦的地基与框架。你还会在思想大厦之上，直面个人生活和社会公共领域的诸多难题，收获审慎而真诚的回答。','35313227','https://book.douban.com/subject/35313227/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('94','我们仨','杨绛','中国','生活·读书·新知三联书店','2012-9','165','23.00元','平装','大家雅音','9787108042453','9.1','42343','《我们仨》是钱钟书夫人杨绛撰写的家庭生活回忆录。1998年，钱钟书逝世，而他和杨绛唯一的女儿钱瑗已于此前（1997年）先他们而去。在人生的伴侣离去四年后，杨绛在92岁高龄的时候用心记述了他们这个特殊家庭63年的风风雨雨、点点滴滴，结成回忆录《我们仨》。这本书分为两部分。第一部分中，作者以其一贯的慧心、独特的笔法，用梦境的形式讲述了最后几年中一家三口相依为命的情感体验。第二部分，以平实感人的文字记录了自1935年伉俪二人赴英国留学并在牛津喜得爱女，直至1998年丈夫逝世，63年间这个家庭鲜为人知的坎坷历程。','19958089','https://book.douban.com/subject/19958089/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('95','离开的，留下的','[意]埃莱娜·费兰特','意','人民文学出版社','2017-10','417','62.00元','平装','那不勒斯四部曲','9787020132003','8.9','61484','两个女人50年的友谊和战争那不勒斯四部曲NO.3全球畅销近千万册被翻译成40多种语言“我希望她在场，这是我写作的目的。”《我的天才女友》《新名字的故事》后续，意大利最神秘的作家费兰特（ElenaFerrante）探索中年的虚无、困惑、野心和近乎残暴的爱《离开的，留下的》是埃莱娜·费兰特的“那不勒斯四部曲”的第三部，聚焦“我”（埃莱娜）和莉拉躁动、紧密相依的中年。“我”在未婚夫彼得罗一家人的帮助下，出版了第一本小说，享受着成功的喜悦，而留在那不勒斯的莉拉却身陷贫困而卑贱的工厂生活。“我”像个骑士一样，再度介入莉拉的生活，并动用丈夫一家人的关系，让莉拉和恩佐的生活有了转机——他们成了那不勒斯地区最早学习、掌握计算机技术的人，他们顽强、坚韧的学习能力让他们开始积累了巨大的财富。我和莉拉之间再次胜负难辨。婚后的“我”开始面临自己的创作危机，而平静的、中产阶级式的婚姻也令“我”疲惫不堪。对社会变革颇为冷淡的彼得罗希望“我”放弃作家的身份，而“我”在扮演“母亲”、“妻子”这些角色时，总是避免不了内心的分裂、紧张。莉拉对“我”的新作品的指责更是让我产生彻底的自我怀疑：我接受的教育和拥有的知识是否只是让我的作品徒有其表，没有真正的思想，也永远无法拥有莉拉近乎粗野的表达中那些令人不安的真相。直到有一天，“我”丈夫突然将尼诺带回了家……','27104959','https://book.douban.com/subject/27104959/',' Storia di chi fugge e di chi resta','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('96','冬牧场','李娟','中国','新星出版社','2012-6','320','29.80元','平装','新经典文库：李娟作品','9787513305662','9','29442','文坛清新之风，阿勒泰的精灵李娟首部长篇纪实散文力作四个月、零距离、全程记述哈萨克民族冬牧场的点点滴滴！春天接羔，夏天催膘，秋天配种，冬天孕育。羊的一生是牧人的一年，牧人的一生呢？这绵延千里的家园，这些大地最隐秘微小的褶皱，这每一处最狭小脆弱的栖身之地……青春啊，财富啊，爱情啊，希望啊，全都默默无声。——李娟2010年冬天，李娟跟随一家熟识的哈萨克牧民深入阿勒泰南部的冬季牧场、沙漠，度过了一段艰辛迥异的荒野生活。李娟是第一位描写哈萨克民族冬牧生活的汉族作家，她以饱含深情又不失节制的文字，呈现出阿尔泰最后一批“荒野主人”冬季转场时的独特生存景观。','10522856','https://book.douban.com/subject/10522856/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('97','教父','[美]马里奥·普佐','美','译林出版社','1997-8','493','23.30元','平装','名著译林','9787805674278','9','38052','《教父》这部小说的不同凡响的艺术魅力就在于：尽管描写的全是坏蛋，但作者曲尽妙笔，竟然能让读者不痛恨个别坏蛋，而痛恨整个龌龊的社会结构。教父及其继承人——他的小儿子迈克尔本来都是坏透了的坏蛋，但是却并不显得令人痛恨，因为他们杀人是整个不合理的社会逼出来的，因为他们杀的也都是更坏的人，他们同那些在幕后“坐地分赃”的政客比较起来，在“坏”的程度上，可真是小巫见大巫了。美国纽约五大黑势力集团之一的维托·考利昂一家采用多种极端手段，实现了在整个美国黑暗势力团体中的独尊地位。在这场斗争中有黑团伙之间的火拼；有走私贩毒的嚣张；有赌场的烟云；有红灯区的人欲横流。本书被认为是描写资本主义社会中黑社会现象的最具权威的作品。>教父英文版>>','1092335','https://book.douban.com/subject/1092335/',' The Godfather','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('98','海子的诗','海子','中国','人民文学出版社','1999-04','292','15.40','平装','蓝星诗库','9787020026456','9','42764','本书收录了已故诗人海子的诗作精华，其诗以独特的风格深受读者的喜爱，从这些诗中反映出诗人那对于一切美好事物的眷恋之情，对于生命的世俗和崇高的激动和关怀。','1011754','https://book.douban.com/subject/1011754/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('99','巨人的陨落','[英]肯·福莱特','英','江苏凤凰文艺出版社','2016-5-1','1168','129.80元','平装','读客全球顶级畅销小说文库：肯·福莱特作品','9787539989891','8.9','80613','在第一次世界大战的硝烟中，每一个迈向死亡的生命都在热烈地生长——威尔士的矿工少年、刚失恋的美国法律系大学生、穷困潦倒的俄国兄弟、富有英俊的英格兰伯爵，以及痴情的德国特工…从充满灰尘和危险的煤矿到闪闪发光的皇室宫殿，从代表着权力的走廊到爱恨纠缠的卧室，五个家族迥然不同又纠葛不断的命运逐渐揭晓，波澜壮阔地展现了一个我们自认为了解，但从未如此真切感受过的20世纪。','26698660','https://book.douban.com/subject/26698660/',' Fall of Giants','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('100','人生的枷锁','[英]毛姆','英','上海译文出版社','2007-3','672','32.00元','平装','译文名著文库','9787532741731','9.1','27831','《人生的枷锁》是毛姆的代表作，具有明显的自传色彩。小说主人公菲利普·凯里自幼父母双亡，不幸又先天残疾，在冷漠而陌生的环境中度过了童年，性格因此孤僻而敏感。在寄宿学校度过的岁月让他饱受了不合理的学校制度的摧残，而当他走入社会后，又在爱情上经历了伤痛。在坎坷的人生道路上，他每跨一步，都要付出艰辛的挣扎，但思想和个性都独立不羁的凯里，一直努力挣脱宗教和小市民意识这两条禁锢自己精神的桎梏，力图在混沌纷扰的生活漩流中，寻求人生的真谛。','2035171','https://book.douban.com/subject/2035171/',' Of Human Bondage','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('101','看不见的城市','[意]伊塔洛·卡尔维诺','意','译林出版社','2012-4','182','25.00元','精装','卡尔维诺经典','9787544722278','9','30584','王小波、苏童、阿城、止庵是他的忠实粉丝朱天文，唐诺是卡尔维诺不余遗力的传播者梁文道说他一直在准备谈卡尔维诺，可是一直没准备好权威版本，全面修订2006年单行本译本，并增补卡尔维诺各作品自序、后记、注释等重要资料知名设计师全新装帧，精装双封设计，书脊烫金，封面采用原创线条图，赋予每部作品文学个性卡尔维诺认为他“写了一种东西，它就像是在越来越难以把城市当作城市来生活的时刻，献给城市的最后一首爱情诗。也许我们正在接近城市生活的一个危机时刻，而《看不见的城市》则是从这些不可生活的城市的心中生出来的一个梦想。”','10555509','https://book.douban.com/subject/10555509/',' Le città invisibili','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('102','人生的智慧','(德)叔本华','中国','上海人民出版社','2008-10-01','','28.00元','平装','叔本华系列','9787208081178','9.2','28714','他于1850年写了《附录和补遗》一书，《人生的智慧》是该书中的一部分。在书中，他以优雅的文体，格言式的笔触阐述了自己对人生的看法。《人生的智慧》使沉寂多年的叔本华在晚年一举成名。我社出版的这本全译本是我国第一个根据德文版译出的中文本。这本书讨论的事情与我们的世俗生活至为接近，诸如健康、财富、名声、荣誉、养生和待人接物所应遵守的原则等。正如叔本华所说的，在这本书里他尽量以世俗、实用的角度考虑问题。因此，这本书尤其适合大众阅读。虽然叔氏尽量放弃了居高俯临的审视角度，但关于错综复杂并因此众说纷纭的世俗人情的话题，经过这位思想大师一贯深刻而因此透彻的讨论之后，变得清晰简明，话题也几已穷尽矣。','3261600','https://book.douban.com/subject/3261600/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('103','叫魂','[美]孔飞力','美','生活·读书·新知三联书店上海三联书店','2012-5','368','38.00元','平装','孔飞力著作集','9787108037909','9.1','26151','《叫魂:1768年中国妖术大恐慌》中在中国的千年帝制时代，清高宗弘历可谓是空前绝后的一人，然而在乾隆盛世达到登峰造极的时候，整个大清的政治与社会生活却被一股名为“叫魂”的妖术搅得天昏地暗。在1768年从春天到秋天的那几个月里，这股妖风冲击到了半个中国，百姓为之人心惶惶，官员为之疲于奔命，皇帝为之寝食不宁。在讲述叫魂故事的过程中，《叫魂:1768年中国妖术大恐慌》重在讨论这背后的历史意蕴。叫魂故事可以帮助我们理解传统中国政治和中国社会的一些基本问题。','10471333','https://book.douban.com/subject/10471333/',' Soulstealers','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('104','飞鸟集','[印]罗宾德拉纳德·泰戈尔','印','哈尔滨出版社','2004-6','186','16.80元','平装','先知双语经典','9787806992197','8.9','61820','《飞鸟集》是泰戈尔的代表作之一，也是世界上最杰出的诗集之一。白昼和黑夜、溪流和海洋、自由和背叛，都在泰戈尔的笔下合而为一，短小的语句道出了深刻的人生哲理，引领世人探寻真理和智慧的源泉。初读这些诗篇，如同在暴风雨过后的初夏清晨，推开卧室的窗户，看到一个淡泊清透的世界，一切都是那样清新、亮丽，可是其中的韵味却很厚实，耐人寻味。','1058661','https://book.douban.com/subject/1058661/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('105','斯通纳','[美]约翰·威廉斯','美','上海人民出版社','2016-1','336','49.00元','精装','约翰·威廉斯在文景','9787208130500','8.8','54964','《斯通纳》讲述了生命中最重要的部分：爱，认同，怜悯，志业，傲骨，信任与死亡。一个勇者有过的失败不失意的人生：即使不能拥有完美的生活，所幸追求过完整的自我。美国，密苏里州。来自偏远农场的农家子弟、19岁的威廉·斯通纳进入州立密苏里大学学习农学。自一堂选修文学课为起点，他的一生就此悄然改变。未来的斯通纳成为了一名大学老师，结婚、生子、教学、退休、衰老、死亡。在他生命的尽头，或许他可以坦然面对这个问题：你的一生，还要期望别的什么吗？一部蕴含着真诚、激情与紧凑力量的小说，探究了历史洪流所忽视的人性之间的冲突、溃败与幸存，重新唤起思考每个个体独特存在的意义。作者约翰·威廉斯向世人展示了凡人中的勇者在如何生活。◆威廉斯的写作就像被打磨光滑的橡木上反射出本色、持久的光泽。这本书的伟大之处在于，它以不带一丝一毫悲喜的冷静洞察了生活本质的全部。《斯通纳》迟到五十年后畅销，是献给被亏欠的艺术一次迟来的正名。——《纽约客》◆斯通纳活过，然后死去，如你我一样。他生活中发生的每一件事就发生在你我面前，看似平淡。然而，写出这种“简单”却需要天才才能完成。读这本书是一本令人难过却又美妙的阅读体验。——《星期日泰晤士报》◆约翰·威廉斯的《斯通纳》刻画了一个将追求智识当作对抗无意义的战争、拒绝与外部世界作廉价妥协的人的一生。它的意义或许不只是一部伟大的小说——它简直是一部几近完美的小说，机构精巧，语言优美，故事动人，展示着一种夺人心魄的完美。——《纽约时报》◆一件文学的珍宝。——《明镜周刊》◆拥有超乎寻常的力量……直击生活的真相，语言清澈优美。它是一部献给所有热爱文学之人的传奇。——伊恩·麦克尤恩◆一本精彩绝伦的小说，既丰富又忧郁，记录着伤痛与失去的同时也留存了美好温柔的时刻。这本小说完美展现了小说艺术的要素，它的叙事节奏与情节构架均堪称完美无缺。这本小说像一条河流，冷静而又流畅的平静笔调裹挟着磅礴延绵的力量。——亚当·福尔兹','26425831','https://book.douban.com/subject/26425831/',' Stoner','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('106','鹿鼎记','金庸','中国','广州出版社花城出版社','2008-3','1821','108.00元','平装','金庸作品集（新修版·平装）','9787806553404','8.9','52981','《鹿鼎记》是金庸创作的最后一部小说，也是他最重要的代表作。小说讲的是一个从小在扬州妓院长大的小孩韦小宝，他以不会任何武功之姿态闯江湖各大帮会、周旋皇帝朝臣之间并奉旨远征云南、俄罗斯之故事，书中充满精彩的对白及逆思考的事件。韦小宝笑称自己“不学无术却处处有术”，他靠着这不学之术在太监、钦命大臣、帮会堂主、和尚、甚至七个美女的共同老公这些角色上游刃有余。这个人物挑战了真实的人性，说明武功和权力不是这世上唯一的可取之道。','1212893','https://book.douban.com/subject/1212893/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('107','梦里花落知多少','三毛','中国','北京十月文艺出版社','2007-6','312','28.00元','精装','三毛集','9787530208922','8.8','93110','《梦里花落知多少》记录了荷西过世后三毛的生活，共23篇。内容均为台湾皇冠出版社81年至88年的初版。荷西的死对三毛又是一场劫难，因此由《背影》到这本书，在文学、心情上都与早期大不相同，说情叙事极其内敛，但在曲折的空间之余，读者当能感受到三毛哀而不伤的笔调里，更多的其实是三毛对人生的坚定。','2070844','https://book.douban.com/subject/2070844/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('108','棋王·树王·孩子王','阿城','中国','江苏凤凰文艺出版社','2016-3-1','179','58.00','精装','阿城文集（精装）','9787539979359','9.3','20691','此次出版的是阿城先生亲自编订的最新版本，增添了许多以往版本所不具备的内容。内容包括以往版本都有的《棋王》《树王》《孩子王》三篇中篇小说，以及以往版本所不具备的画家曹力为作者画的漫画像，还包括以往各版本（意大利文版、日文版、旧简体版、台湾繁体版）的序言。','26734559','https://book.douban.com/subject/26734559/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('109','绝叫','[日]叶真中显','日','北京联合出版公司','2020-7','440','58.00元','平装','叶真中显作品','9787559641182','8.9','62097','罗翔老师推荐！“这本书深刻地揭示了日本的社会问题，日本40多年来社会变迁的各种缩影。”豆瓣「2020年度读书榜单」推理·悬疑类top1！怀才者的自怜是天鹅的挽歌，平庸者的崩坏是野兽的绝叫。-★同名日剧位居豆瓣“2019评分最高日本剧集”榜单Top10★日本现象级社会派推理小说★长达四十年的恶女编年史★绝对现实，却超乎想象！-【内容简介】铃木阳子死了，死在独居的公寓里。正确说来，是铃木阳子几个月前死了。因为发现她时，不但遗体遭到屋内的十一只猫啃食，连猫也全数饿死了。铃木阳子显然是「孤独死」的最佳范例，但这名女子为何落到这步田地？她的亲人、朋友、同事在哪里？她的人生轨迹又是如何？-【编辑推荐】《绝叫》是日本新锐社会派推理作家叶真中显创作的推理小说，讲述了女主人公阳子在平庸懦弱的表象之下，隐密的跌宕、罪恶人生。该书揭露常被人们忽视的社会问题，深刻而引人深思，刻画人物思想的变化细腻而一针见血，以阳子的离奇经历，连缀了日本四十年来社会变迁的各种缩影，是日本近年来具有代表性的优秀推理小说。该书在日本一经出版就引起极大反响，入围第68回日本推理作家协会奖、第36回吉川英治文学新人奖、第27回“这本推理小说了不起”大奖、第12回书店大奖、第11回启文堂大奖、周刊文春年度推理小说榜。后《绝叫》繁体版问世，使国内推理小说爱好者得以一探究竟。《绝叫》繁体版在豆瓣获得8.6的高评分，是少有的在有简体中文版之前就广受好评的外国小说。2019年，根据《绝叫》改编的同名日剧播出，又获得了豆瓣逾万人点评、8.4分的好成绩，并入围豆瓣“2019评分最高日本剧集”榜单Top10。2020年7月，《绝叫》简体中文版正式上市，以飨读者。','35031587','https://book.douban.com/subject/35031587/',' 絕叫','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('111','这些人，那些事','吴念真','中国','译林出版社','2011-9','203','28.00元','平装','吴念真作品','9787544717731','8.8','64197','吴念真累积多年、珍藏心底的体会与感动。他写的每个故事，都蕴藏了我们无法预知的生命能量与心灵启发。跟他一起回望人生种种，您将学会包容、豁达与感恩……本书是吴念真导演经历过人生的风风雨雨和最大低潮后，所完成的生命记事。他用文字写下心底最挂念的家人、日夜惦记的家乡、一辈子搏真情的朋友，以及台湾各个角落里最真实的感动。这些人和事，透过他真情挚意的笔，如此跃然的活在你我眼前，笑泪交织的同时，也无可取代的成为烙印在你我心底、这一个时代的美好缩影……特别收录吴念真近年唯一小说创作《遗书》，写下对胞弟离开人间的真情告白特别邀请作家雷骧绘制插画，看两位大师以图文激荡出精采火花生命里某些当时充满怨怼的曲折，在后来好像都成了一种能量和养分……这些人、那些事在经过时间的筛滤之后，几乎都只剩下笑与泪与感动和温暖。——《这些人，那些事》','6388661','https://book.douban.com/subject/6388661/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('110','陆犯焉识','严歌苓','中国','作家出版社','2011-10','415','35.00元','平装','','9787506360876','8.9','53719','《陆犯焉识》内容简介：陆焉识本是上海大户人家才子+公子型的少爷，聪慧而倜傥，会多国语言，也会讨女人喜欢。父亲去世后，年轻无嗣的继母冯仪芳为了巩固其在家族中的地位，软硬兼施地使他娶了自己的娘家侄女冯婉喻。没有爱情的陆焉识很快出国留学，在美国华盛顿毫无愧意地过了几年花花公子的自由生活。毕业回国后的陆焉识博士开始了风流得意的大学教授生活，也开始了在风情而精明的继母和温婉而坚韧的妻子夹缝间尴尬的家庭生活。五十年代，陆焉识因其出身、更因其不谙世事的张扬激越而成为”反革命“”，在历次运动中，其迂腐可笑的书生气使他的刑期一次次延长，直至被判为无期。这位智商超群的留美博士由此揣着极高的学识在西北大荒草漠上改造了二十年。精神的匮乏、政治的严苛、犯人间的相互围猎与倾轧，终使他身上满布的旧时代文人华贵的自尊凋谢成一地碎片。枯寂中对繁华半生的反刍，使他确认了内心对婉喻的深爱。婉喻曾是他寡味的开端，却在回忆里成为他完美的归宿。“文革”“结束后，饱经思念的陆焉识和冯婉喻终于可以团聚，然而回到上海家中的陆焉识却发现岁月和政治彻底改变了他的生活，他再也找不到自己存在的位置：一生沉沦、终成俗庸小市民的儿子一直排斥和利用他，才貌俱佳、终成大龄剩女的小女儿对他爱怨纠结，态度几经转变，唯一苦苦等待他归来的婉喻却在他到家前突然失忆。《陆犯焉识》编辑推荐：被中西方文坛誉为；“华文世界最值得期待的作家”；严歌苓颠覆性转型之作；当政治与人生相撞，孰是孰非……最大胆的笔触，直逼人类精神的灰色地带；最温情的回眸，抚摸岁月带给爱情的伤痕。严歌苓近年来一直萦怀于对自身家族史特别是对其祖父人生遭际和精神世界的探寻。依此多年的功课，《陆犯焉识》以深远的济世情怀，将知识分子陆焉识的命运铺展在中国近当代政治这块庞大而坚硬的底布上，检视了残酷岁月里生命可能达到的高度。她的笔触往返于主人公盛年时流连的浮华地美国、上海和其后半生被禁锢的流放地西北大荒漠，世态的炎凉和命运的多诡尽收眼底。她的讲述，冷静与幽默同行，温情与练达并重，大有“翻手为苍凉，覆手为繁华”之概。','6880158','https://book.douban.com/subject/6880158/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('112','边城','沈从文/黄永玉卓雅插图.','中国','北岳文艺出版社','2002-4','174','12.00元','平装','沈从文作品(插图本)','9787537823425','8.7','215558','《边城》是沈从文的代表作，写于一九三三年至一九三四年初。这篇作品如沈从文的其他湘西作品，着眼于普通人、善良人的命运变迁，描摹了湘女翠翠阴差阳错的生活悲剧，诚如作者所言：“一切充满了善，然而到处是不凑巧。既然是不凑巧，因之素朴的善终难免产生悲剧。”《边城》写出了一种如梦似幻之美，像摆渡、教子、救人、助人、送葬这些日常小事，在作者来都显得相当理想化，颇有几分“君子田”的气象。当然，矛盾也并非不存在，明眼人一看便知，作者所用的背景材料中便隐伏着社会矛盾的影子。作者亦不曾讳言他的写作意图是支持“民族复兴大业的人”，“给他们一种勇气和信心”。','1057244','https://book.douban.com/subject/1057244/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('113','失明症漫记','[葡]若泽·萨拉马戈','葡','南海出版公司','2014-2','281','29.50','平装','新经典文库：若泽·萨拉马戈作品','9787544264112','9.1','26083','你为什么会写这样一部冷酷的作品？虽然我活得很好，但这个世界却不好。诺贝尔文学奖得主萨拉马戈代表作入选诺贝尔学院“所有时代百部最佳文学作品”被《泰晤士报》誉为“当今世界最卓越的一部作品”与奥威尔的《一九八四》、卡夫卡的《审判》并驾齐驱。——《柯克斯书评》萨拉马戈用想象力、同情心和反讽所维系的寓言，让我们把握到捉摸不定的现实。——诺贝尔颁奖辞萨拉马戈和马尔克斯是我心目中最好的两位作家，但在我看来，萨拉马戈对现实的隐喻更强。——著名作家苏童繁忙的路口，绿灯亮了，中间车道的头一辆汽车却停止不前，司机在挡风玻璃后面挥舞着手臂，围观的人打开车门之后，才知道他在喊：我瞎了！没有人会相信，他的眼睛清晰明亮，巩膜像瓷器一样洁白致密，然而他却一再绝望地喊着：我瞎了！我瞎了！一位路人送他回家，却被传染上失明的怪疾。眼科医生成了第三个牺牲品。失明症迅速蔓延，整个城市陷入了一场空前的灾难。','20428302','https://book.douban.com/subject/20428302/',' Ensaio sobre a Cegueira','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('114','也许你该找个人聊聊','[美]洛莉·戈特利布','美','果麦文化/上海文化出版社','2021-7-28','460','68.00元','平装','','9787553522838','9','32660','这是一位心理治疗师的回忆录，讲述了发生在诊室中的故事。在这个小小的密闭空间里，人们会展现出最真实、最脆弱的一面；也是在这里，人们获得了陪伴和倾听，也获得了宝贵的觉察、成长与改变。在书中，我们会看到四个来访者的故事，他们是：一个四十多岁、事业成功、自以为是，认为身边所有人都是蠢货的好莱坞制片人；一个三十多岁、刚刚新婚就被诊断出患有绝症，时日不多的大学女教师；一个六十九岁、离过三次婚，感觉孤独绝望，声称生活再不好转就要在七十岁生日当天自杀的老太太；一个二十多岁、有原生家庭创伤和酗酒问题，在爱情中频频受挫的姑娘。同时，书中还有第五个寻求帮助的人，那就是治疗师自己。她是一个单身的职场妈妈，四十多岁时遭遇失恋，几乎崩溃。有朋友对她说“或许你该找个人聊聊”，于是她也给自己找了一位心理治疗师。当她切换到来访者的位置，坐到另一位心理治疗师的沙发上诉说自己内心的脆弱与悲伤，就更能感受到心理治疗为何具有治愈和改变的力量。这本书从治疗师和来访者的双重视角展现了心理治疗的过程，让我们发现：无论身份背景有多相异，人类面对的烦恼其实都相通——爱与被爱、遗憾、选择、控制、不确定、死亡，这些都是我们身而为人必须共同学习面对的议题。我们在现实生活中所遭遇的切肤之痛和生命困境，都能在这本书中得到共鸣、找到希望。本书是2019年美国众多媒体推荐的心理自助书——它荣登《纽约时报》《时代》《人物》《综艺》等媒体年度必读书榜单，是美国亚马逊当年度销售TOP10图书、哈佛商学院年度推荐图书，它的有声书版本也是当年Audible非虚构类有声书第一名。刚上市一个月，这本书就冲上了美国亚马逊图书总榜TOP100和《纽约时报》畅销榜；出版两年来，已经在全球授权了四十多个语言版本，总销量超过七十万册，并且正在拍摄电视连续剧，由曾出演《绝望主妇》的知名演员伊娃·朗格利亚担纲主演。享誉全球的心理学泰斗、美国斯坦福大学精神病学终身荣誉教授欧文·亚隆为这本书写下推荐语：“我读心理治疗的书超过半个世纪了，但从来没见过这样的书：这么大胆、这么直白、这么多好故事，又这么深刻而引人入胜。”国内知名心理学家李松蔚为这本书担任专业审校，并写下推荐序：“读这本书的过程，就是在灵魂的方寸之地一寸寸探索。越往下，越是晦暗幽深，于无声处听惊雷。”','35481512','https://book.douban.com/subject/35481512/',' Maybe You Should Talk to Someone','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('115','金色梦乡','[日]伊坂幸太郎','日','译林出版社','2010-9','432','35.00元','精装','乐读文库','9787544713986','9','27133','平凡的送貨員──青柳雅春和多年不見的老友森田森吾偶遇，卻突然被告知首相即將被暗殺，而青柳則是兇手！還一頭霧水的青柳，早已成為警方不擇手段要緝捕的對象，更慘的是，馬上成為媒體炒作下十惡不赦的罪人，唯一的對策只有不斷的逃、逃、逃……一路上，小混混、無差別連續殺人魔、前女友等各路人馬相繼出現，情義相挺，他最後會栽在莫名其妙的命運作弄中，還是能順利逃出重圍呢？','5038409','https://book.douban.com/subject/5038409/',' ゴールデンスランバー','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('116','受戒','汪曾祺','中国','漓江出版社','1987-10','573','3.95元','平装','作家自选集系列','9787540701758','9.3','22425','本书收入了小说54篇、散文12篇和少量的诗。其中《受戒》和《大淖记事》曾获全国短篇小说奖。','1760432','https://book.douban.com/subject/1760432/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('117','时间简史','[英]史蒂芬·霍金','英','湖南科学技术出版社','2010-4','245','45.00元','平装','','9787535732309','8.9','58513','《时间简史》讲述是探索时间和空间核心秘密的故事，是关于宇宙本性的最前沿知识，包括我们的宇宙图像、空间和时间、膨胀的宇宙不确定性原理、基本粒子和自然的力、黑洞、黑洞不是这么黑、时间箭头等内容。第一版中的许多理论预言，后来在对微观或宏观宇宙世界观测中得到证实。自1988年首版以来，《时间简史》已成为全球科学著作的里程碑。它被翻译成40种文字，销售了近1000万册。此版更新了内容，把许多观测揭示的新知识，以及霍金最新的研究纳入，并配以250幅照片和电脑制作的三维和四维空间图。','1034282','https://book.douban.com/subject/1034282/',' A Brief History of Time','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('118','毛泽东选集 第一卷','毛泽东','中国','人民出版社','1991-5','340','40.00元','精装','毛泽东选集','9787010009148','9.6','21596','《毛泽东选集》第一卷包括了毛泽东同志在中国革命各个时期中的重要著作。几年前各地方曾经出过几种不同版本的《毛泽东选集》，都是没有经过著者审查的，体例颇为杂乱，文字亦有错讹，有些重要的著作又没有收过去。现在的这部选集，是按照中国共产党成立后所经历的各个历史时期并且按照著作年月次序而编辑的。这部选集尽可能地搜集了一些为各地方过去印行的集子还没有包括在内的重要著作。选集中的各篇著作，都经著者校阅过，其中有些地方著者曾作了一些文字上的修正，也有个别的文章曾作了一些内容上的补充和修改。下面有几点属于出版事务的声明：第一，现在出版的这个选集，还是不很完备的。由于国民党反动派对于革命文献的毁灭，由于在长期战争中革命文献的散失，我们现在还不能够找到毛泽东同志的全部著作，特别是毛泽东同志所写的许多书信和电报（这些在毛泽东同志著作中占很大的部分）。第二，有些曾经流行的著作，例如《农村调查》，遵照著者的意见，没有编入；又如《经济问题与财政问题》，也遵照著者的意见，只编进了其中的第一章（即《关于过去工作的基本总结》）。第三，选集中作了一些注释。其中一部分是属于题解的，附在各篇第一页的下面；其他部分，有属于政治性质的，有属于技术性质的，都附在文章的末尾。第四，本选集有两种装订的本子。一种是各时期的著作合订的一卷本，另一种是四卷本。四卷本的第一卷包括第一次国内革命战争时期和第二次国内革命战争时期的著作；第二卷和第三卷包括抗日战争时期的著作；第四卷包括第三次国内革命战争时期的著作。','1139360','https://book.douban.com/subject/1139360/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('119','王尔德童话','[英]王尔德','英','译林出版社','2003-6','129','6.00元','精装','译林少儿文库','9787805676104','9.1','21729','王尔德是19世纪英国最伟大的艺术家之一，以其剧作、诗歌、童话和小说名世。在风流才子那颓废唯美、狷狂放浪的表面姿态下，是一颗纯美纯善，永难泯灭的童心。而这可贵童心一经与卓绝才智结合，便诞生了《王尔德童话》。它不仅为作者奠定了文学声名的基石，更成为世界文学宝库中的传世佳作。其语言纯正优美堪称典范，其意境高洁悠远益人心智，值得向每一个童稚未凿的孩子、每一位葆有赤子之心的成人郑重推荐。','1119839','https://book.douban.com/subject/1119839/',' The tales of Oscar Wilde','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('120','小径分岔的花园','[阿根廷]豪·路·博尔赫斯','阿根廷','上海译文出版社','2015-7','102','23.00','平装','博尔赫斯全集','9787532762897','8.9','36307','本书为1941年的短篇小说集，收小说七篇。其中，《小径分岔的花园》是侦探小说，讲述一桩罪行的准备工作和实施过程。小径分岔的花园是一个谜语，或者说寓言，而谜底正是时间。','25796120','https://book.douban.com/subject/25796120/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('121','你当像鸟飞往你的山','[美]塔拉·韦斯特弗','美','南海出版公司','2019-10','388','59.00元','精装','','9787544276986','8.7','153376','人们只看到我的与众不同：一个十七岁前从未踏入教室的大山女孩，却戴上一顶学历的高帽，熠熠生辉。只有我知道自己的真面目：我来自一个极少有人能想象的家庭。我的童年由垃圾场的废铜烂铁铸成，那里没有读书声，只有起重机的轰鸣。不上学，不就医，是父亲要我们坚持的忠诚与真理。父亲不允许我们拥有自己的声音，我们的意志是他眼中的恶魔。哈佛大学，剑桥大学，哲学硕士，历史博士……我知道，像我这样从垃圾堆里爬出来的无知女孩，能取得如今的成就，应当感激涕零才对。但我丝毫提不起热情。我曾怯懦、崩溃、自我怀疑，内心里有什么东西腐烂了，恶臭熏天。直到我逃离大山，打开另一个世界。那是教育给我的新世界，那是我生命的无限可能。','33440205','https://book.douban.com/subject/33440205/',' Educated','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('122','十万个为什么','','','少年儿童出版社','1962','14册','30.00元','平装','','','9.1','16994','1980年《十万个为什么》开始进行第三次大修订，推出第四版。这次大修订，肯定了初版本的特色，否定了“文革”版本，大体上保持第二版的规模和创作特色，仍是14分册，仍是保持通俗易懂、生动活泼、引人入胜的风格。第四版仍为14个分册，包括数学1、物理2、化学2、动物2、植物2、地学1、天文1、气象1、医学2。自1980年4月起至1981年10月出齐。这一版除拨乱反正了第3版受“阶级斗争”、“路线斗争”影响的内容外，还根据科学技术的发展，增加了许多新的内容。1990年初，又着手编辑10个分册的“续编本”，于1993年3月一次出齐，同前14个分册合在一起，至此，《十万个为什么》第四版共计24个分册，包括数学2、物理3、化学3、动物3、植物3、地学2、天文2、气?2、医学4。','2160556','https://book.douban.com/subject/2160556/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('123','昨日的世界','[奥]斯蒂芬·茨威格','奥','广西师范大学出版社','2004-5-1','350','32.00元','平装','影响过一代人的书','9787563345809','9.3','13467','作者以饱满真挚的感情、平实顺畅的文宇叙述了他所认识的特定时期的各种人物，他亲身经历的社会政治事件，他对那个动荡不安的时代的感受，记录了当时欧洲从一战前夜到二战欧洲危局的社会现实，批露了世界文化名人鲜为人知的生活轶事，同时穿插了作者各种细腻的心迹。...','1062343','https://book.douban.com/subject/1062343/',' Die Welt von Gestern','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('124','克莱因壶','[日]冈岛二人','日','化学工业出版社','2019-9','299','48.00元','平装','冈岛二人作品','9787122346032','8.9','47348','什么时候你开始怀疑这个世界是假的？.日本虚拟现实VR题材开山杰作，超前《盗梦空间》20年！传奇推理作家组合冈岛二人预言之书，以超强的技术预见力，30年前就已完美构想出虚拟现实游戏体验装置！.这是一部拥有“噩梦特质”的小说，是一次鲜有的、堪比3D观影、脑洞大开的、深陷其中难以自拔的独特体验。——资深推理人天蝎小猪..上杉彰彦从未如此兴奋——他写的故事被伊普西隆研发公司买下，即将制作成颠覆时代的全新游戏《克莱因2》（Klein2），并受邀与少女梨纱一同担任游戏测试员。上杉彻底陷入了由K2造就的完美虚拟世界，为它的逼真、超前赞叹不已。然而随着测试过程的深入，伊普西隆公司行事神秘得令他生疑，游戏中更不断听到有人警告他：“快逃！”与此同时，一个自称是梨纱好友的女孩找到上杉，她说梨纱已失踪多日，音信全无。然而在寻人的过程中，两人都开始怀疑对方在撒谎，因为他们的记忆完全对不上……','33658616','https://book.douban.com/subject/33658616/',' クラインの壷','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('125','桶川跟踪狂杀人事件','[日]清水洁','日','四川人民出版社','2021-2','328','45.00元','平装','','9787220118906','9','32083','推动反跟踪骚扰法案出台的凶杀案件！是谁杀害了一个女孩三次？调查记者全程追踪，直击日本官僚体制的结构性罪恶日本纪实文学金字塔尖之作，日本记者会议大奖作品◎编辑推荐“我的女儿被杀害了三次。”第一次是罪犯，第二次是怠于调查的警方，第三次是伤害她名誉的媒体。前男友跟踪骚扰、买凶杀害+警方坐视不理+媒体污名化报道，无辜的受害人被一次次推向深渊。遭遇骚扰和暴力、受到死亡威胁的女性，保留证据，奋力求救，为何无人听见？饱受白眼的“三流”记者，为何挺身而出，站在公权力的对立面，揭露警方的一个又一个谎言？■调查记者清水洁全程追踪，秉持“人”的良心而报道，传递受害人的真实声音。先于警方找到实行犯，揭露警察与官方媒体的罔顾事实、操纵舆论的“共犯”结构。■十幅照片还原侦破过程，直击逮捕现场。■本作“日本记者会议（JCJ）大奖”“编辑选择杂志报道奖”，被誉为“记者的教科书”“日本纪实文学的金字塔”。■此案推动了日本《跟踪骚扰行为规范法》出台。◎内容简介一九九九年十月二十六日，年轻女子猪野诗织在日本埼玉县JR桶川站前遭人持刀刺死。受害者生前因长期受到跟踪骚扰而多次向警方报案，警方却未予重视，最终没能阻止悲剧的发生。记者清水洁在调查这一事件的过程中，依从受害者生前留下的“遗言”的引导，多方走访查证，在警方轻视线索、调查不力的情况下，以记者的身份找到了实行犯，进而揭露了警方对受害者生前报案的漠视、敷衍，以及案发后试图抹黑受害者、掩盖渎职事实的行为。此案引发强烈的社会反响，推动了日本《跟踪骚扰行为规范法》的出台。本书是该事件的全记录。◎媒体评论此书除了让我们重新审视公权力的体制内在、媒体载舟覆舟的能力，也揭示了这样的意义：每个人都可能是促成社会之恶的原因，但每个人也都可以有所选择、选择自己不去构成社会之恶，甚至可以交付一己之力成就善的一部分。——2019台湾OPENBOOK年度好书推荐语清水先生是一位著名的记者，曾采访报道过“桶川跟踪狂杀人事件”“足利事件”等大案，放出过许多推翻警方与检方调查结论的独家特写。——伊藤诗织在《黑箱》中评价清水洁用这种方式，清水洁在调查上面把警方绝对不愿意别人知道的内幕给揭露出来，当然他绝对来不及救回被害者，但他尽到了作为一个调查记者最重要的责任，他不放弃把警方不愿意被大众知道的事实给揭露出来。这本书值得大家看一下，想一下。——《杨照读书》为了回应被害者的「遗言」而开始活动的清水记者，带着甚至是「愚直」的执念，最终将犯人和警察双方逼迫得走投无路，真的很精彩。我认为这才是记者的精神，这就是正义。我不常使用“感动”、“正义”等词语（而我这次使用了），所以我关于这部著作的感想，应该可以得到大家的信任。——明治大学法科大学院教授瀬木比吕志','35094680','https://book.douban.com/subject/35094680/',' 桶川ストーカー殺人事件','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('126','故事','[美]罗伯特·麦基','美','天津人民出版社','2014-9','522','68.00元','精装','','9787201088334','9.2','16710','自1997年初版以来，《故事》一直是全世界编剧的第一必读经典，至今，仍属于美国亚马逊最畅销图书中的Top1%。集结了罗伯特•麦基30年的授课经验，本书在对《教父》《阿甘正传》《星球大战》等经典影片的详细分析中，清晰阐述了故事创作的核心原理，其指导意义不应只被影视圈的人所认识，更应得到小说创作、广告策划、文案撰写人才的充分开发。2014年《故事》中文版全新修订升级，译者周铁东拥有深厚的国内外影视行业实践经验，文风犀利。新增58条专业术语和背景知识注释，补充了未被原书整理进附录部分的剧作资料，同步更新458条所涉及影片、作品的中文信息，采用特殊开本和全新的封面材质。','25976544','https://book.douban.com/subject/25976544/',' Story','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('127','人间草木','汪曾祺','中国','江苏文艺出版社','2005-01','315','20.00元','平装','大家散文文存','9787539921969','9.2','26932','《人间草木》(汪曾祺著)是汪曾祺优秀的散文集。当代文坛上,能同时在小说和散文两块田地里经营,且自成一家的并不多,汪曾祺先生算是其中的一个。汪曾祺先生是公认的文体家,不仅能写一手优秀的小说,还能写一手漂亮的散文。','1253310','https://book.douban.com/subject/1253310/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('128','茶馆','老舍','中国','人民文学出版社','2002-1','142','9.80元','平装','大学生必读','9787020036509','9.1','26897','三幕话剧《茶馆》以北京一家大茶馆为背景，描写了清末、民初、抗战胜利以后三个历史时期的北京生活风貌，深刻地写出了王利发等人的真实命运。','1036613','https://book.douban.com/subject/1036613/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('129','绿毛水怪','王小波','中国','时代文艺出版社','2009-1','322','30.00元','平装','时代文艺出版社王小波系列','9787538725230','9.1','22002','《绿毛水怪》作者王小波的作品一直盛行不衰，王小波的小说在世界文学之林中创造出属于他的美，这美就像一束强光，刺穿了时间的阻隔，启迪了一代又一代刚刚开始识字读书的青年的心灵。王小波所虚构的艺术之美，以及他通过对现实世界的批评所传播的自由主义理念，已经在这个世界的文化和思想宝库中占据了一席之地，虽然并没有一个像诺贝尔文学奖之类的证书来印证这一点，但是，我们相信，时间就是他作品价值的证书。','3518332','https://book.douban.com/subject/3518332/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('130','國史大綱（上下）','錢穆','中国','商务印书馆','2013-8','914','76.00元','平装','钱穆学术著作','9787100017664','9.4','14937','這是一部中國通史，因用大學教科書體例寫成，不得不力求簡要，僅舉大納，刪其瑣節。內容於學術思想，政治制度，社會風氣，國際形勢，兼有顧及，惟但求其通為一體，明其治亂盛衰之所由，聞其一貫相承之為統，以指陳吾國家民族生命精神之所寄。至其人物之詳，事業之備，則待教者讀者之自加參考，自為引伸。本書主旨則在發明其相互影響，及先後之演變發展，以作國人如何應付現時代之種種事變作根據之借鑒。','1046492','https://book.douban.com/subject/1046492/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('131','一个陌生女人的来信','[奥]斯蒂芬·茨威格','奥','上海译文出版社','2007-7','340','20.00元','平装','斯蒂芬·茨威格作品集','9787532741571','8.7','135997','这是一部短篇小说集，除《一个陌生女人的来信》，亦按时间顺序收录了《火烧火燎的秘密》、《马来狂人》等名篇，作者的创作历程一目了然。《一个陌生女人的来信》讲述一个刻骨铭心的爱情故事，一个女子暗恋男主人公18年，直至临死才决定向他告白。','2154960','https://book.douban.com/subject/2154960/',' Brief einer Unbekannten','张玉书');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('132','经济学原理（上下）','[美国]格里高里·曼昆','美国','机械工业出版社','2003-8','323；410','88.00元','平装','华章教材经典译丛','9787111126768','9.1','20551','此《经济学原理》的第3版把较多篇幅用于应用与政策，较少篇幅用于正规的经济理论。书中主要从供给与需求、企业行为与消费者选择理论、长期经济增长与短期经济波动以及宏观经济政策等角度深入浅出地剖析了经济学家们的世界观。','1028842','https://book.douban.com/subject/1028842/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('133','海的女儿','安徒生','中国','上海译文出版社','1978年6月','150','0.43','平装','安徒生童话全集','','9.1','20233','','1765512','https://book.douban.com/subject/1765512/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('134','面纱','[英]威廉·萨默塞特·毛姆','英','江西人民出版社','2016-4-1','288','39.8元','平装','果麦·外国文学经典','9787210082835','9.1','26015','“那描画的面纱，芸芸众生称之为生活”凯蒂怎么也琢磨不透，丈夫到底有没有发现她出轨的秘密——偷情时门把手的转动、晚餐时意有所指的话语，还有突然要带她前往霍乱之地的决定，这些都让凯蒂的神经越绷越紧。终于，忍无可忍的她决定摊牌。然而面纱揭开，爱情的真相与人性的冷漠将她的天真击得粉碎。','26757680','https://book.douban.com/subject/26757680/',' The Painted Veil','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('135','阿勒泰的角落','李娟','中国','万卷出版公司','2010-6','272','25.00元','平装','','9787547010105','9.1','18162','《阿勒泰的角落》是关于新疆的最美丽文字，这是现代版《呼兰河传》。由作者1998-2003年之间陆续完成并在《文汇报》、《南方周末》等发表的短篇散文集结成册。作者以天然而纯真的笔调描述阿勒泰地区哈萨克族日常生活点滴趣事：做裁缝、可爱的孩子、来来去去的陌生人。她刻画的不是一组有关新疆的异域风情，她刻画的是我们内心的牧歌：白雪和阳光，青草和白桦林，优美、明亮。李娟阿勒泰的角落，给你带来最生动的故事！《阿勒泰的角落》适用于：青年，女性，对新疆好奇的读者，文艺青年。','4881850','https://book.douban.com/subject/4881850/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('136','万水千山走遍','三毛','中国','哈尔滨出版社','2003-08','215','13.80','平装','三毛全集（华文天下2003版）','9787806399057','8.9','43293','大地啊，我来到你岸上时原是一个陌生人，住在你房子里时原是一个旅客，而今我离开你的门时却是一个朋友了。当飞机降落在墨西哥首都的机场时，我的体力已经透支得几乎无法举步。长长的旅程，别人睡觉，我一直在看书。眼看全机的人都慢慢地走了，还让自己绑在安全带上。窗外的机场灯光通明，是夜间了。','1036490','https://book.douban.com/subject/1036490/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('137','史蒂夫·乔布斯传','[美]沃尔特·艾萨克森','美','中信出版社','2011-10-24','560','68.00元','平装','','9787508630069','8.7','57455','这本乔布斯唯一授权的官方传记，在2011年上半年由美国出版商西蒙舒斯特对外发布出版消息以来，备受全球媒体和业界瞩目，这本书的全球出版日期最终确定为2011年11月21日，简体中文版也将同步上市。两年多的时间，与乔布斯40多次的面对面倾谈，以及与乔布斯一百多个家庭成员、朋友、竞争对手、同事的不受限的采访，造就了这本独家传记。尽管乔布斯给予本书的采访和创作全面的配合，但他对内容从不干涉，也不要求出版前阅读全文的权利。对于任何资源和关联的人，他都不设限，甚至鼓励他所熟知的人袒露出自己的心声。“我已经做了很多并不值得自豪的事情，比如23岁时就让我的女友怀了孕，以及我对这件事的处理方式”，他说，“对我而言，没有什么不可以对外袒露的。”谈及和他共过事的人以及竞争对手，他直言不讳，甚至尖酸刻薄。他的激情、精力、欲望、完美主义、艺术修养、残暴还有对掌控权的迷恋塑造出的商业哲学一览无余。同样，他的朋友、敌人，还有同事得以为我们提供了一个前所未有的毫无掩饰的视角。他是一位极具创造力的企业家，他有如过山车般精彩的人生和犀利激越的性格，充满追求完美和誓不罢休的激情，他创造出个人电脑、动画电影、音乐、手机、平板电脑以及数字出版等6大产业的颠覆性变革。乔布斯的个性经常让周围的人愤怒和绝望，但其所创造出的产品也与这种个性息息相关，全然不可分割的，正如苹果的硬件和软件一样。他的故事既具有启发意义，又有警示意义，充满了关于创新、个性、领导力以及价值观的教益。','6798611','https://book.douban.com/subject/6798611/',' Steve Jobs','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('138','全球通史(上)','[美]斯塔夫里阿诺斯','美','北京大学出版社','2006-1-10','342','42.00','平装','培文书系·人文科学系列','9787301109489','9.2','16174','本书分八个部分，四十四个章节，主要讲述了世界历史的进化，世界文明的发展及其对现代社会的影响。作者着眼于全球，侧重于那些有影响的、促进历史发展的历史事件，其中包括原始社会、欧亚大陆的古代文明、欧亚大陆的原始文明、欧亚以外的世界、地区分割后的世界、西方世界的崛起、西方人统治的世界、西方的衰落与成功等八个主要的部分。重点突出，主题鲜明，着重有三个主要的问题，即（1）欧洲扩张的根源；（2）欧洲扩张前的儒家文化、穆斯林和其它非欧洲的世界；（3）欧洲扩张的各个历史时期：伊比利亚时期，荷兰、法国、英国时期，沙俄时期。本书为上册。斯塔夫里阿诺斯的这部潜心力作自1970年初版问世以来，赞誉如潮．被译成多种语言流传于世，可谓经典之中的经典。第7版在保留原文精华的基础上．融入了时新的研究成果．新增了数百幅生动珍贵的图片和脉络清晰的地图，使这部名著在内容和体系上更加完善。作者文笔隽永，笔力深厚，才思横溢，整部著作风格前后一贯，令您在颇具历史韵律的行文中思接千载、视通万里。','1922216','https://book.douban.com/subject/1922216/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('139','长日将尽','[英]石黑一雄','英','上海译文出版社','2018-5','337','48','精装','石黑一雄作品','9787532777532','8.9','28433','★2017诺贝尔文学奖得主石黑一雄代表作、布克奖获奖小说★长日将尽，一曲帝国衰落的挽歌，一场擦肩而过的爱情★同名电影（又译《告别有情天》）获多项奥斯卡奖、英国电影学院奖提名，英国影帝安东尼•霍普金斯、实力女星爱玛•汤普森主演《长日将尽》是诺奖得主石黑一雄1989年获布克奖的作品，也是石黑一雄最重要的代表作。小说以管家史蒂文斯的回忆展开，讲述了自己为达林顿勋爵服务的三十余年时光里的种种经历；虽然达到了职业巅峰，但史蒂文斯过于冷酷地压抑自我情感，追求完美履行职责，而在父亲临终前错过最后一面，之后又与爱情擦肩而过。小说通过主人公的回忆，将一个人的生命旅程在读者眼前抽丝剥茧，同时也折射出一战与二战之间那段非常时期的国际政治格局。1993年根据小说翻拍的同名电影（又名《告别有情天》）由英国著名演员安东尼•霍普金斯和艾玛•汤普森主演，获得八项奥斯卡奖提名和六项英国电影学院奖提名，成为影史经典。','30142261','https://book.douban.com/subject/30142261/',' The Remains of the Day','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('140','13 67','陳浩基','中国','皇冠文化出版有限公司','2014-6-9','496','NT$350','平装','陳浩基作品','9789573330806','9.1','16786','我們以為自己走在正確的道路上，為什麼走著走著，人生卻變了樣？第一本讓我們感到驕傲的華文警察小說最高傑作！一組數字，六個片斷，構成一位警探的故事，一座城市的故事，一個時代的故事……四個月之前，沒有人想到這個城市會有這樣的改變！因為一樁糾紛，蔓延成暴動，整個城市陷入蠢蠢不安，危機一觸即發。有人怒吼著抗爭，想要改變現狀，也有人只是默默希望一個穩定的未來，而他徘徊在兩個極端之間，站在界線上。曾經，他嚮往成為一名警察，只是身處在這個動盪的時代裡，讓他不得不打消念頭。沒想到，偶然間聽到的一句話，竟把他捲進危險的漩渦，彷彿命中注定要跟警察同進退。但他更沒想到的是，他和身邊的人都想反抗自己的命運，卻從此走上天差地遠的道路……這是一部讓你想向作者脫帽致敬的小說！新生代作家陳浩基為我們展現了他無比的潛力和企圖心，六個短篇串連出一位警探傳奇的一生，充滿意外性與戲劇性的情節，緊緊扣住我們的脈息，而在虛實交錯的故事中，我們看見了關於使命、關於服從、關於公義與自由的叩問，更看見個人的抉擇、時代的移轉、城市的變遷，如何深深地在命運的畫布上烙下斑駁的印痕。','25897884','https://book.douban.com/subject/25897884/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('141','温柔的夜','三毛','中国','北京十月文艺出版社','2007-5','294','28.00元','精装','三毛集','9787530208915','9.1','21628','《温柔的夜》记录了三毛在加纳利群岛的生活，共十四篇。','2068249','https://book.douban.com/subject/2068249/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('142','苏菲的世界','[挪威]乔斯坦·贾德','挪威','作家出版社','2007-10','528','26.00元','平装','贾德名作系列','9787506341271','8.8','52963','这本书以小说的形式，通过一名哲学导师向一个叫苏菲的女孩传授哲学知识的经过，揭示了西方哲学发展的历程。由前苏格拉底时代到萨特，以及亚里士多德、笛卡儿、黑格尔等人的思想都通过作者生动的笔触跃然纸上，并配以当时的历史背景加以解释，引人入胜。评论家认为，对于那些从未读过哲学课程的人而言，此书是最为合适的入门书，而对于那些以往读过一些哲学而已忘得一干二净的人士，也可起到温故知新的作用。14岁的少女苏菲不断接到一些极不寻常的来信，世界像谜团一般在她眼前展开。在一位神秘导师的指引下，苏菲开始思索，她运用少女天生的悟性与后天知识，企图解开这些谜团。然而，事实真相远比她所想的更怪异、更离奇……','2284311','https://book.douban.com/subject/2284311/',' Sofies verden','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('143','我的天才女友','[意]埃莱娜·费兰特','意','人民文学出版社','2017-1','330','42.00元','平装','那不勒斯四部曲','9787020120130','8.7','95220','只有你身为女人才会知道这些丑陋的秘密两个女人，50年的友谊和战争过去五年，几乎所有欧美读者都在谈论她、全球畅销百万册被翻译成40种语言《金融时报》2015年度女性2016《时代》周刊“世界最有影响力的100人”希拉里·克林顿、乔纳森·弗兰岑、詹姆斯·伍德、詹姆斯·弗兰科都是她的粉丝《我的天才女友》是埃莱娜·费兰特“那不勒斯四部曲”的第一部，讲述了两个女主人公莉拉和埃莱娜的少女时代。故事一开始，已经功成名就的埃莱娜接到莉拉儿子里诺的电话，说他母亲彻底消失了。埃莱娜想起莉拉对自己命运的预言，于是她写下她们一生的故事……莉拉和埃莱娜一起成长于那不勒斯一个破败的社区，从小形影不离，彼此信赖，但又都视对方为自己隐秘的镜子，暗暗角力。莉拉聪明，漂亮。她可以毫不畏惧地和欺凌自己的男生对质，也可以去找人人惧怕的阿奇勒﹒卡拉奇要回被他夺走的玩具；埃莱娜既羡慕莉拉的学习天赋和超人的决断力，又一直暗暗模仿莉拉。家人不支持莉拉继续求学，因此她到父亲和兄长苦苦维持的修鞋店帮工，又面临几个纨绔子弟的追求。埃莱娜则怀着对朋友的关爱、嫉妒和理解，独自继续学业，却始终无法面对和莉拉竞争的失落。最终，十六岁的莉拉决定嫁给肉食店老板，但在婚宴上，她发现了丈夫的背叛。而埃莱娜也站在成人世界的入口，既为前途担忧，也因对思想前卫的尼诺产生朦胧好感而彷徨。名家评论费兰特将两个贫穷的都市女孩之间的爱、分离和重逢，铸造成她们居住的那个城市的悲剧。——《纽约时报》埃莱娜·格雷科是一个幸存者，她必须从那些依附和挣脱的戏剧冲突中，让自己幸存下来。她带着一种幸存者的羞愧，就好像她是从莉拉的宝库盗取了对财富的允诺。——《纽约客》和索福克勒斯或者奥维德一样，费兰特处理的也是命运的问题。莉拉是一个造物主，一个神话般的人物。——《大西洋月刊》焦虑、疲惫、女性经期……费兰特探索了所有我们恐惧的题材。费兰特笔下的女人们切肤地体验了被抛弃、不公和汹涌的情感——这些不再是被当作弱点、而被作为一种事实来看待。——《新共和》和卡夫卡一样，费兰特将她的主人公的内心世界揭露得一览无余。——《卫报》《我的天才女友》是一部单声道（而非双声道）的成长小说；我们从很早就意识到，莉拉还困在自己的世界里，而作者，埃莱娜则会挣脱那个世界。——《泰晤士文学增刊》','26878124','https://book.douban.com/subject/26878124/'," L'amica geniale",'');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('144','德米安','[德]赫尔曼·黑塞','德','上海人民出版社','2009-3','193','25.00元','平装','赫尔曼·黑塞作品系列','9787208081550','8.9','30286','《德米安：埃米尔·辛克莱的彷徨少年时》是黑塞的代表作之一，讲述少年辛克莱寻找通向自身之路的艰辛历程。出生并成长于“光明世界”的辛克莱，偶然发现截然不同的“另一个世界”，那里的纷乱和黑暗，使他焦虑困惑，并陷入谎言带来的灾难之中。这时，一个名叫德米安的少年出现，将他带出沼泽地，从此他开始走向孤独寻找自我的前路。之后的若干年，“德米安”以不同的身份面目出现，在他每一次孤独寻找、艰难抉择的时候，成为他的引路人……','3578470','https://book.douban.com/subject/3578470/',' Demian','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('145','爱的艺术','[美国]艾·弗洛姆','美国','上海译文出版社','2008-4','144','15.00元','平装','译文心理','9787532745159','8.8','42519','《爱的艺术》是德裔美籍心理学家和哲学家、法兰克福学派重要成员艾里希-弗洛姆最著名的作品，自1956年出版至今已被翻译成32种文字，在全世界畅销不衰，被誉为当代爱的艺术理论专著最著名的作品。在这本书中，弗洛姆认为，爱情不是一种与人的成熟程度无关，只需要投入身心的感情。如果不努力发展自己的全部人格并以此达到一种创造倾向性，那么每种爱的试图都会失败，如果没有爱他人的能力，如果不能真正谦恭地、勇敢地、真诚地和有纪律地爱他人，那么人们在自己的爱情生活中也永远得不到满足。弗洛姆进而提出，爱是一门艺术，要求想要掌握这门艺术的人有这方面的知识并付出努力。在这里，爱不仅仅是狭隘的男女爱情，也并非通过磨练增进技巧即可获得。爱是人格整体的展现，要发展爱的能力，就需要努力发展自己的人格，并朝着有益的目标迈进。此版特别收录弗洛姆学术助手纪念文章《弗洛姆生命中的爱》。','3026879','https://book.douban.com/subject/3026879/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('146','少年凯歌','陈凯歌','中国','人民文学出版社','2001-6','189','10.00元','平装','','9787020034109','9.3','11596','在成为著名电影导演之前，他曾有过怎样的人生？陈凯歌亲自执笔，直面他的少年时代。他敢于面对，勇于审视自己，并承认曾经犯过的错误。他的回忆与思考，既富于自省和批判精神，又具有相当的思想深度和历史的鲜活性。深刻的思想、优美的文笔、个人的视角，对成名之前生活、思想、艺术积累的小结，能够启迪我们许许多多的人。','1091203','https://book.douban.com/subject/1091203/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('147','霸王别姬','李碧华','中国','人民文学出版社','1999-1','272','10.20元','平装','李碧华文集人民文学版','9787020028504','9.1','20174','婊子无情，戏子无义。婊子合该在床上有情，戏子只能在台上有义。台上，一生一旦，英雄美人，一出《霸王别姬》唱红四十年代的北平。台下，师兄师弟，性情各异。一具风流倜傥，与红妓两情相悦；一个人戏不分，泥足深陷。由此，三人在爱与恨的漩涡中角逐纠缠......风雨人生沾有缕缕血腥，前尘往事留下几许绮丽和幽怨？本书已由大陆及港、台三方联合拍成电影，获第四十六届戛纳电影节金棕榈奖。','1088711','https://book.douban.com/subject/1088711/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('148','亮剑','都梁','中国','解放军文艺出版社','2005-3','570','28.00元','平装','','9787503311864','9','27724','李云龙是一个叱咤风云、百战沙场的职业军人，是一个一生都在血与火中搏斗的名将。他的人生信条是：面对强大的对手，明知不敌，也要毅然亮剑，即使倒下，也要成为一座山、一道岭。在战争与和平的时空转换中，他的命运注定要充满悲欢离合—无论是政治生涯还是婚姻、爱情。','1088022','https://book.douban.com/subject/1088022/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('149','不能承受的生命之轻','米兰·昆德拉','中国','上海译文出版社','2010-8','394','29.00元','平装','米兰·昆德拉作品系列（2010版）','9787532751655','8.8','42452','小说依托六十年代的捷克斯洛伐克的政治历史背景，围绕着托马斯、特蕾莎、萨比娜和弗兰茨等人物，以托马斯与特蕾莎的爱情为主线展开故事。爱与做爱，灵魂与肉体，拯救与放逐，忠诚与背叛，昆德拉把一个个的矛盾摆在面前，“到底选择什么？是重还是轻？”“重便真的残酷，而轻便真的美丽？”重与轻不再是物理上的简单对立，而是如昆德拉所说：“重与轻的对立是所有对立中最神秘、最模糊的。','4920007','https://book.douban.com/subject/4920007/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('150','古文观止','吴楚材/吴调侯','中国','中华书局','1987-1-1','527','21.00元','平装','','9787101000290','9.1','17715','《古文观止》是清康熙年间吴乘权、吴大职编选的一部古文读本，凡十二卷，收录自先秦至明末的散文二百二十二篇，每篇都有注释和评论。据《左传》襄公二十九年记载，吴公子季札在鲁国观看乐舞《韶�》时，以为尽善尽美，无以复加，赞叹道：“观止矣：若有他乐吾不敢请已！”本书书名中“观止”二字即由此而来，是表示所选的古文极好，堪称最佳读本。','1014561','https://book.douban.com/subject/1014561/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('151','我不知道该说什么，关于死亡还是爱情','[白俄]S·A·阿列克谢耶维奇','白俄','花城出版社','2014-6-15','280','34.80元','平装','','9787536071377','9.1','18521','★获得2015年诺贝尔文学奖，真实记录切尔诺贝利核灾难事件★“她的复调书写，是对我们时代的苦难和勇气的纪念。”★“每一页，都是感人肺腑的故事。”-切尔诺贝利核灾难幸存者口述实录，每一页都是感人肺腑的故事作者冒着核辐射危险，深入切尔诺贝利，采访当地幸存者人类史上最惨烈的科技悲剧，今天的我们该如何避免灾难重演？-1986年4月26日，史上最惨烈的反应炉事故发生在切尔诺贝利。这是史上最浩大的悲剧之一。作者访问了上百位受到切尔诺贝利核灾影响的人民，有无辜的居民、消防员、以及那些被征招去清理灾难现场的人员。他们的故事透露出他们至今仍生活在恐惧、愤怒和不安当中。本书将这些访谈以独白的方式呈现，巨细靡遗的写实描绘，使这场悲剧读起来像世界末日的童话。人们坦白地述说着痛苦，细腻的独白让人身历其境却又难以承受。-★《纽约时报》——每一页都是奇异而残忍的故事，就像那些残留在幸存者身上的辐射。★《伦敦时报》——如同古希腊悲剧表演的合唱团，本书收录了众人的心声。短期之内，我们恐怕不会再有机会读到这般真实记录愤怒、愚昧、英勇和伤亡的文字。★《每日电讯报》——从受访者的独白中，阿列克谢耶维奇创造了这样一种历史：无论离这些事件有多远，读者都能感同身受，如有切肤之痛。读完这本书，我才知道切尔诺贝利是欧洲的一场海啸，这场海啸不仅是由我们人类所造成的，而且还永无止尽。如果你对未来抱有好奇心，我强烈推荐这本书。书中的切尔诺贝利是个充满极端与未知的地方，一个现代科技发展造就的戏剧世界。★《出版人周刊》——口语叙述的历史在脑海中挥之不去，充斥着面对命运时的无奈、艰苦卓绝的勇气，以及浓厚的黑色幽默……如同不可磨灭的X光透视着俄国人的灵魂。★《国家》杂志——阿列克谢耶维奇的这本杰出著作，忠实地记录着她那些白俄罗斯同胞的生命与死亡。本书终于将在美国上架……这是无与伦比的见证。★《书目杂志》——书中记录着受污染的世界里骇人的生活。这些典型的故事分别传达出不同的声音：愤怒、恐惧、无知、艰苦、英勇、同情和爱。阿列克谢耶维奇冒着损害健康的风险，深入前线收集这些见证，把故事转化成令人难忘的精辟著作，我们只能期盼书中的灾难不会重现。','25884880','https://book.douban.com/subject/25884880/',' Voices from Chernobyl','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('152','遥远的向日葵地','李娟','中国','花城出版社','2017-11','258','38.00元','软精装','李娟作品系列（花城版）','9787536084469','9.1','20384','此书为李娟近两年开始写作并发表在《文汇报》笔会的专栏——“遥远的向日葵地”的最新文字结集。“向日葵地”在阿勒泰戈壁草原的乌伦古河南岸，是李娟母亲多年前承包耕种的一片贫瘠土地。李娟一如既往用她细腻、明亮的笔调，记录了劳作在这里的人和他们朴素而迥异的生活细节：她勤劳乐观的母亲、高龄多病的外婆，大狗丑丑小狗赛虎，鸡鸭鹅，以及日渐华盛，却被鹅喉羚毁了再种，种了又毁的九十亩葵花地……刻画的不只是母亲和边地人民的坚韧辛劳，更是他们内心的期翼与执着，也表达了对环境的担忧和对生存的疑虑。呈现出一种完全暴露在大自然中脆弱微渺的，同时又富于乐趣和尊严的生存体验。','27184303','https://book.douban.com/subject/27184303/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('153','我也有一个梦想','林达/李晓林','中国','生活·读书·新知三联书店','2004-08','428','25.00元','平装','林达作品系列','9787108012715','9','15188','本书通过精彩动人的故事，展示了美国种族问题相关联的，社会意识和法律演进史。介绍了在契约社会里，立法的民众基础，法律对人性的思考，法律的变化与社会理趟的关系等问题。通过作者的叙述，读者能够看到，在法治国家里，民众，尤其是弱势人群怎样运用法律，经过长期抗争，取得自身权益，并由此推动社会认识的深化，使整个国家在消除不公正的历史进程中取得稳定的进步。','1014825','https://book.douban.com/subject/1014825/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('154','球状闪电','刘慈欣','中国','四川科学技术出版社','2005-6','281','22.00元','平装','科幻世界·中国科幻基石丛书','9787536455382','8.7','76193','在某个离奇的雨夜，一颗球状闪电闯进了少年的视野。它的啸叫低沉中透着尖利，像是一个鬼魂在太古的荒原上吹着埙。当鬼魂奏完乐曲，球状闪电在一瞬间将少年的父母化为灰烬，而他们身下板凳却是奇迹般的冰凉。这一夜，少年的命运被彻底改变了，他将毕其一生去解开那个将他变成孤儿的自然之谜。但是他未曾想到，多年以后，单纯的自然科学研究被纳入进“新概念武器”开发计划，他所追寻的球状闪电变成了下一场战争中决定祖国生存或是灭亡的终极武器！当被禁锢在终极武器中的大自然的伟力被释放时，一轮冰冷的“蓝太阳”升起在大西部的戈壁滩上，整个戈壁淹没在它的蓝光中，这个世界变得陌生而怪异。一个从未有人想像过的未来，在宇宙观测者的注视下，降临在人类面前……','1192090','https://book.douban.com/subject/1192090/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('155','强风吹拂','三浦紫苑','中国','广西师范大学出版社','2015-1-1','434','39.00元','平装','','9787549559671','9','21340','豆瓣2015年度读书榜单，最佳暖心治愈作品No.1.跑步界的《灌篮高手》,“纵有疾风起，人生不言弃。”B站正版引进热血新番《强风吹拂》同名原著小说。.长跑的目标不是更快，而是更强。“明明这么痛苦，这么难过，为什么就是不能放弃跑步？因为全身细胞都在蠢蠢欲动，想要感受强风迎面吹拂的滋味。”.宽政大学宿舍“竹青庄”的十名舍友凑成杂牌长跑队，在队长清濑灰二的魔鬼训练下，从零开始向日本历史最悠久的长跑接力赛“箱根驿传”挺进。这十名大学生包括两名田径队逃兵、一对神经大条又聒噪的双胞胎、俊帅漫画宅男、尼古丁中毒的万年留级生、逻辑超强的毒舌精英、不爱跑步的黑人留学生、老实好青年、百发百中猜谜王。这些“选手”刚开始时连自己是田径队都不知道，且一半成员没有长跑经验。他们能否创造“箱根驿传”史上最大奇迹？不到最后一棒，没人知道答案。.“箱根驿传”二三事.1、日本历史最悠久的长跑接力比赛，始于1920年。每年1月2日、3日举办，由关东学生田径联盟主办，读卖新闻协办。是日本新年最重要的、全民关注的体育赛事。2、路线从读卖新闻东京本社前出发，至箱根芦之湖后折返，去程有5个区间，回程同样有5个区间，合计10个区间总长约217公里。3、参赛门槛为曾经取得17分钟内完成5公里的记录赛正式记录。（后调整为16分钟30秒内完成5公里或34分钟内完成10公里）4、参赛队伍共20所大学代表队，各10名选手。每支队伍的每位参赛选手至少要跑20公里。（第4区例外，为18.5公里）.编辑推荐△直木赏得主、《编舟记》《多田便利屋》作者三浦紫苑，历时六年创作之运动励志文学代表作——明明这么痛苦，为什么就是不能放弃跑步？长跑的目标不是更快，而是更强。跑步真谛，亦是人生心诀。△以日本全民关注的体育盛事“箱根驿传”为背景，《强风吹拂》的世界铸就一起奔跑的热血青年——2012年由村上春树首次执笔文案的长跑广告片在“箱根驿传”期间投放，鼓舞大地震后民众士气，而片中于痛苦中超拔的“驿传精神”，也通过互联网在全亚洲传播：疼痛无可避免，苦痛却可以自己选择。△以20公里时速接力完成217公里长跑，十名大学生挑战“不可能任务”的青春群像——跑步是孤单的运动，跑步者却可以不孤单。《强风吹拂》中一支勉强取得参赛资格的弱小田径队，在队长清濑灰二率领之下与传统强校比肩较量，队员们共同经历魔鬼训练与严酷现实，终于创造“箱根驿传”史上最大奇迹。强风吹拂，只有跑者了解的状态，“纵有疾风起，人生不言弃。”','26210487','https://book.douban.com/subject/26210487/',' 風が強く吹いている','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('156','一句顶一万句','刘震云','中国','长江文艺出版社','2009-3','362','29.80','精装','刘震云作品','9787535439765','8.8','60420','《一句顶一万句》的故事很简单，小说的前半部写的是过去：孤独无助的吴摩西失去唯一能够“说得上话”的养女，为了寻找，走出延津；小说的后半部写的是现在：吴摩西养女的儿子牛爱国，同样为了摆脱孤独寻找“说得上话”的朋友，走向延津。一走一来，延宕百年。书中的人物大部分是中国最底层的老百姓，偏偏安排了一个意大利牧师老詹。','3633461','https://book.douban.com/subject/3633461/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('157','激荡三十年（上）','吴晓波','中国','中信出版社浙江人民出版社','2007-1','297','35.00','平装','中国企业史三部曲','9787508607719','8.8','30146','上卷记载1978-1992年间的企业变革。《激荡三十年》的作者没有用传统的教科书或历史书的方式来写作这部作品，而是站在民间的角度，以真切而激扬的写作手法描绘了中国企业在改革开放年代走向市场、走向世界的成长、发展之路。改革开放初期汹涌的商品大潮；国营企业、民营企业、外资企业，这三种力量此消彼长、互相博弈的曲折发展；整个社会的躁动和不安……整部书稿中都体现得极为真切和实在。作者用激扬的文字再现出人们在历史创造中的激情、喜悦、呐喊、苦恼和悲愤。尽管任何一段历史都有它不可替代的独特性，可是，1978年—2008年的中国，却是最不可能重复的。在一个拥有13亿人口的大国里，僵化的计划经济体制日渐瓦解了，一群小人物把中国变成了一个巨大的试验场，它在众目睽睽之下，以不可逆转的姿态向商业社会转轨。','1970428','https://book.douban.com/subject/1970428/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('158','鹿川有许多粪','李沧东','中国','武汉大学出版社','2021-8','314','58.00元','平装','鹿书：李沧东作品','9787307223950','9','20470','豆瓣2021年度读书榜单“年度外国文学（小说类）”“年度高分图书”“豆瓣书店最受欢迎图书”李沧东封笔之作直面错综复杂的生活本身，探索真正价值的可能性从《烧纸》到《燃烧》，永恒的审视与追问本书是韩国导演李沧东于1992年出版的中短篇小说集，借此他获得了《韩国日报》创作文学奖，并在第二年受导演朴光洙之邀进入电影界，随后他便转型并逐渐成为具有国际影响力的电影大师。这部小说集展现了一批裹挟在复杂多变的历史浪潮中的底层人物形象，他们艰辛地在生活中追求真正的价值，与现实中的痛苦进行抗争，同时寻找个人生活的意义。作者通过这些人物的遭遇审视韩国现实，但并非止步于讲述历史事件或故事本身，而是着重刻画了人物在此过程中发生的转变，他们逐渐开始对生活中的真正价值和自己的身份认同提出疑问并进行探索。','35517022','https://book.douban.com/subject/35517022/',' 녹천에는 똥이 많다','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('159','我的阿勒泰','李娟','中国','云南人民出版社','2010-7','260','22.00元','平装','','9787222065413','8.9','24693','本书是作者十年来散文创作的合集。分为阿勒泰文字、阿勒泰角落和九篇雪三辑。这是一部描写疆北阿勒泰地区生活和风情的原生态散文集。充满生机活泼、新鲜动人的元素。记录作者在疆北阿勒泰地区生活的点滴，包括人与事的记忆。作者在十年前以天才的触觉和笔调初现文坛并引起震惊。作品风格清新、明快，质地纯粹，原生态地再现了疆北风物，带着非常活泼的生机。','4884218','https://book.douban.com/subject/4884218/',' My Altay','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('160','海风中失落的血色馈赠','[加拿大]阿利斯泰尔·麦克劳德','加拿大','上海文艺出版社','2015-6-1','163','CNY20.00','平装','短经典','9787532156825','9.1','17577','《海风中失落的血色馈赠》所收入的七个故事，有少年渴望摆脱家族在海岛世代挖煤的命运而在成年之际离家远行，有人到中年的大学教师回忆少年时他那心怀壮志但困居海岛打鱼为生的父亲，有散居各地的大家族在老祖母96岁生日之际齐聚老祖母寡居的海角……这些故事勾画了男女之间、父母与子女之间紧密的纽带和难以逾越的鸿沟，既温柔又残酷。这些故事都发生在新斯科舍省布雷顿角那些严酷的风景中，写的都是复杂、神秘的人心。它们被记忆和传说浸润，被海水和鲜血冲刷，而这些海水和鲜血，都曾流淌于同土地和海洋漫长的搏斗中；麦克劳德颂扬的是一种和自然世界的深情交融，以及面对变迁、面对爱与失去，多少世代之间某些一脉相承的东西。很难想出还有谁能写出具有阿利斯泰尔•麦克劳德般魅力的小说。——爱丽丝•门罗和福克纳或契诃夫的作品一样，阿利斯泰尔•麦克劳德的短篇小说既是地域的，又是普世的，而且，我也认为它们是不朽的。——迈克尔•翁达杰阿利斯泰尔•麦克劳德的《海风中失落的血色馈赠》自1976年出版以来，已经成了加拿大的经典。这些怀着深厚情感考究雕琢出来的短篇，背景都设在布雷顿角……但读者不会感觉他在描绘异域奇事，或在创造狭隘的地域文学，反而，他们会想到一些与之亲近的世界。……阿利斯泰尔•麦克劳德的布雷顿角无处不在。任何人只消跨一步便能进入。——乔伊斯•卡罗尔•欧茨因为这些故事的音调、节奏控制得极好，也因为麦克劳德擅于处理纯正、真实的情感，它们渐渐越来越出名……当代潮流和现代反讽，他都不感兴趣。他的故事天才之处就在于，他使笔下的虚构世界没有时代局限。——科尔姆•托宾','26357614','https://book.douban.com/subject/26357614/',' The Lost Salt Gift of Blood','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('161','草房子','曹文轩','中国','江苏少年儿童出版社','2009-6','296','18.00元','平装','曹文轩纯美小说系列','9787534618727','9','44023','这是一部讲究品位的少年长篇小说。作品写了男孩桑桑刻骨铭心、终身难忘的六年小学生活。六年中，他亲眼目睹或直接参与了一连串看似寻常但又催人泪下、撼动人心的故事：少男少女之间毫无暇疵的纯情，不幸少年与厄运相拼时的悲怆与优雅，残疾男孩对尊严的执著坚守，垂暮老人在最后一瞬所闪耀的人格光彩，在死亡体验中对生命的深切而优美的领悟，大人们之间扑朔迷离且又充满诗情画意的情感纠葛……这一切，既清楚又朦胧地展现在少年桑桑的世界里。这六年，是他接受人生启蒙教育的六年。作品格调高雅，由始至终充满美感。叙述风格谐趣而又庄重，整体结构独特而又新颖，情节设计曲折而又智慧。荡漾于全部作品的悲悯情怀，在人与人之间的关系日趋疏远、情感日越淡漠的当今世界中，也显得弥足珍贵、格外感人。通篇叙述既明白晓畅，又有一定的深度，是那种既是孩子喜爱也可供成人阅读的儿童文学作品。','1052990','https://book.douban.com/subject/1052990/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('162','社会心理学','[美]戴维·迈尔斯','美','人民邮电出版社','2006-1','504','68.00元','平装','新曲线·心理学丛书','9787115138804','9','24835','《社会心理学》这本书被美国700多所大学或学院的心理系所采用，是这一领域的主导教材，已经成为评价其他教材的标准。这本书将基础研究与实践应用完美地结合在一起，以富有逻辑性的组织结构引领学生了解人们是如何思索、影响他人并与他人建立联系的。是人们了解自身、了解社会、了解自己与社会之间关系的最佳的指导性书籍。','1476651','https://book.douban.com/subject/1476651/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('163','倾城之恋','张爱玲','中国','北京十月文艺出版社','2006-12','467','29.80元','精装','张爱玲集','9787530208656','8.7','48508','一九四四年八月十五日，张爱玲的小说集《传奇》由上海杂志社出版，书中收入中短篇小说《金锁记》、《倾城之恋》、《茉莉香片》、《沉香屑：第一炉香》、《沉香屑：第二炉香》、《琉璃瓦》、《心经》、《年青的时候》、《花凋》和《封锁》。《传奇》的出版，是中国现代文学史上的一件大事，也奠定了张爱玲在中国现代文学进程中的历史地位。“五四”开创的新文学运动衍变到此，才出现了一种新的发展可能。这本《倾城之恋》收入《传奇》增订本的全部，同时在附录部分收入《传奇》各版的作者自序和关于《倾城之恋》的两篇创作体会。《倾城之恋》是张爱玲的成名作与代表作。白流苏和范柳原这一对现实庸俗的男女，在战争的兵荒马乱之中被命运掷骰子般地掷到了一起，于“一刹那”体会到了“一对平凡的夫妻”之间的“一点真心”。','1985223','https://book.douban.com/subject/1985223/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('164','天朝的崩溃','茅海建','中国','生活·读书·新知三联书店','2005-7','600','32.00元','平装','','9787108022943','9.2','13768','《天朝的崩溃》是作者系统研究鸦片战争十余年所得的成果。作者详尽考订了与战争相关的一系列重要史实，力图以当时的道德观念、思维方式与行为规范去理解历史，使许多在今人看来是荒谬的现象得以显示其在当时环境中的“合理性”。同时，通过对有关人物及其言行活动分析，观察清王朝在历史转折关头的作为，揭示历史进程中的偶然与必然。鸦片战争在中国近代史上影响重大，有关著述已出版不少。这本书的研究可谓推陈出新，作者一系列独到创见可能在学术界引起争议，相信这种争议将有助于深化中国近代史研究。','1675478','https://book.douban.com/subject/1675478/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('165','麦琪的礼物','[美]欧·亨利','美','上海社会科学院出版社','2003-7','354','25.00元','平装','','9787806812297','8.7','71597','选收欧·亨利的小说34篇，国外当代文学类重要工具书介绍的有代表性的作品均已收入。欧·亨利是位有独特风格的杰出短篇小说家，以巧妙的构思、夸张和幽默的文笔反映了他那个时代的人和事。','1051193','https://book.douban.com/subject/1051193/','','张经浩');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('166','安娜·卡列尼娜','[俄]列夫·托尔斯泰','俄','上海文艺出版社','2007-8','788','37.00元','平装','世界十大文学名著','9787532132256','9.2','23109','《安娜·卡列尼娜》是托尔斯泰第二部里程碑式的长篇小说，创作于1873—1877年。作品由两条既平行又相互联系的线索构成：一条是安娜与卡列宁、伏伦斯基之间的家庭、婚姻和爱情纠葛；一条是列文和吉娣的爱情生活及列文进行的庄园改革。安娜是一个上流社会的贵妇人，年轻漂亮，追求个性解放和爱情自由，而她的丈夫却是一个性情冷漠的“官僚机器”。一次在车站上，安娜和年轻军官伏伦斯基邂逅，后者为她的美貌所吸引，拼命追求。最终安娜堕入情网，毅然抛夫别子和伏伦斯基同居。但对儿子的思念和周围环境的压力使她陷入痛苦和不安中，而且她逐渐发现伏伦斯基并非一个专情的理想人物。在相继失去儿子和精神上最后一根支柱——伏伦斯基后，经过一次和伏伦斯基的口角，安娜发现自己再也无法在这个虚伪的社会中生活下去，绝望之余，她选择了卧轨自杀。小说揭露了19世纪六七十年代俄罗斯上流社会的丑恶与虚伪，同时也表达了作者处在社会转型期时所进行的复杂的道德探索和思想探索。','2253380','https://book.douban.com/subject/2253380/',' Anna karenina','草婴');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('167','浮生六记','（清）沈复','中国','人民文学出版社','1999-1','80','38','平装','中国小说史料丛书','9787020028313','8.7','65322','这是一部自传体文学的作品，原书六卷，已逸其二，现仅存四卷（有所谓“足本”者，后二记系伪作。书中记叙了作者夫妇间平凡的家居生活，坎坷际遇，和各地浪游闻见。文辞朴素，情感真挚，前人曾有“幽芳凄三角，读之心醉”的评语。本书文字不长，但向为文学爱好者和研究者所重视，影响广泛。','1088581','https://book.douban.com/subject/1088581/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('168','九州·缥缈录','江南','中国','新世界出版社','2005-6','249','20.00元','平装','九州书系','9787801876942','8.8','30465','当这个世界都要崩溃；当星辰和阳光也熄灭，当马蹄踏过弱者的尸骨，当黑暗的血色吞噬人心，不死的鹰再次降落在草原，英雄还在哭泣，在铁铸的摇篮中成长……《九州·缥缈录》的故事就发生在这里，游牧部落内部的权力争夺激烈，青阳与东陆王朝间恩怨重重。这是乱世的英雄史诗，当古老的王朝日渐衰微，掌管着星辰命运的神秘宗教走入了政治斗争的漩涡，年轻的王朝继承者崭露出耀眼的锋芒。','1321017','https://book.douban.com/subject/1321017/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('169','孩子你慢慢来','龙应台','中国','生活·读书·新知三联书店','2009-12-1','138','28.00元','平装','龙应台作品精选','9787108033635','8.7','76015','作为华人世界最有影响的一支笔，龙应台的文章有万丈豪气，然而《孩子你慢慢来》却令人惊叹，她的文字也可以有款款深情。这本书里的龙应台是一个母亲，作为母亲的龙应台和作为一个独立的人的龙应台有着丰富、激烈的内心冲突，而正是通过对这一冲突的诉说，表现出她内心深处的母爱。但它不是传统母爱的歌颂，是对生命的实景写生，只有真正懂得爱的作家才写得出这样的生活散文。十五年前龙应台以一位母亲的亲身经验写下《孩子你慢慢来》，她在书中说：“谁能告诉我做女性和做个人之间怎么平衡？我爱极了做母亲，只要把孩子的头放在我胸口，就能使我觉得幸福。可是我也是个需要极大的内在空间的个人……女性主义者，如果你不曾体验过生养的喜悦和痛苦，你究竟能告诉我些什么呢？”十五年过去，龙应台不仅成为华文界最有影响力的一枝笔，也以她自己的智慧走出女性在个人事业和母亲角色的冲突，而这本书也给无数读者带来感动和启迪。在《孩子你慢慢来》出版十五周年之际，三联书店推出全新简体版：大开本彩色印刷，精美呈现数十幅龙应台私家珍藏照片；特别收录龙应台的孩子：少年华飞、青年华安在该书出版十周年时所撰的跋——看华文世界最受瞩目的女性，原来她是这样做妈妈的。','4207781','https://book.douban.com/subject/4207781/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('170','万物有灵且美','[英]吉米·哈利','英','中国城市出版社','2010-1','298','28.80','平装','吉米·哈利“万物”系列','9787507421774','8.9','21723','“活泼的生命完全无须借助魔法，便能对我们述说至美至真的故事。大自然的真实面貌，比起诗人所能描摹的境界，更要美上千百倍。”把追车当做一门艺术的狗，策划群猫暴动的精灵古怪的老猫……动物们的温馨感人喜剧在轮番上演着。大自然怀抱中的乡野风情，多姿多彩的人和动物，构成了一幅芸芸众生的绚烂画卷。年轻的乡村兽医哈利，每天开着一辆冒黑烟的老爷车“南征北战”。和恶犬贴身肉搏，随时准备应对母马的“无影脚”……，各种让人哭笑不得的“惨痛”遭遇层出不穷。在这里他遇上有生以来最困窘的时刻，也享受到最温暖动人的真情。作者以轻松幽默的笔触，记录乡间行医的点点滴滴，满溢着兽医生活的笑与泪，朴实的人情和土地的智慧。动物行为学的开山祖师，1973年诺贝尔生理医学奖得主劳伦兹说过“为了能够确切描写动物的故事，一个人必须对所有的生命，都怀有一份发自内心的真感情。”发自内心的真感情，也许这正是这本书能够打动亿万读者的真正原因吧。哈利是个热爱生活的人，他尽情地享受着大自然的恩赐，并赞颂着生命的奇迹。他善于从生活中的点滴小事中发掘美好，更重要的是他能够把他的热爱以近乎完美的方式，传递给读者。让我们能够同样感受到生命、爱与欢笑。','4187411','https://book.douban.com/subject/4187411/',' All Things Bright and Beautiful','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('171','情书','[日本]岩井俊二','日本','天津人民出版社','2004-7','256','18.00元','平装','新经典文库·岩井俊二作品','9787201048161','8.6','102305','日本神户，渡边博子在未婚夫藤井树的三周年祭日上又一次陷入到悲痛和思念之中。博子在藤井树的中学同学录里找到了他在小樽市读书时的地址。由于抑制不住对爱人的怀念，博子按着这个地址给远在天国的藤井树寄去了一封充满问候和思念的书信。不可思议的是，不久博子竟然收到了署名为“藤井树”的回信。经过进一步了解，这个藤井树是一个年轻的女子，而且她还曾经是男性藤井树的同班同学，原来是博子从同学录中误抄了她的地址。为了多了解一些男友在中学时代的情况，博子继续与女性藤井树保持书信来往。而藤井树在不断的回忆中，竟逐渐发现中学时代那个和自己同名同姓的少男曾经对自己产生过一段真挚的感情……《情书》由一个同名同姓的误会开始，通过两个女子书信的交流，以含情脉脉的笔触舒缓地展现了两段可贵的爱情。女主角博子对藤井树的眷恋，两个藤井树之间朦胧的情感，都没有由于藤井树的意外死亡而枯萎，而通过细腻感人的影象深深地印在每一个观众的心里，永远不变。在精心描绘爱情的同时，岩井俊二还着意表现了对逝去岁月的怀念和追忆。《情书》正象普鲁斯特那本小说的名字，追忆着似水的年华。过往的爱情和青春也正是在主人公的回忆中才逐渐清晰、复活。与现实相比，影片中的过去更为明快优美。在那一幅幅唯美的画面中，漫天飞舞的片片樱花，暗生情愫的少男少女，都唤起我们的无限遐想。而《情书》中所构筑的那个美好的中学时代，可能也正是岩井俊二和很多人最为温馨纯洁的回忆。《情书》中对过去的追忆和有关生死的描绘都极具东方气质，含蓄优美、感而不伤地表达了故事的主题——珍惜有限的生命和宝贵的爱情。','1080370','https://book.douban.com/subject/1080370/',' ラヴレター','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('172','恶意','[日]东野圭吾','日','南海出版公司','2016-11','268','39.50元','精装','新经典文库·东野圭吾作品','9787544285148','8.7','92825','《恶意》是东野圭吾挑战写作极限的长篇杰作，与《白夜行》《嫌疑人X的献身》《解忧杂货店》并称东野圭吾四大杰作。《恶意》深刻揭示人性，故事中无边的恶意深不见底，有如万丈深渊，让人不寒而栗。从未遇到这样的案子：杀人不是目的，而是手段；死亡不是结束，而是开始。读完《恶意》，才算真正认识东野圭吾。============================================================畅销作家在出国前一晚被杀，警方很快锁定了凶手。此人供认自己是一时冲动犯下了罪行。案子到此已经可以了结。可办案的加贺警官并不这么认为，因为他找不到凶手作案的动机，凶手也一直对动机避而不谈。加贺不愿草草结案，大量走访。渐渐显露的真相让他感到冰冷的寒意——“你心里藏着对他的恶意，这仇恨深不见底，深得连你自己都无法解释。正是它导致了这起案件。这股恶意到底从何而起呢？我非常仔细地调查过，却实在找不出任何合乎逻辑的理由。这是怎样的一种心态啊！就算被捕也不怕，即使赌上自己的人生，也要达成目的。这真是惊人的想法，简直前所未闻。”============================================================东野圭吾在《恶意》中巧妙地设置了一个又一个反转，从阴谋实施到真相大白间的精巧设计，令读者赞叹不已。——《出版家周刊》（美国）在《恶意》中，东野圭吾将陀思妥耶夫斯基式的心理现实主义和阿加莎·克里斯蒂式的经典侦探解谜故事结合得炉火纯青。——《华尔街日报》（美国）当你以为东野圭吾已经穷尽了诡计的所有可能性时，他就祭出一个新的来让你大吃一惊。——《科克斯书评》（美国）《恶意》作为一部推理小说，极尽曲折复杂周密；同时又深刻揭示了人性，达到很多纯文学作品未能达到的深度。《恶意》可与同一作者的《红手指》对照着读。在《恶意》中，恶是没有底线的；在《红手指》中，善残存于恶的底线之下。——止庵潜伏在《恶意》故事底下的，是一条黑色的河流，名为人的“恶意”。虽然可以听到流淌的微弱水声，却无法轻易看到它的流动。——桐野夏生','26877752','https://book.douban.com/subject/26877752/',' 悪意','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('173','送你一颗子弹','刘瑜','中国','上海三联书店','2010-1','339','25.00元','平装','刘瑜作品系列','9787542631664','8.6','139152','这本书里记录的是作者2005—2009年左右(尤其是2006—2007年)生活里的点点滴滴。在这本书里，被“审视”的东西杂七杂八，有街上的疯老头，有同宿舍的室友，有爱情、电影和书，大到制度，小到老鼠。由于我写这些东西的时候，出发点并不是写一本书，所以不同文章往往风格迥异，长短不一，质量不均，随着社会形势、荷尔蒙周期以及我逃避生活的力度而起伏。','4238362','https://book.douban.com/subject/4238362/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('174','渴望生活','[美]欧文·斯通','美','北京十月文艺出版社','2008-4','525','29.80元','平装','','9787530209127','9.2','11130','《渴望生活——梵高传》是欧文·斯通仅二十六岁时的作品。欧文·斯通认为，最能打动读者的不是名人深厚的成就和辉煌，而是他们追求和探索的过程。七十余年来，梵高悲惨而成就辉煌的人生震撼无数读者。这部作品也成为欧文·斯通的成名作，被译成八十余种文字，发行数千万册，感动亿万读者。','3054821','https://book.douban.com/subject/3054821/',' Lust for Life','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('175','尼罗河上的惨案','[英]阿加莎·克里斯蒂','英','人民文学出版社','2006-5','345','22.00元','平装','阿加莎·克里斯蒂侦探推理系列','9787020056088','8.7','45984','林内特·里奇维被一颗子弹打穿了头颅，尼罗河之旅的宁静也因此被打破。这位年轻，美丽，时尚——拥有一切的女孩，最终失去了自己的生命。赫尔克里·波洛的耳边回响着早先一位旅伴的话语：“我真想用我那把亲爱的小手枪顶着她的头，扣动扳机。”然而在这异国他乡的场景中，一切并不像表面显示的那样平静……','1818347','https://book.douban.com/subject/1818347/',' Death on the Nile','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('176','银河系漫游指南','[英]道格拉斯·亚当斯','英','四川科学技术出版社','2005-6','191','16.00元','平装','世界科幻大师丛书','9787536457577','8.8','20962','地球被毁灭了，因为要在它所在的地方修建一条超空间快速通道。主人公阿瑟·邓特活下来了，因为他有一位名叫福特·长官的朋友。这位朋友表面上是个找不着工作的演员，其实是个外星人，是名著《银河系漫游指南》派赴地球的研究员。两人开始了一场穿越银河的冒险，能够帮助他们的只有《银河系漫游指南》一书中所包括的无限智慧。旅途中，他们遇上了一批非常有趣的同伴：赞福德·毕博布鲁克斯：长着两个头、三条胳膊的银河大盗，他的另一个身份是银河帝国总统。崔莉恩：赞福德的同伙，除阿瑟·邓特之外惟一一个幸存下来的地球人。事实上，阿瑟从前认识崔莉恩，而且曾经试图勾搭人家，可惜没有成功。马文：天才机器人，疑心病极其重，极其沮丧，极其唠叨。这些人物结成一个小团队，他们将揭开一个骇人听闻的大秘密……','1394364','https://book.douban.com/subject/1394364/'," The Hitchhiker's Guide to the Galaxy",'徐百柯');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('177','2001：太空漫游','[英]阿瑟·克拉克','英','上海文艺出版社','2019-5-10','320','62.00元','精装','读客外国小说文库：阿瑟·克拉克作品','9787532170692','9.3','16535','◆《太空漫游》拓展了人类理解宇宙的宽度、广度和深度。从普通读者到刘慈欣到NASA科学家，都从中获得启迪。◆刘慈欣：“我所有作品都是对《太空漫游》的拙劣模仿！”◆科幻文学历史上不可超越的至高神作！◆50年来，影响了后世几乎所有科幻作品：《三体》《流浪地球》《星际穿越》《星球大战》《阿凡达》《黑客帝国》《地心引力》《降临》《异形》《飞向太空》《第三类接触》《人工智能》《终结者》《超时空接触》《普罗米修斯》《疯狂的外星人》……◆入选美国国家公共电台、《轨迹》杂志、英国《卫报》有史以来最伟大的100部科幻-奇幻小说。◆入选美国亚马逊评选的有生之年必读的100部科幻-奇幻小说！◆同名改编电影，由斯坦利•库布里克执导，登顶所有科幻电影榜单！◆关于人类探索太空和自身的恢宏构想！讲透“我们是谁，我们从哪里来，我们到哪里去”的终极奥秘！◆作者阿瑟•克拉克与阿西莫夫、海因莱因一起并称为“20世纪科幻三巨头”，被誉为科幻小说之王。一生创作100多部作品，被翻译成40多种语言，作品总销量1亿多册。◆阿瑟•克拉克被誉为20世纪伟大的太空预言家，他针对太空技术作出的许多预测都成为现实，地球静止卫星的轨道正是因为他而被命名为“克拉克轨道”。◆《2001：太空漫游》是克拉克永恒的科幻经典《太空漫游四部曲》第一部，也是阿瑟•克拉克极负盛名和被公认的至高杰作！◆全新精装典藏版！采用烫银+UV工艺！收录15幅精美剧照！◆随书附赠一张精美太空船票，跟随本书一起探索太空！·【内容简介】·关于人类探索太空和自身的恢宏构想！讲透“我们是谁，我们从哪里来，我们到哪里去”的终极奥秘！·一块跨越300万年历史的神秘石板，从300万年前促使猿人向人类进化，到现在指引人类驶向太空的更深处。一场跨越地球到土星十亿公里的太空之旅，人类跟随更高级文明的指引，试图找到太空和自身的答案。人类文明究竟是自然演化，还是更高级文明的一场实验？宇宙文明演化的终点又在哪里？在《2001：太空漫游》里，阿瑟•克拉克给了我们一个终极而恢宏的答案。·【名人推荐】·我所有作品都是对《2001：太空漫游》的拙劣模仿，科幻文学在此达到了一个顶峰，之后再也没有人能超越，即使是克拉克本人。————刘慈欣（《三体》《流浪地球》作者）·无论是小说还是电影，《2001：太空漫游》都开启了人们探索未来和太空的心智。——尼尔•盖曼（《美国众神》《北欧众神》作者）·是克拉克为我们的登月提供了最重要的知识动力。——美国国家航空航天局（NASA）·阿瑟•克拉克是世界上独一无二的科幻作家，拥有无穷的想象力、智慧、知识和好奇心。——斯坦利•库布里克（《2001：太空漫游》电影导演）·阿瑟•克拉克和阿西莫夫的作品，让我走上了成为科幻作家的道路。——特德•姜（科幻作家，《你一生的故事》作者）·克拉克对我产生了很大的影响……克拉克的英文是很优美的，不是花哨，而是在选择用语的时候非常抒情、优雅。当有很多种可选择的表达时，他总是很仔细地选择。所以他对我的影响来自这三方面：他的风格、他对抽象问题的探讨和他的表达。——罗伯特•索耶（雨果奖、星云奖得主）·还没看过《2001：太空漫游》的地球人要抓紧恶补一下了。这非常有助于你为即将快速到来的全民太空时代做好最基础的准备。——姬十三（果壳网CEO）·至于我自己，为什么要写作科幻呢？要回答这个问题，我必须回到我13岁时，读完阿瑟•克拉克的《2001太空漫游》后，仰望星空，觉得宇宙如此的浩瀚，而我自己特别渺小……就是这种原初的感动和敬畏，让我开始拿起笔来写作，创作我自己的科幻世界。——陈楸帆（科幻作家，《荒潮》作者）·使大刘成为大刘的一套书。喜欢大刘的人能不看吗？非看不可！反复读才是！——吴岩（科幻作家，中国科普作家协会副理事长）·【媒体评价】·充满诗意、科学想像力和克拉克典型的讽刺幽默。他站在宇宙的前端，让我们以不同的视角看到这个宇宙……一本关于人类历史的复杂寓言。──《纽约客》·令人眼花缭乱……痛苦扭曲……一次思想的狂欢。——《时代周刊》·令人难以置信！——《生活》杂志·《2001：太空漫游》并不是一部简单的科幻小说，它是科幻小说的里程碑。——《纽约时报》·克拉克用他对外星世界一丝不苟的创造力创作了一部令人惊讶的作品，这也证明了克拉克是一个大师！——《图书馆杂志》·在克拉克诞辰100周年的今天，克拉克仍然是无可置疑的科幻小说之王。克拉克是一位能够提升你的想象力的作家。——《卫报》','30471298','https://book.douban.com/subject/30471298/',' 2001','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('178','额尔古纳河右岸','迟子建','中国','北京十月文艺出版社','2005-12','262','29.00元','平装','','9787530209936','9','21834','这是第一部描述我国东北少数民族鄂温克人生存现状及百年沧桑的长篇小说。似一壁饱得天地之灵气，令人惊叹却难得其解的神奇岩画；又似一卷时而安恬、时而激越，向世人诉说人生挚爱与心灵悲苦的民族史诗。著名女作家迟子建，以一位年届九旬，这一弱小民族最后一个酋长女人的自述，向我们娓娓道来——在中俄边界的额尔古纳河右岸，居住着一支数百年前自贝加尔湖畔迁徙而至，与驯鹿相依为命的鄂温克人。他们信奉萨满，逐驯鹿喜食物而搬迁、游猎，在享受大自然恩赐的同时也艰辛备尝，人口式微。他们在严寒、猛兽、瘟疫……的侵害下求繁衍，在日寇的铁蹄、“文革”的阴云……乃至种种现代文明的挤压下求生存。他们有大爱，有大痛，有在命运面前的殊死抗争，也有眼睁睁看着整个民族日渐衰落的万般无奈。然而，一代又一代的爱恨情仇，一代又一代的独特民风，一代又一代的生死传奇，显示了弱小民族顽强的生命力及其不屈不挠的民族精神。小说语言精妙，以简约之美写活了一群鲜为人知、有血有肉的鄂温克人。小说以小见大，以一曲对弱小民族的挽歌，写出了人类历史进程中的某种悲哀，其文学主题具有史诗品格与世界意义。','3311253','https://book.douban.com/subject/3311253/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('179','理想国','[古希腊]柏拉图','古希腊','商务印书馆','1986-8','458','28.00元','平装','汉译世界学术名著丛书·哲学','9787100017565','8.8','32503','《理想国》涉及柏拉图思想体系的各个方面，包括哲学、伦理、教育、文艺、政治等内容，主要是探讨理想国家的问题。《理想国》一书是柏拉图的一篇重要对话录，对话录里柏拉图以苏格拉底之口通过与其他人对话的方式设计了一个真、善、美相统一的政体，即可以达到公正的理想国。柏拉图的理想国是人类历史上最早的乌托邦。在他的理想国里统治者必须是哲学家，他认为现存的政治都是坏的，人类的真正出路在于哲学家掌握政权，也只有真正的哲学家才能拯救当时城邦所处的危机。这种信念构成了柏拉图成熟的政治哲学体系的核心。在他的眼里“哲学家”有着特殊的内涵。他认为哲学家是最高尚、最有学识的人，而这种贤人统治下的贤人政体就是最好是政体。所以，只有建立以哲学家为国王的国家才是最理想的国家。这个国家就是存在于天上的模范国家。在他看来，哲学家的本质是具有知识，具有智慧、正义、善的美德，只有哲学家才能达到对国家最高理念的认识，即对“善”的把握，而其他人也只能把握“意见”而已。治国作为一门知识，也只有哲学家才能掌握它，进而有资格执政，也就是说只有哲学家才能达到对于国家理念的认识，知道“理想国”应该怎样组织、怎样治理。这样，所谓哲学家执政，就被柏拉图理解为高超的智慧、真实的知识、完美的德行和绝对最高权力的结合。他坚信只有哲学家才可拯救城邦和人民，哲学家是理想国必然的统治者。人类追求的正义与善就是柏拉图理想国的主题，他认为国家、政治和法律要朝向真正的存在并与人的灵魂相关才有意义。在《理想国》里苏格拉底刚开始讨论的话题就是“正义”问题，由此我们可以看到柏拉图对正义有着多么强烈的憧憬与向往！他认为绝对的正义在神那里，这正好印证了对话结尾的宣言，“让我们永远走向上的路，追求正义和智慧”。正义和智慧不仅是国家的主题，也可以说是整个宇宙存在的本质，因为国家的起点“就是永无止境的时间以及时间带来的变化”。柏拉图的正义表现在爱情上就是节制。在我们很多人看来，柏拉图式的爱情似乎是精神恋的代名词，用来指称那种超越时间、空间，不以占有对方肉体为目的的只存在于灵魂间的爱情。而实际上柏拉图式爱情的真谛指的是一种对节制的崇尚，对善和美的追求。','1004821','https://book.douban.com/subject/1004821/',' Πολιτεία','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('180','罗生门','[日]芥川龙之介','日','上海译文出版社','2008-7','189','12.00元','平装','译文名著文库','9787532744756','8.7','64450','芥川龙之介(1892—1927)，日本新思潮派代表作家，创作上既有浪漫主义特点，又具有现实主义倾向。以其名字命名的“芥川奖”成为日本文坛的重要奖项之一。作品以短篇小说为主，多为历史题材，情节新奇甚至诡异，以冷峻的文笔和简洁有力的语言让读者关注到社会丑恶现象，这使得他的小说既具有高度的艺术性，又成为当时社会的缩影。《罗生门》是其代表作。本书收录芥川的中短篇小说共十三篇。《罗生门》以风雨不透的布局将人推向生死抉择的极限，从而展示了“恶”的无可回避，第一次传递出作者对人的理解，对人的无奈与绝望。书山稗海，文史苑囿，于中沉潜含玩，钩没抉隐，一日发而为文，自是信手拈来，随机生发，纵横捭阖，不可抑勒。由庙堂高官到市井小民，由紫宸之深到江湖之运，其笔下无不呼之即来，腾跃纸上。芥川生性敏感，一般来说，他不重描绘而意在发掘，疏于叙述而工于点化，少的是轻灵与潇洒，多的是沉郁与悲凉。这点不难从其作品中窥其一斑。','3136271','https://book.douban.com/subject/3136271/',' 羅生門','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('181','一桩事先张扬的凶杀案','[哥伦比亚]加西亚·马尔克斯','哥伦比亚','南海出版公司','2013-6','128','25.00元','精装','新经典文库：加西亚·马尔克斯作品','9787544266123','8.7','40555','★马尔克斯自认为掌控得最好的作品。★这是一个残忍的犯罪故事，一部极具张力的小说，为此我写了三十年。——加西亚•马尔克斯★它综合了我以往所有作品的元素，我希望写的东西百分之百、准确无误地达到了。——加西亚•马尔克斯“凶手千方百计找人阻止他们行凶，得到的却是所有人的漠视、旁观。”1951年，加西亚•马尔克斯的一个朋友在全镇人面前惨遭杀害。“我如此急切地想要讲述这桩案件，也许是它最终确定了我的作家生涯。”1981年，经过30年的调查和思考，马尔克斯终于找到这出悲剧的关键。此时的马尔克斯，为抗议独裁统治，已经进行了5年文学罢工。然而为完成这一作品，他打破誓言，写下了这部触目惊心的悲剧——《一桩事先张扬的凶杀案》。次年，马尔克斯荣获诺贝尔文学奖。','24382569','https://book.douban.com/subject/24382569/',' Crónica de una muerte anunciada','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('182','野火集','龙应台','中国','文汇出版社','2005-8','252','25.00元','平装','龙应台作品系列','9787806767245','8.8','34155','龙应台常常针对一种社会现象，一类具体事物，甚至于一个人、一句话、一件事，给予无情的透视和直接的批评，马上让人心有戚戚焉。这些事，就发生在周围，看得见，摸得着，那么具体、实在、确切；而内中的缘由、涵义、影响、作用，常人似乎无所感，一经点破；立时豁然开朗。此次重新编整后推出的《野火集》，除了保留作者当年的“野火”文字，附加了作者对此文的感言，或对当时时代背景的补充说明，或观照了对文章产生的现实意义。并邀请二十位华语文学名人如柏杨、余秋雨等，为文或追忆或评析走过“野火时代”的台湾。','1426971','https://book.douban.com/subject/1426971/','','');
insert into book(id,name,author,country,publisher,year,page,price,frame,category,isbn,star,comment_num,brief,douban_bookid,link,name_o,trans) values('183','冰与火之歌','乔治·马丁','中国','重庆出版社','2013-10-1','全五册','330.00元','精装','独角兽书系','9787223361835','9.5','15562','《冰与火之歌》（ASongofIceandFire）是由美国著名科幻奇幻小说家乔治·R·R·马丁（GeorgeR.R.Martin）所著的史诗奇幻小说，是当代奇幻文学一部影响深远的里程碑式的作品。于1996年初问世时，便以别具一格的结构、浩瀚辽阔的视野、错落有致的情节和生动活泼的语言，迅速征服了欧美文坛。迄今，本作已被译为三十多种文字，并在各个国家迭获大奖。作品主要描述了在一片虚构的中世纪大陆上所发生的一系列相互联系的宫廷斗争、疆场厮杀、游历冒险和魔法抗衡的故事，全书七卷（已出版到第五卷）浑然一体，共同组成了一幅壮丽而完整的画卷。从1995年至今，《权力的游戏》、《列王的纷争》、《冰雨的风暴》、《群鸦的盛宴》和《魔龙的狂舞》陆续出版，引发了全球性的追捧热潮。2008年底，该系列图书销量接近一千万册，在全球以三十多种语言翻译出版。由于HBO同名电视剧的推动，该系列图书仅今年二季度，在美国一地就卖出了400万册之多，长时间霸占了《纽约时报》畅销书排行榜前十位的多个位置。','25826936','https://book.douban.com/subject/25826936/',' A Song of Ice and Fire','');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (184, '雨季不再来', '三毛', '中国', '北京十月文艺出版社', '2007-7', '294', '28.00元', '精装', '三毛集', '9787530208960', 8.7, 51706, '《雨季不再来》以三毛的生命历程为主题，记录了三毛17岁到22岁的成长过程，真实呈现出三毛少女时代的成长感受，辍学、自闭、叛逆，游学西班牙、德国、美国后，渐渐成长为独立自信的青年，这本书中透露的纯真情怀和异质美感，可以清楚地印证她传奇性格的痕迹。', '2136140', 'https://book.douban.com/subject/2136140/', '', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (185, '倚天屠龙记', '金庸', '中国', '生活·读书·新知三联书店', '1999-04', '1335', '47.00', '平装', '金庸作品集（三联口袋本）', '9787108012692', 8.7, 57932, '《倚天屠龙记》，金庸武侠小说，著于1961年，是“射雕三部曲”系列第三部，现收录在《金庸作品集》中。该书以元末群雄纷起、江湖动荡为广阔背景，叙述武当弟子张无忌的江湖生涯，表现众武林豪杰质朴自然，形态各异的精神风貌，展现其不可替代的人格力量。', '1070851', 'https://book.douban.com/subject/1070851/', '', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (186, '半生缘', '张爱玲', '中国', '北京十月文艺出版社', '2006-12', '372', '28.00元', '精装', '张爱玲集', '9787530208694', 8.6, 79452, '他和曼桢认识，已经是多年前的事了。算起来倒已经有十四年了——真吓人一跳！马上使他连带地觉得自己已老了许多。日子过得真快，尤其对于中年以后的人，十年八年都好像是指顾间的事。可是对于年轻人，三年五载就可以是一生一世。他和曼桢从认识到分手，不过几年的工夫，这几年里面却经过这么许多事情，仿佛把生老病死一切的哀乐都经历到了。曼桢曾经问过他，他是什么时候起开始喜欢她的。他当然回答说：“第一次看见的时候。”说那句话的时候是在那样的一种心醉的情形下，简直什么都可以相信，自己当然绝对相信那不是谎话。其实他到底是什么时候第一次看见她的，根本就记不清楚了。', '1963684', 'https://book.douban.com/subject/1963684/', '', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (187, '孙子兵法', '孙武', '中国', '上海古籍出版社', '2006-7', '175', '20.00元', '平装', '', '9787532544400', 9.4, 11896, '《孙子兵法》是我国最古老最杰出的一部兵书，历来备受推崇，研习者辈出不穷。我们现在能见到的最早为《孙子兵法》作注的乃一代枭雄曹操，字里行间不乏曹操的真知灼见。当代著名军事家郭化若更是长期研究《孙子兵法》，不但为全文作了校勘、注释、今译，还撰有题解及考订、评论文字，既有较高的学术水平，又浅显易懂。本书在郭化若注评的基础上又加上了曹操的注，将孙武、曹操、郭化若的军事思想融为一书，以裨读者把握我国两千年来军事思想演化的脉络。同时为方便阅读，在开本、版式、装帧等方面亦作了精心的设计。堪称形式与内容相得益彰。', '1949420', 'https://book.douban.com/subject/1949420/', '', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (188, '当呼吸化为空气', '[美]保罗·卡拉尼什', '美', '浙江文艺出版社', '2016-12', '256', '48.00元', '精装', '', '9787533946562', 8.9, 25356, '全世界只有万分之零点一二的人会在36岁前患上肺癌，保罗·卡拉尼什（PaulKalanithi）是其中之一。当你读到这本书时，他已经不在人世。保罗曾获得过美国斯坦福大学英语文学及人体生物学双料学位，并于英国剑桥大学获得科学史与哲学研究硕士学位，以优异成绩获得美国耶鲁大学医学博士学位，即将获得斯坦福医学院外科教授职位并主持自己的研究室。2013年，即将抵达人生巅峰的保罗，忽然被诊断出患有第四期肺癌。自此，他开始以医生和患者的双重身份，记录自己的余生，反思医疗与人性。他的文章刊登在《纽约时报》《华盛顿邮报》等媒体，获得了全球读者关注。本书文笔优美，诚挚感人，书里有着对人性、生死、医疗的深沉思索，让全球无数读者为之动容。', '26896859', 'https://book.douban.com/subject/26896859/', ' When Breath Becomes Air', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (189, '最好的我们', '八月长安', '中国', '湖南文艺出版社', '2013-8-5', '560', '55', '套装', '“振华”系列', '9787540462642', 8.7, 68548, '你总是说青春从不曾永远，而那时候的我们，就是最好的我们。这一次，我们和整个青春做告别。八月长安全新力作。你还记得高中时的同桌吗？那个少年有世界上最明朗的笑容，那个女生有世界上最好看的侧影。高中三年，两个人的影子和粉笔灰交织在一起，黑白分明，在记忆里面转圈。本书以怀旧的笔触讲述了女主角耿耿和男主角余淮同桌三年的故事，耿耿余淮，这么多年一路走过的成长故事极为打动人心，整个故事里有的都是在成长过程中细碎的点点滴滴，将怀旧写到了极致，将记忆也写到了极致。', '24754316', 'https://book.douban.com/subject/24754316/', ' 流水混账', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (190, '雷雨', '曹禺', '中国', '人民文学出版社', '1999-05', '188', '9.20', '平装', '中国现代名剧丛书', '9787020018567', 8.7, 73841, '《雷雨》所展示的是一幕人生大悲剧，是命运对人残忍的作弄。专制、伪善的家长，热情、单纯的青年，被情爱烧疯了心的魅惑的女人，痛悔着罪孽却又不自知地犯下更大罪孽的公子哥，还有家庭的秘密，身世的秘密，所有这一切在一个雷雨夜爆发。有罪的，无辜的人一起走向毁灭。曹禺以极端的雷雨般狂飙恣肆的方式，发泄被抑压的愤懑，毁谤中国的家庭和社会。', '1013416', 'https://book.douban.com/subject/1013416/', '', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (191, '荒原狼', '[德]赫尔曼·黑塞', '德', '上海译文出版社', '2008-7', '193', '13.00', '平装', '译文名著文库', '9787532744800', 9, 19255, '小说的主人公哈勒尔是个正直的作家，他鄙视现代社会生活方式，常常闭门不出，令人窒息的空气使他陷于精神分裂的境地。一天他偶尔读到一本《评荒原狼》的小书，顿觉大梦初醒，认为自己就是一个“人性”和“狼性”并存的荒原狼。之后他应邀参加聚会，发现与会者都有狭隘的民族主义观点，而他的反战言论遭到斥责，更觉自己孤独；回家时他遇到酒吧女郎赫尔米娜，获得肉欲欢乐；经赫尔米娜介绍他又结识了音乐人帕布洛和一姑娘玛丽亚，他在音乐和感官享受中忘却了一切烦恼和忧虑。但当他看到赫尔米娜和帕布洛亲近时，便“狼性”大发，出于嫉妒将赫尔米娜杀死。小说幻想色彩浓郁，象征意味深远，被认为有“超现实主义”风格；托马斯·曼称它为“德国的尤利西斯”。', '3131626', 'https://book.douban.com/subject/3131626/', '', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (192, '你好，旧时光（上 下）', '八月长安', '中国', '新世界出版社', '2009-12', '533', '39.80元', '平装', '', '9787510407109', 8.7, 46095, '最好的青春共鸣文本！盛大的记忆共鸣曲，写给每个女孩的“成长之书”，带你重回“最美的时光”。网络原名《玛丽苏病例报告》，随书附赠精美2010年历卡！1部晋江网6年来唯一不谈情爱而人气爆棚、高居榜首的小说！1部让所有心中有爱的70、80、90后共同追捧的青春奇书！“玛丽苏”是一种“自以为是主角”的病，我们都是患者。感染无须惊慌，它只宣告成长的开始。这是一个小女孩的成长故事，这或许也是你的故事。她是女侠、雅典娜、月野兔、花仙子、希瑞、白娘子……她以为所有人都爱她，世界等着她拯救。却没想到，这世界无人可以拯救，她所能做的，只是长大。彼得潘终于走完了小时候，彼时的少年站在成长的尽头，回首过去，一路崎岖竟已遍地繁花盛开。', '4166819', 'https://book.douban.com/subject/4166819/', ' 玛丽苏病例报告', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (193, '天生有罪', '[南非]特雷弗·诺亚', '南非', '北京联合出版公司', '2018-2-1', '304', '58.00', '平装', '', '9787559614056', 9.1, 15713, '火遍微博、微信的《小崔每日秀》主持人特雷弗·诺亚首部回忆录比尔·盖茨2017年夏季书单推荐图书美国《纽约时报》《新闻日报》《时尚先生》及“国家公共电台”年度好书2017年瑟伯美国幽默文学奖得主美国亚马逊周阅读榜连续在榜，评分高达4.8编辑推荐：★囧司徒接班人崔那娃爆笑回忆录作为美国最具影响力的脱口秀节目之一，《囧司徒每日秀》曾在中国收获了一大批忠实粉丝，他宣布辞职后，钦点了南非脱口秀明星特雷弗·诺亚担任继任者。名不见经传的诺亚以自己独特的喜剧风格，在美国内外收获了大批粉丝，并被中国观众亲切称为“崔那娃”。在本书中，小崔将他的幽默天赋发挥到了极致，完美展示了为什么他会被选为《每日秀》的主持人。★“生而有罪”的崔那娃讲述贫民窟的成长经历小崔出生在种族隔离制度下的南非，其父母属于跨种族结合，因此他生下来就犯了罪，再加上他从小生活在贫民窟，似乎注定要走上一条犯罪道路。然而，倔强的小崔与伟大的母亲凭借着幽默、乐观与智慧，最终摆脱了暴力与贫穷的怪圈，从南非的贫民窟走向了世界的舞台。★多家媒体“年度好书”，普利策奖获得者、《纽约时报》毒舌书评家角谷美智子赞不绝口南非不只有曼德拉和查理兹·塞隆，本书在讲述作者各种或爆笑、或痛苦、或感人的童年经历时，还将目光投降了南非这块神奇的土地以及反人类的种族隔离制度之恶，充满了浓烈的异域色彩与人文关怀。美国当红脱口秀《小崔每日秀》主持人特雷弗·诺亚一出生就犯了罪：因为他的父亲是白人，母亲是黑人，而在当时种族隔离的南非，这样的结合属于犯罪，他父母会面临五年有期徒刑的惩罚，诺亚自己也会被送到孤儿院。为了保护诺亚，他那位勇敢、反叛、幽默、坚强、虔诚信教的母亲，想出了各种极端甚至荒唐的办法，在歧视、贫困、暴力的怪圈中，竭力为他创造了一种正常的生活。而自幼便遗传了母亲性格的诺亚，除了像大多数青少年那样成长外，还需要学会如何“尴尬”地在一个他本不该存在的世界上生活。这本回忆录中收集的故事，时而幽默，时而惊险，时而感人，但无一例外都彰显了诺亚惊人的喜剧天赋，以及一位母亲无条件的、无止境的、无与伦比的爱。', '30125030', 'https://book.douban.com/subject/30125030/', ' Born a Crime', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (194, '枪炮、病菌与钢铁', '[美国]贾雷德·戴蒙德', '美国', '上海译文出版社', '2006-4-1', '460', '45.00元', '平装', '世纪人文系列丛书·开放人文', '9787532739233', 8.7, 45864, '为什么是欧亚大陆人征服、赶走或大批杀死印第安人、澳大利亚人和非洲人，而不是相反？为什么么小麦和玉米、牛和猪以及现代世界的其他一些“不了起的”作物和牲畜出现在这些特定地区，而不是其他地区？在这部开创性的著作中，演化生物学家贾雷德.戴蒙德揭示了事实上有助于形成历史最广泛模式的环境因素，从而以震撼人心的力量摧毁了以种族主义为基础的人类史理论，因其突出价值和重要性，本书荣获1998年美国普利策奖和英国科普书奖，并为《纽约时报》畅销书排行榜作品。本书是理解人类社会发展史方面的一个重大进展，它记录了现代世界及其诸多不平等所以形成的原因，也是一部真正关于全世界各民族的历史，是对人类生活的完整一贯的叙述，娓娓道来，具有很强的可读性。', '1813841', 'https://book.douban.com/subject/1813841/', ' Guns, Germs and Steel', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (195, '一千零一夜', '', '', '人民文学出版社', '2003-01', '560', '22.00', '平装', '名著名译插图本', '9787020040346', 8.7, 55202, '《一千零一夜》被高尔基誉为\'世界民间文学史上最壮丽的一座纪念碑\'。本书故事精彩、妙趣横生、想象丰富、扣人心弦，充满神秘奇幻的色彩，蕴藏瑰丽丰富的风貌，既是儿童幻想的眼睛，又是老年人心灵的慰藉，老少咸宜，令人百读不厌，回味无穷。', '1035848', 'https://book.douban.com/subject/1035848/', ' The Arabian Nights', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (196, '银河帝国：基地七部曲', '[美]艾萨克·阿西莫夫', '美', '江苏凤凰文艺出版社', '2015-10-1', '2888', '328.00元', '平装', '读客外国小说文库：艾萨克·阿西莫夫作品', '9787539983356', 9.5, 12553, '人类蜗居在银河系的一个小角落——太阳系，在围绕太阳旋转的第三颗行星上，生活了十多万年之久。人类在这个小小的行星（他们称之为“地球”）上，建立了两百多个不同的行政区域（他们称之为“国家”），直到地球上诞生了第一个会思考的机器人。在机器人的帮助下，人类迅速掌握了改造外星球的技术，开启了恢弘的星际殖民运动；人类在银河系如蝗虫般繁衍扩张，带着他们永不磨灭的愚昧与智慧、贪婪与良知，登上了一个个荒凉的星球，并将银河系卷入漫长的星际战国时代，直至整个银河被统一，一个统治超过2500万个住人行星、疆域横跨十万光年、总计数兆亿人口的庞大帝国崛起——银河帝国。一个微妙的转折发生在银河帝国建国后的12020年。哈里·谢顿，这个刚满32岁的年轻数学家，开创了“心理史学”，这门学科能用数学公式准确推演全人类的未来——“预言”从此成为一门可以信任的科学，人类由此可以看见未来。谢顿的第一个预言是：虽然毫无征兆，但已存在一万两千年之久的银河帝国即将灭亡。一时间，银河震动，帝国飘摇；皇帝、宰相、夺权者、反叛星球，各方势力立刻剑拔弩张，人类银河时代最伟大的传奇就此开启……', '26389895', 'https://book.douban.com/subject/26389895/', '', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (197, '水浒传（全二册）', '罗贯中/施耐庵', '中国', '人民文学出版社', '1997-1', '1314', '50.60元', '平装', '中国古典文学读本丛书', '9787020008742', 8.7, 71519, '《水浒传》是我国第一部以农民起义为题材的长篇章回小说，是我国文学史上一座巍然屹立的丰碑，也是世界文学宝库中一颗光彩夺目的明珠。数百年来，它一直深受我国人民的喜爱，并被译为多种文字，成为我国流传最为广泛的古代长篇小说之一。《水浒传》具体而生动地描写了以宋江为首的农民起义发生、发展直至失财的整个过程，揭露了封建社会的黑暗、腐朽和统治阶级的种种罪恶，热情歌颂了起义英雄的反抗精神和正义行动，塑造了一大批梁山好汉的光辉形象，形象地揭示了封建社会“官逼民反”的客观真理和农民起义失败的内在原因。', '1008357', 'https://book.douban.com/subject/1008357/', '', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (198, '论语', '', '', '中华书局', '2006-12', '191', '9.80元', '平装', '', '9787101054187', 9.1, 21159, '《论语》是一部语录体思想散文集。早在春秋后期孔子设坛讲学时期，其主体内容就已初始创成；孔子去世以后，他的辫子和再传弟子代代传授他的言论，并逐渐将这些口头记诵的语录言行记录下来，集腋成裘，最终在战国初年汇辑论纂成书，因此称“论”。《论语》主要记载孔子及其弟子的言行，因此称为“语”。孔子思想以立身为出发点，而人能立身于世的首要条件就是具有“君子”人格。君子具备仁爱之心，自重自律；表里如一，言行一致；积极进取，德才兼备；孜孜于学，注重实践；安贫乐道，谨守正义等等；始终坚守人之间的相处之道，遵从不同的伦理关系，构建着和谐友爱的人际关系和社会环境。这本书以权威版本为底本，约请张燕婴作了晓畅通俗的白话翻译。文白对照，并以对开的方式排版，以为读者提供阅读最方便的文本。', '1964774', 'https://book.douban.com/subject/1964774/', '', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (199, '你一生的故事', '[美]特德·姜', '美', '译林出版社', '2015-5', '292', '36.00元', '精装', '译林幻系列', '9787544752473', 8.8, 32062, '一瞥之下，过去与未来轰轰然同时并至，我的意识成为长达半个世纪的灰烬，时间未至已成灰。五十年诸般纷纭并发眼底，我的余生尽在其中。还有，你的一生。《你一生的故事》结集特德·姜早期的七篇作品：代表作《你一生的故事》、处女作《巴比伦塔》以及《领悟》、《除以零》、《七十二个字母》、《人类科学之演变》、《地狱是上帝不在的地方》——一个语言学家学会了一门外星语言，彻底改变了她的世界观。过去、现在、未来，在她眼前同时展开。为了离上帝更近，人类伐木烧砖，拉车砌塔，经过无数个世代的辛劳，终于建成一座巍峨耸立于天地之间的通天塔。只要一小瓶药水，就能拥有超人智慧，这个诱惑无人能挡。可惜超人不止一个，荡气回肠的超人对决之后，领悟终极的智慧。任何数字除以零，都不会得出一个有意义的结果。两个人的感情除以零，结果是什么？在一个“名字驱动一切”的世界，命名师们玩转七十二个字母，寻找人类种族繁衍的密码。当人类的所有科学研究仅仅局限在解读后人类的科研成果时，这种研究是否还有存在的必要？妻子在天使下凡时去世，丈夫却必须学习如何热爱上帝，以便死后与妻子团聚。', '26295448', 'https://book.douban.com/subject/26295448/', ' Stories of Your Life and Others', '');
INSERT INTO `book` (`id`, `name`, `author`, `country`, `publisher`, `year`, `page`, `price`, `frame`, `category`, `isbn`, `star`, `comment_num`, `brief`, `douban_bookid`, `link`, `name_o`, `trans`) VALUES (200, '象棋的故事', '[奥]斯蒂芬·茨威格', '奥', '上海译文出版社', '2007-7', '387', '23.00元', '平装', '斯蒂芬·茨威格作品集', '9787532741601', 9.1, 11799, '《象棋的故事》是茨威格生前发表的最后一部中篇小说。小说表面上讲述了一条从纽约开往南美的轮船上一位业余国际象棋手击败了国际象棋世界冠军的故事，实际上倾诉了纳粹法西斯对人心灵的折磨及摧残。作为人文主义者的茨威格借这篇小说表达了他对纳粹法西斯的痛恨。他以这种文学形式的抵抗加入了世界反法西斯同盟的行列。《感情的混乱》是一则关于同性恋者的故事。一位英国文学教授过着令人难堪的双重生活。他在学识上是个颇有造诣的莎士比亚专家，知识渊博，才华横溢，可是偏好男色，于是感情混乱，夫妻、师生关系反常，内心苦闷，最后身败名裂，郁郁死去。《看不见的珍藏》写德国在第一次世界大战后，皇帝和将军打了败仗，万千战士死于疆场。战后德国百业萧条，通货膨胀，民不聊生，受苦受难的是黎民百姓。一夜之间，人们多年辛辛苦苦积攒下来的储蓄化为乌有。……', '2158684', 'https://book.douban.com/subject/2158684/', '', '张玉书');